style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rtlnieuws.nl") {

* {
    box-shadow: none !important;
    border-radius: 0 !important;
    border: 0 !important;
}
    
html, body {
    background: #C4DEEE url("http://i.ytimg.com/vi/T9UbXSareP4/maxresdefault.jpg") no-repeat fixed 54.4% center / calc(1920px / 1.2) !important;
}

/*BG none*/
header#topHeader, body #canvas, section#topNewsHeader {
    background: none;
} 

/*BG Transparent*/
div#canvas, div.dart-footer, div.comments, .article-topnieuws {
    background-color: transparent;
}

/*No Border*/
div#quicklinks, section#topNewsHeader, section#topNewsHeader article {
    border: 0;
}

/*Display No*/
.dartHeader.billboard, [id="rtl-cookie-message"] {
    display: none;
}

body nav#mainNav div.container ul li a {
    padding: 13px 8px;
}

div#canvas.fixed header#topHeader {
    position: absolute;
}

 body nav#mainNav div.container ul li:hover {
     background-color: rgb(53, 167, 215) !important;
}

/*Paneel*/
    div#quicklinks, .news, .topnieuws__article {
        background-color: rgba(255, 255, 255, .8);
    }

    footer#footer {
        background: rgba(19, 18, 23, .8);
    }

    article.story {
        background-color: rgba(238, 239, 241, .3);
    }
/*Paneel*/
section#topNewsHeader article:first-child img:not(:hover) {
    border-right: 26px solid rgba(255, 255, 255, 0);
    opacity: .8;
}

section.sidebarList article h5 a, .mostRead article h5 a, .mostRead article p.story-title a
        , section#topNewsHeader article:not(:first-child)  h5 > a
, div#dailyInfo > a, section#topNewsSidebar article h5 a, section#topNewsSidebar article p.story-title a, section#topNewsSidebar article:nth-child(2) p.story-title a, .article--topnieuws__title {
    color: white;
    text-shadow: 1px 1px 3px black, 1px 1px 3px black, 1px 1px 3px black;;
}

}
