style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("com.nicovideo.jp") {
  .area-communityHeader,
  .mainContentBox_header,
  .submitButton {
    background-color: #272727 !important;
  }
  .mainHeader {
    border-top-color: #272727 !important;
  }
  
  .md-settingButton {
    background-color: #434343 !important;
  }
  
  .area-sideContent a, .area-sideContent a:link,
  .area-mainContent a, .area-mainContent a:link,
  .communityControlList a,
  h3.noticeTitle,
  .md-sideContentBox .text.inactive,
  .now_live_title {
    color: #0033cc !important;
  }
  
  .cp-communityNavigation a {
    color: #272727 !important;
  }
  
  .js-checkBroadcastLimit a.broadcastButton {
    color: #FFF !important;
  }
  
  .blomaga_displayControl.inactive {
    border-color: #0033cc !important;
  }
  
  .cp-communityNavigation .item.active,
  .cp-communityNavigation .item.active:hover{
    border-bottom: 3px solid #272727;
  }
}
