@-moz-document domain("recolor.me") {
#header {
	position: relative;
	height: 110px;
	z-index: 3;
	text-align: left;
	line-height: 35px;
	vertical-align: middle;
}
.limited {
	max-width: 1100px;
}
#footer {
	position: relative;
	z-index: 1;
	width: 100%;
	clear: both;
	margin: 0;
	background: repeat scroll 0 0 #140c05;
	/* border-top: 7px solid #211c18; */
	/* border-bottom: 4px solid #1C1C1C; */
	
	text-align: center;
	/* padding: 16px 0; */
	
	color: blue;
}
}
