style.css:
@-moz-document domain("tumblr.com") {
/* box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14); */

body {
    font-family: Roboto, Helvetica Neue, HelveticaNeue, Helvetica, Arial, sans-serif;
    font-weight: 400;
    line-height: 1.4;
    font-size: 14px;
    font-style: normal;
}

tumblelog_title {
    color: hsla(0, 0%, 100%, .6);
    font-weight: 500;
    padding: 1px 0 0;
    line-height: 17px;
    font-size: 14px;
}

.ui_dialog_lock, .blue#glass_overlay, .blue#hello_glass, .blue.glass {
    background: rgba(0,0,0,.75);
}
#discover_actions_discover .elevator, #search_actions_search .elevator, .elevator-wrapper.visible .elevator {
    background-color: rgba(0,0,0,.5);
    border-radius: 50%;
    padding: 10px 12px;
    margin-bottom: -10px;
    margin-right: -12px;
}



/* ===============================  Header  =============================== */

.l-header-container--refresh, .discover.explore-takeover.hero-scrolled .l-header-container--refresh {
    -webkit-transform: none;
    transform: none;
    background: #424242;
    box-shadow: 0 1px 8px rgba(0,0,0,.3);
}

.l-header-container--refresh .l-header {
    box-sizing: border-box;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    position: relative;
    width: 100%!important;
    max-width: 1580px;
    left: 0;
    top: 0;
    margin: auto;
    padding: 0 20px 0 0;
    min-width: inherit;
    height: 64px;
}

.logo {
    display: block;
    float: left;
    height: 59px;
    width: auto;
    overflow: hidden;
    margin-right: -3px;
    padding-top: 5px;
}

.ui_search {
    color: hsla(0,0%,100%,.6);
    position: relative;
    padding-right: 0;
    padding-top: 5px;
    overflow: hidden;
    height: 100%;
    width: auto;
    margin: 0;
}

.compose-button {
    position: relative;
    cursor: pointer;
    height: 36px;
    width: 49px;
    margin-left: 16px;
    margin-top: 14px;
    padding: 2px;
    float: right;
    z-index: 1;
    fill: #529ecc;
}

.tab_notice .tab_notice_value {
    color: #f0f0f0;
}

.tab-bar-container {
    position: relative;
    margin-right: 6px;
    height: 100%;
    float: right;
    width: auto;
    margin: 0;
    right: 0;
    top: 0;
}

.tab_bar {
    height: 32px;
    position: relative;
    padding-top: 15px;
    padding-right: 0;
}

.tab-bar-container .tab_notice {
    left: 27px;
    top: 11px;
    padding-top: 1px;
}

.tab-notice--outlined {
    min-width: 18px;
    height: 18px;
    border: 0px solid #36465d;
    border-radius: 2px;
}

