.discover-controls-wrapper .types-tabs>.content-control.active {
    color: #fff;
    font-weight: 700;
    border-bottom-style: solid;
    border-bottom-width: 2px;
    border-bottom-color: white;
    padding-bottom: 9px;
}
.ui_search .search_query {
    transition: background .15s,color .15s;
    color: hsla(0,0%,100%,.6);
    padding-right: 10px;
    padding-bottom: 2px;
    padding-left: 39px;
    background: rgba(255,255,255,0.2);
    font-weight: 400;
    line-height: inherit;
    font-size: 15px;
    height: 34px;
    width: 100%;
    border: 0;
    border-radius: 2px;
    margin-top: 10px;
}

.tab_bar .tab_anchor {
    transition: opacity 0ms;
    margin-top: -3px;
    /*margin-left: 1px;*/
    padding-top: 8px;
    padding-right: 14px;
    padding-left: 14px;
    height: 35px;
    text-decoration: none;
    text-align: center;
    text-indent: 0;
    display: block;
    font-size: 0;
    float: right;
    opacity: .5;
    color: #fff;
    /* background: none; */
    /*background-color: #cccccc;*/
}
