.rapid-recs-container .rapid-recs {
    position: absolute;
    right: -356px;
    bottom: 20px;
    box-sizing: border-box;
    z-index: 20;
    float: right;
    pointer-events: auto;
    width: 335px;
    margin: 0 0 0px;
    padding: 15px 5px 12px 5px;
    transition: opacity .2s ease-out;
    background-image: none;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
    background-color: #ffffff;
    border-radius: 2px;
}
.rapid-recs-container .rapid-recs .rapid-rec-title {
    color: rgba(0,0,0,.5);
    font-size: 12px;
    font-weight: 500;
    text-transform: uppercase;
    padding: 0 20px 4px 10px;
    position: relative;
    -webkit-transform: translateZ(0);
    transform: translateZ(0);
    transition: -webkit-transform .2s ease;
    transition: transform .2s ease;
}
.rapid-recs-container.peepr-open .rapid-recs {
    float: right;
    position: static;
    background-image: none;
    padding: 15px 5px 12px 5px;
    margin-bottom: 10px;
    margin-right: 10px;
    -webkit-animation: related-rec-fadein .35s ease-out 1s backwards;
    animation: related-rec-fadein .35s ease-out 1s backwards;
}


