options.value:
@-moz-document domain("eldarya.fr") { 

/*ИКОНКА*/
#pet-headerPortrait #pet-headerPortrait-base {
   background: url("http://s8.hostingkartinok.com/uploads/images/2016/10/707c71428b18606c836d1bc8f7da42fa.png") no-repeat !important;
   -webkit-filter: hue-rotate(0deg);
   filter: hue-rotate(0deg);
}  

/*НА СТРАНИЦЕ*/ 
   #pet-portrait {
    background : url("http://s8.hostingkartinok.com/uploads/images/2016/10/8bd0b772807e5e7d8d34a16f82f8742a.png") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
}   
  
/*В ПРОФИЛЕ ГГ*/
#pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}
#player-display-pet {
    background : url("http://s8.hostingkartinok.com/uploads/images/2016/10/ed7dcb4ff638d2327ab8835007a27a89.png") center no-repeat !important ;
    background-size: auto 400px !important ;
    height:  144px !important ;
    width:  181px !important ;
}  #player-display-pet > img[src*="/static/img/pet/mood/profile/"] {opacity : 0 !important ; }
   #player-display-pet {
    background : url("http://s8.hostingkartinok.com/uploads/images/2016/10/ed7dcb4ff638d2327ab8835007a27a89.png") center no-repeat !important ;
} 
  
/*Наименование*/
 #pet-info-racegender:before {
    content : "Bébé Azeralon" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
}  
#pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
}  
  
/*Еда*/
#pet-info-food:before {
    line-height: 27px;
    content : "Scorpion ambré" !important;
    font-size : 20px ;
    margin: 200px auto auto 34px ;   
}  
#pet-info-food > div {
    margin-top : 2px ;
    line-height: 15px;
    font-size: 12px;
    margin: 13px 0 0 100px;
}
#pet-info-food > img {
  background : url("http://s8.hostingkartinok.com/uploads/images/2016/10/e861718a5b9c4c4db29d063a9792037d.png") !important ;
  background-size : 72px 72px !important ;
  margin-top : 0px !important ;
  padding-left : 72px !important ;
  padding-top : 72px !important ;
  width : 0 !important ;
  height : 0 !important ;    
  border-radius: 50px;
  border: 1px solid #ebc518;
} 
  #pet-info-food {font-size : 0 !important;}    
  #pet-info-food > span {font-size : 15px !important;}
}

