.audio_page_section_layout.has_friends_block ._audio_page_titled_block {
    display: inline-block;
    float: right;
    max-width: 203px;
    margin-bottom: 30px;
    margin-top: -20px;
    margin-right: 2px;
}
.audio_page_layout.has_friends_block .audio_page__audio_rows .audio_page__sort_dd {
    position: absolute;
    right: 0;
    top: 3px !important;
}
.audio_page_section_layout.has_friends_block ._audio_page_titled_block h2 {
	display: inline-block;
}
.audio_page_layout .has_friends_block .audio_page_block__playlists_items {
	margin: 0px;
}
.has_friends_block ._audio_pl_item {
    display: inline-block;
    cursor: pointer;
    width: 153px;
    padding: 0px;
    padding-left: 7px;
    padding-bottom: 10px;
    margin-left: -7px;
    margin-bottom: 0px;
    border-radius: 4px;
    box-sizing: border-box;
    vertical-align: top;
    width: 100%;
    height: 37px;
    overflow: visible;
}
.audio_page_layout .has_friends_block .audio_page_separator,
.has_friends_block ._audio_pl_item .audio_pl__subtitle {
	display: none !important;
    height: 0 !important;
    margin: 0 !important;
    overflow: hidden !important;
    visibility: hidden !important;
}
.audio_page_layout .has_friends_block .audio_page__audio_rows > h2 {
	font-size: 16px;
	margin-top: 0px;
}
.has_friends_block ._audio_pl_item .audio_pl__info {
	margin-top: 0px !important;
}


/* pl covers show */
.has_friends_block ._audio_pl_item .audio_pl__cover {
    display: inline-block;
    background-repeat: no-repeat;
    background-position: 50%;
    background-size: 80%;
    border-radius: 3px;
    height: 24px;
    width: 24px;
    margin-top: 6px;
    margin-right: 2px;
    float: left;
}
.has_friends_block ._audio_pl_item .audio_pl__cover_stats {
	bottom: 0 !important;
    top: 5px !important;
}
._audio_pl_item .audio_pl__cover:hover .audio_pl__actions_btns {
    top: 0;
}
.audio_page_layout .has_friends_block .audio_item__title,
.audio_page_layout .has_friends_block .audio_item__title > a {
	display: block;
    font-weight: 500;
    font-size: 14px;
    padding: 0px;
    padding-left: 10px;
    line-height: 36px; 
    border-radius: 4px; 
    color: #2a5885;
    overflow: hidden;
    box-sizing: border-box;
    text-decoration: none !important;
}
.audio_page_layout .has_friends_block ._audio_pl_item:hover {
    background: #F2F4F7 !important;
}
.has_friends_block ._audio_pl_item .audio_pl__actions_btns {
    -webkit-transform: translateY(25px);
    -ms-transform: translateY(25px);
    -o-transform: translateY(25px);
    transform: translateY(25px);
    opacity: 1;
    left: 0;
    top: 0;
}
.has_friends_block .audio_pl__actions_wrap:hover .audio_pl__cover_stats {
    -webkit-transform: translateY(0) !important;
    -ms-transform: translateY(0) !important;
    -o-transform: translateY(0) !important;
    transform: translateY(0) !important;
}
.has_friends_block ._audio_pl_item .audio_pl__actions_btns > div,
.has_friends_block ._audio_pl_item .audio_pl__stats_listens {
    display: none !important;
}
.has_friends_block ._audio_pl_item .audio_pl__actions_btns > .audio_pl__actions_play {
    display: block !important;
    width: 44px;
    height: 24px;
    background-size: 20px;
    overflow: visible;
    background-position: 3px 1px;
    box-sizing: border-box;
    margin: 1px 0 0 0;
}
.has_friends_block .audio_pl__actions_wrap .audio_pl__cover_stats {
    transition: opacity 160ms linear, -webkit-transform 160ms ease;
    -o-transition: transform 160ms ease, opacity 160ms linear, -o-transform 160ms ease;
    transition: transform 160ms ease, opacity 160ms linear;
    transition: transform 160ms ease, opacity 160ms linear, -webkit-transform 160ms ease, -o-transform 160ms ease;
}
.has_friends_block ._audio_pl_item:hover .audio_pl__actions_wrap {
    opacity: 1;
}
.has_friends_block .audio_pl__actions_wrap:hover .audio_pl__cover_stats {
    -webkit-transform: translateY(-25px) !important;
    -ms-transform: translateY(-25px) !important;
    -o-transform: translateY(-25px) !important;
    transform: translateY(-25px) !important;
}
.has_friends_block .audio_pl__actions_wrap:hover .audio_pl__actions_btns {
    -webkit-transform: translateY(0) !important;
    -ms-transform: translateY(0) !important;
    -o-transform: translateY(0) !important;
    transform: translateY(0) !important;
}
.audio_page_layout .has_friends_block .audio_page_block__show_all_link {
    float: right;
    margin-top: 22px !important;
}
.has_friends_block ._audio_pl_item .audio_pl__stats_count {
    position: relative;
    right: -3px;
    padding: 0px;
    text-align: right;
    float: none;
    margin: auto !important;
}
.has_friends_block ._audio_pl_item .audio_pl__stats_count:before {
    display: block;
    position: absolute;
    width: 17px;
    height: 15px;
    left: -11px;
    top: 1px;
    float: left;
    opacity: .5;
}

