style.css:
@-moz-document domain("www.mobile01.com")
{
	/*	============ 廣告 ============
			這一段擋廣告的語法會拖慢效能
			若執行效率差，請自行刪除這區塊 */
	.ad-adblock,
	.c-group__title>span:before,
	div[id^="div-gpt-ad-"],
	div[class^="ad_"] {
		display: none !important
	}
	
	/*	==============================	*/
	
	.c-menuLv2__link[target="_blank"],
	.l-content__main>#next_page_2~.u-gapNextV--lg .l-pagination {
		display: none !important
	}
	
	::-webkit-scrollbar {
		width: 1mm;
		height: 1mm
	}
	
	::-webkit-scrollbar-thumb {
		background: #193;
		box-shadow: inset 0 0 0 .5px #ccc;
		border-radius: 1mm
	}
	
	body {
		font-family: sans-serif;
		color: #334;
		background: #88888b;
		height: auto;
		min-height: 100vh
	}
	
	iframe body {
		background: transparent !important
	}
	
	body,
	article,
	.l-signedIn,
	.t5,
	.o-selectItemTitle,
	.c-group__options a,
	.c-dropdown__select ul li,
	.l-articlePage__publish blockquote,
	.c-dropdown--sort .c-dropdown__select>li>span,
	.c-dropdown--sort .c-dropdown__select ul li,
	.c-checkbox__original>span,
	.c-radio__original>span,
	.c-selectItem__title,
	#_popIn_side_pc ._popIn_recommend_art_title a {
		font-size: calc(10pt + .32vw) !important;
		line-height: 1.55
	}
	
	.l-listTable__thead .l-listTable__td,
	.l-excerpt__desc,
	.o-fSubMini,
	.c-breadCrumb__item,
	.c-breadCrumb li a,
	.c-filter li a,
	.c-articleCard--small .c-articleCard__desc,
	.l-jumpList li a,
	.l-jumpList li span,
	.c-tabNav__wrap>li a,
	.o-fMini,
	.o-hashtag.is-mini,
	.l-footerLinks>li a {
		font-size: calc(9pt + .28vw)
	}
	
	.l-listTable__td--time,
	.l-footer__copyright .list {
		font-size: calc(9pt + .15vw)
	}
	
	html,
	body,
	.l-container,
	.l-header,
	.l-main,
	.l-footer {
		min-width: 1080px
	}
	
	.compensate-for-scrollbar {
		margin: 0
	}
	
	.l-footer__copyright {
		width: calc(100% - 10mm) !important;
		max-width: 1600pt
	}
	
	.background-side,
	.l-footer .l-wrap {
		width: auto !important
	}
	
	img {
		object-fit: contain
	}
	
	.l-container {
		min-height: 100vh;
		overflow: hidden;
		position: relative;
		width: auto
	}
	
	main.l-main,
	.content-side,
	.u-distinguish {
		background-color: transparent;
		overflow-x: hidden
	}
	
	.l-main {
		min-height: 100vh
	}
	
	.content-side {
		width: 100%;
		padding: 2mm 0
	}
	
	.l-signInPage__content,
	.l-content__side {
		background-color: rgba(245, 245, 235, .7)
	}
	
	.l-wrap {
		width: calc(100% - 24mm);
		min-width: 1050px;
		overflow-x: visible
	}
	
	.l-content,
	.l-content__main,
	.l-content__side {
		display: inline-block
	}
	
	.l-content {
		width: calc(100% + 380px + 8vw)
	}
	
	.o-heading:before {
		display: none
	}
	
	.l-tabulate__nav+.l-tabulate__content {
		margin: 2mm 0 0
	}
	
	.l-tabulate__action,
	.l-appearArea__trigger {
		padding: 0;
		margin: 0
	}
	
	.gsc-results .gsc-cursor-box,
	.l-tabulate__action .l-pagination,
	.u-tac .l-pagination,
	.l-appearArea__trigger .l-pagination {
		position: fixed;
		right: 12%;
		bottom: 3mm;
		background-color: rgba(240, 240, 230, .2);
		box-shadow: 0 0 0 1px #fff, 1px 1px 2mm #555;
		padding: 1.5mm 3mm;
		z-index: 3;
		border-radius: 2.5mm;
		opacity: .5;
		transition: .3s
	}
	
	.gsc-results .gsc-cursor-box:hover,
	.l-tabulate__action .l-pagination:hover,
	.u-tac .l-pagination:hover,
	.l-appearArea__trigger .l-pagination:hover {
		background-color: rgba(240, 240, 230, .8);
		opacity: 1;
		backdrop-filter: blur(3mm);
		-webkit-backdrop-filter: blur(3mm)
	}
	
	.l-pagination__page {
		display: inline-block
	}
	
	.c-pagination:hover {
		box-shadow: inset 0 0 0 1px rgba(0, 0, 0, .2), 0 1px 3px rgba(0, 0, 0, .4)
	}
	
	.c-pagination:active {
		box-shadow: inset 0 1px 2mm rgba(0, 0, 0, .4)
	}
	
	.l-pagination__page.is-active a.c-pagination {
		box-shadow: inset 0 1px 2mm rgba(0, 0, 0, .4);
		cursor: default
	}
	
	.gsc-results .gsc-cursor-box {
		margin: 0
	}
	
	.gsc-results .gsc-cursor-box .gsc-cursor-page {
		display: inline-block;
		min-width: 5mm;
		padding: .2em 5px;
		margin: 0 3px
	}
	
	.gsc-results .gsc-cursor-box .gsc-cursor-page,
	.l-pagination a {
		font: 400 12pt sans-serif;
		color: #080;
		background-color: rgba(240, 248, 240, .8);
		background-image: none !important;
		border: none;
		border-radius: 8px;
		box-shadow: inset 0 0 0 1px #8a8;
		text-align: center;
		transition: color .1s, background-color .1s
	}
	
	.gsc-results .gsc-cursor-box .gsc-cursor-page:last-child {
		margin: 0
	}
	
	.c-pagination--prev:before {
		display: block;
		content: "▲";
		transform: rotate(-90deg)
	}
	
	.c-pagination--next:before {
		display: block;
		content: "▲";
		transform: rotate(90deg)
	}
	
	.c-link,
	.l-jumpList li .c-link {
		position: relative;
		color: #334;
		padding: 1px .5em;
		white-space: nowrap;
		text-overflow: ellipsis;
		overflow: hidden;
		border-radius: 1em;
		transition: color .2s, background-color .2s, box-shadow .2s
	}
	
	.l-jumpList li .c-link {
		max-width: 45vw
	}
	
	.c-link:hover,
	.l-jumpList li .c-link:hover {
		color: #374;
		background-color: rgba(255, 255, 245, .7);
		box-shadow: inset 0 0 0 1px #374, 0 1px 1mm rgba(0, 0, 0, .3);
		z-index: 2;
		transition: color 60ms, background-color 60ms, box-shadow 60ms
	}
	
	.l-content__side .c-link:hover {
		background: none;
		box-shadow: none
	}
	
	.c-link:active,
	.l-jumpList li .c-link:active {
		background-color: rgba(225, 225, 215, .5);
		box-shadow: inset 0 0 0 1px #152, inset 0 1px 1mm rgba(0, 0, 0, .4)
	}
	
	.l-listTable .c-link {
		display: inline-block;
		max-width: calc(100% - 1em)
	}
	
	.u-ellipsis {
		display: inline-block;
		white-space: nowrap;
		max-height: none
	}
	
	.o-gotop {
		right: 5mm !important;
		bottom: 27mm;
		z-index: 122;
		cursor: pointer
	}
	
	.l-toolBar__item {
		line-height: 1.2;
		padding: 0 2mm;
		border-right: 1px solid #fff
	}
	
	.l-articlePage .o-fNotes {
		line-height: 1.8
	}
	
	.c-popUp__content {
		border-radius: 3mm
	}
	
	.c-popUp__title {
		background-color: #ddd;
		border-radius: 3mm 3mm 0 0
	}
	
	.fancybox-slide--html .fancybox-close-small {
		top: 0;
		right: 0;
		border-radius: 0 3mm 0 2mm;
		overflow: hidden
	}
	
	/* profile */
	.l-memberIntro {
		background-color: rgba(255, 255, 255, .7)
	}
	
	.l-interact {
		margin: 3mm 0
	}
	
	.c-textStatistics {
		background-color: rgba(240, 240, 240, .4)
	}
	
	.c-panel .c-panel__item {
		background-color: rgba(200, 200, 200, .8);
		margin: 0 -1px -1px 0;
		transition: .2s
	}
	
	.c-panel .c-panel__item:hover {
		background-color: rgba(255, 255, 255, .7);
		transition: 0s
	}
	
	.l-formRow__title--lg {
		padding: 9px 3mm 9px 0
	}
	
	.l-formRow__title {
		min-width: 6em;
		padding: .4em 4mm 0 0
	}
	
	.l-formRow+.l-formRow {
		margin-top: 0
	}
	
	.l-formRow__content {
		padding: 4px
	}
	
	.l-formText,
	.c-radio,
	.c-checkbox,
	.c-textbox,
	.c-iconBox,
	.c-textbox {
		font-size: calc(13px + .25vw) !important;
		border-radius: 2mm;
		padding: 5px
	}
	
	.l-formText {
		height: auto
	}
	
	.c-radio__original>span,
	.c-checkbox__original>span {
		margin-top: -5px
	}
	
	/* top menu */
	.l-header {
		position: relative;
		width: 100vw;
		box-shadow: 0 0 2mm #888;
		z-index: 122;
		user-select: none;
		-webkit-user-select: none
	}
	
	.l-header__main {
		animation: colorBorder 1.5s 1s 2
	}
	
	@keyframes colorBorder {
		0% {
			border-top-color: #30a651
		}
	
		50% {
			border-top-color: #c25
		}
	
		100% {
			border-top-color: #30a651
		}
	}
	
	.l-header .l-header__main,
	.l-header .l-menu {
		background-color: #ebeae7;
		transition: background-color .6s
	}
	
	.l-header:hover .l-header__main,
	.l-header:hover .l-menu {
		background-color: #f9f8f5;
		transition: background-color .3s
	}
	
	.l-header__left {
		position: relative;
		height: 2.3em
	}
	
	.o-logo {
		height: calc(100% - .2em);
		margin: .1em 8mm .1em 0;
	}
	
	.l-header__group {
		position: absolute;
		top: 0;
		left: 100%;
		padding: .4em 0;
		white-space: nowrap
	}
	
	.l-headerTools {
		padding: 0;
		height: auto
	}
	
	.l-headerTools__search {
		height: auto;
		padding: calc(.2em + .1vw) 3mm calc(.1em + .2vw)
	}
	
	.l-headerTools__login {
		padding: calc(.15em + .2vw) 0 0 3mm;
		height: 100%;
		vertical-align: middle
	}
	
	.l-headerTools__login--signedIn {
		padding-left: 4mm;
	}
	
	.l-signedIn__funs {
		margin-left: 2mm
	}
	
	.c-textbox.c-searchBox__input,
	.c-searchBox__search {
		border-radius: 1em
	}
	
	.c-searchBox.is-active .c-searchBox__input {
		padding: 7px 10mm 5px 4mm;
		background-color: #e0e7f0;
		transition: 1s
	}
	
	.c-searchBox.is-active .c-searchBox__input:focus {
		background-color: #f0fdf2;
		border: none;
		box-shadow: 0 0 0 1px #30a651;
		transition: .3s
	}
	
	.c-searchBox.is-active .c-searchBox__search {
		background-color: transparent
	}
	
	.c-group__options {
		padding: 0;
		top: 2em
	}
	
	.c-group__options--left:before {
		left: 8%
	}
	
	.c-group__options--right:before {
		right: 25%
	}
	
	.c-dropdown--sort .c-dropdown__select ul {
		top: 2em;
		background-color: rgba(255, 255, 255, .9);
		padding: 0
	}
	
	.c-group__options:before,
	.c-dropdown--sort .c-dropdown__select ul:before {
		top: -3px;
		box-shadow: -3px 3px 3px 0 #ccc
	}
	
	.c-dropdown--sort .c-dropdown__select ul li:first-of-type {
		border-radius: 2mm 2mm 0 0
	}
	
	.c-dropdown--sort .c-dropdown__select ul li:last-of-type {
		border-radius: 0 0 2mm 2mm
	}
	
	.c-group__options a:not(:last-of-type) {
		border-bottom: 1px solid #ddd
	}
	
	.c-group__options a:first-child {
		border-radius: 2mm 2mm 0 0
	}
	
	.c-group__options a:last-child {
		border-radius: 0 0 2mm 2mm
	}
	
	.c-dropdown__select ul li,
	.c-group__link,
	.c-menuLv1 a {
		color: #444;
		padding: 6px .5em
	}
	
	.sf-menu ul a:not(:last-child) {
		margin-right: 1em
	}
	
	.sf-menu ul a:not(:last-child):after {
		position: absolute;
		right: -.5em;
		content: "▸"
	}
	
	.c-menuLv1 {
		text-align: center;
		font-size: 0;
		white-space: nowrap;
		user-select: none;
		-webkit-user-select: none
	}
	
	.c-menuLv1>li {
		display: inline-block;
		float: none;
		text-align: left;
		border-radius: 2mm 2mm 0 0;
		transition: .1s
	}
	
	.c-menuLv1 li:hover>a {
		color: #fff
	}
	
	.c-menuLv1 ul li:first-child {
		border: none
	}
	
	.c-menuLv1 ul li {
		border-color: rgba(0, 0, 0, .2) !important
	}
	
	.c-menuLv1 ul ul {
		left: 100%
	}
	
	.c-menuLv1 li:hover {
		text-shadow: 0 1px 3px #000;
		background: linear-gradient(0deg,
				#284 0,
				#195 30%,
				#195 75%,
				#284 100%);
		transition: color .1s, background-color .1s
	}
	
	.c-menuLv1 li:not(:hover) {
		background-color: transparent;
		text-shadow: none;
		transition: color .2s, background-color .2s
	}
	
	.c-menuLv1 ul {
		background: rgba(255, 252, 242, .75);
		box-shadow: 2px 2px 3mm rgba(0, 0, 0, .4);
		border-radius: 0 3mm 3mm 3mm;
		z-index: 2;
		backdrop-filter: blur(4mm);
		-webkit-backdrop-filter: blur(4mm)
	}
	
	.c-menuLv1 ul li:first-of-type {
		border-radius: 0 3mm 0 0
	}
	
	.c-menuLv1 ul li:last-of-type {
		border-radius: 0 0 3mm 3mm
	}
	
	.c-menuLv1 ul li:first-of-type:last-of-type {
		border-radius: 0 3mm 3mm 0
	}
	
	.c-menuLv1 a {
		border: none !important
	}
	
	.c-menuLv1 li {
		transition: color .2s, background-color .2s
	}
	
	.c-filter {
		background-color: rgba(255, 255, 255, .33);
		padding: 3px 5px
	}
	
	.c-filter:first-child {
		background-color: rgba(255, 255, 255, .4);
		overflow: hidden;
		padding: 4px 6px;
		border-radius: 2mm;
		opacity: .6;
		user-select: none;
		-webkit-user-select: none;
		box-shadow: 0 0 0 1px rgba(255, 255, 255, .4), inset 0 0 0 1px rgba(0, 0, 0, .2), inset 0 0 5px rgba(0, 0, 0, .5);
		transition: opacity 1s
	}
	
	.c-filter:first-child:hover {
		opacity: 1;
		transition: opacity .2s
	}
	
	.c-filter li a {
		display: inline-block;
		padding: .1em .5em;
		margin: 1px;
		border-radius: 2em
	}
	
	.c-filter .c-iconLink--gn,
	.c-filter .c-link--gn {
		font-weight: 400;
		color: #222;
		text-shadow: 1px 1px 1px #fff;
		background-color: rgba(0, 0, 0, .15);
		box-shadow: inset 0 0 0 1px #777, inset 0 1px 5px rgba(0, 0, 0, .5)
	}
	
	/* forum title */
	.l-breadcrumb {
		margin: 0
	}
	
	.t1,
	.t2 {
		display: inline-block;
		line-height: 1.5;
		padding: calc(10px - .4vw) 0
	}
	
	.o-heading {
		display: inline-block;
		min-width: 16.666%;
		padding: 0 10mm 1px 0;
		margin: 2mm 0 1mm;
		border-bottom: 3px solid #083
	}
	
	.c-breadCrumb {
		display: inline-block;
		text-shadow: 1px 1px 1px #fff;
		background-color: rgba(235, 235, 235, .7);
		padding: 3px;
		margin: 0 0 1mm;
		user-select: none;
		-webkit-user-select: none;
		border-radius: 8px 5mm 5mm 8px;
		box-shadow: 0 0 0 1px rgba(255, 255, 255, .4), inset 0 0 0 1px #888, inset 0 0 1mm #777
	}
	
	.c-breadCrumb li,
	.c-breadCrumb li a {
		line-height: 1.4
	}
	
	.c-breadCrumb li+li {
		padding-left: .8em
	}
	
	.c-breadCrumb li+li:before {
		content: "▸";
		top: 0;
		left: .1em;
		color: #999;
		background: none
	}
	
	.c-breadCrumb li a {
		color: #666;
		display: block
	}
	
	.c-breadCrumb li:first-child a {
		padding-left: .4em;
		border-radius: 7px 5mm 5mm 7px
	}
	
	.u-gapNextV--lg+.u-gapNextV--lg {
		margin: 0
	}
	
	.l-heading {
		margin: 0
	}
	
	.l-heading--lg {
		margin: 2px 0
	}
	
	.l-hotCardGroup {
		display: block;
		margin: 0 0 2mm;
		padding: 2mm 0;
		overflow: hidden;
		border-radius: 2mm;
		background-color: rgba(100, 100, 100, .1);
		box-shadow: 0 0 0 1px #ccc, inset 0 0 5px 1px #666
	}
	
	.l-hotCardGroup li {
		width: calc((100% - 12mm)/5);
		padding: 0;
		margin: 0 0 0 2mm
	}
	
	.l-hotCardGroup .c-articleCard__thumbnail {
		padding-top: 0;
		height: 60px;
		transition: height .8s
	}
	
	.l-hotCardGroup:hover .c-articleCard__thumbnail {
		height: 10vw;
		transition: height .3s .4s
	}
	
	.l-hotCardGroup .c-articleCard {
		opacity: .66;
		transition: opacity .8s
	}
	
	.l-hotCardGroup:hover .c-articleCard {
		opacity: 1;
		transition: opacity .3s
	}
	
	.widget {
		background: #ccc
	}
	
	.widget .thumbnail-wrap.zoom-in .thumbnail {
		transition: .3s
	}
	
	.c-articleCard {
		margin: 0;
		transition: .25s
	}
	
	.c-articleCard:hover {
		box-shadow: 0 1mm 4mm rgba(0, 0, 0, .7);
		transition: .1s
	}
	
	.c-articleCard .c-articleCard__thumbnail {
		background: #aaa
	}
	
	.c-articleCard .c-articleCard__desc {
		background-color: #d8dcd8;
		border-top: 1px solid #ccc;
		padding: 1mm 3mm !important
	}
	
	.c-articleCard:hover .c-articleCard__desc {
		color: #050;
		background: #eee
	}
	
	.l-cardSwiper__container {
		overflow: visible
	}
	
	.l-cardSwiper__item {
		text-align: center
	}
	
	.l-heading__title>.l-toolBar {
		float: right;
		margin: 0 6mm 0 0
	}
	
	.l-toolBar a .c-icon--social {
		border-radius: 50%;
		opacity: .66;
		box-shadow: 0 0 0 1px rgba(255, 255, 255, .8)
	}
	
	.l-toolBar a:hover .c-icon--social {
		margin: -1px 0 1px;
		opacity: 1;
		box-shadow: 0 0 0 1px rgba(255, 255, 255, .8), 0 1px 5px rgba(0, 0, 0, .8)
	}
	
	.c-icon--social {
		transition: .5s
	}
	
	.c-icon--social:hover {
		transform: scale(1.25);
		transition: .1s
	}
	
	.c-icon--social:before,
	.c-icon--social:after {
		background-size: 8mm;
		width: 8mm;
		height: 8mm;
		background-position: 50% 50%;
		border-radius: 50%
	}
	
	.c-textStatistics .c-icon:before,
	.c-textStatistics .c-icon:after {
		background-size: 10mm;
		width: 10mm;
		height: 10mm;
		padding: 2mm;
		background-color: rgba(255, 255, 255, .6);
		background-position: 50% 50%;
		border-radius: 50%
	}
	
	.l-wrap>.u-gapNextV--lg:nth-of-type(2) {
		padding: 2mm 5mm;
		margin: 1.5mm 0;
		background-color: #fff
	}
	
	/* right sidebar */
	.l-content .l-content__side {
		position: relative;
		left: 0;
		width: calc(360px + 8vw);
		padding: 5mm 10mm 5mm 6mm;
		z-index: 121;
		opacity: .6;
		border-radius: 3mm;
		box-shadow: 0 1mm 4mm #777;
		transition: left .8s .2s, opacity 1s;
		backdrop-filter: blur(3mm);
		-webkit-backdrop-filter: blur(3mm)
	}
	
	.l-content__side:hover {
		left: calc(-8vw - 300px);
		opacity: 1;
		transition: left .8s .3s cubic-bezier(0.1, 0.9, 0.1, 1), opacity .2s
	}
	
	.l-content__side .u-gapNextV--lg {
		margin-bottom: 4mm
	}
	
	.l-content__side .c-articleCard {
		margin-bottom: 2mm
	}
	
