style.css:
@-moz-document domain("www.mobile01.com") {
/*	===================== 廣告 =====================
		這一段擋廣告的語法會拖慢效能
		若執行效率差，請自行刪除這區塊 */
	div[id^="div-gpt-ad-"],
	div[class^="ad_"] {
		display: none !important
	}
	
	/* =============================================== */
	
	#ad_popUp,
	ins iframe,
	.ad-adblock,
	._popIn_recommend_article_ad,
	.c-group__title>span::before,
	.c-menuLv2__link[target="_blank"],
	.l-content__main>#next_page_2~.u-gapNextV--lg .l-pagination {
		display: none !important
	}
	
	::-webkit-scrollbar,
	::-webkit-scrollbar-track,
	::-webkit-scrollbar-corner {
		background: none;
	}
	
	::-webkit-scrollbar {
		max-width: 1mm;
		max-height: 1mm;
	}
	
	::-webkit-scrollbar-thumb {
		border-radius: 1mm
	}
	
	:root {
		--font-size1: calc(10pt + .32vw);
		--font-size2: calc(9pt + .28vw);
		--font-size3: calc(9pt + .15vw);
	}
	
	body {
		font-family: sans-serif;
		height: auto;
		min-height: 100vh
	}
	
	iframe body {
		background: transparent !important
	}
	
	body,
	article,
	.l-signedIn,
	.o-selectItemTitle,
	.c-group__options a,
	.c-dropdown__select ul li,
	.l-articlePage__publish blockquote,
	.imageset,
	.c-dropdown--sort .c-dropdown__select>li>span,
	.c-dropdown--sort .c-dropdown__select ul li,
	.c-checkbox__original>span,
	.c-radio__original>span,
	.c-selectItem__title,
	.l-toolBar__item .c-group .c-icon,
	#_popIn_side_pc ._popIn_recommend_art_title a,
	.c-scrollSpy__item {
		font-size: var(--font-size1) !important;
		line-height: 1.4
	}
	
	.l-listTable__thead .l-listTable__td,
	.l-excerpt__desc,
	.o-fSubMini,
	.c-breadCrumb__item,
	.c-breadCrumb li a,
	.c-menuLv1 a,
	.c-filter li a,
	.c-articleCard--small .c-articleCard__desc,
	.c-tabNav__wrap>li a,
	.l-jumpList li a,
	.l-jumpList li span,
	.o-fMini,
	.o-hashtag.is-mini,
	.o-selectItemTitle,
	.c-chartLegend>li,
	.l-footerLinks>li a {
		font-size: var(--font-size2) !important
	}
	
	.l-articlePage .o-fNotes,
	.l-listTable__td--time,
	.c-authorInfo__id,
	.l-footer__copyright .list {
		font-size: var(--font-size3) !important
	}
	
	.compensate-for-scrollbar {
		margin: 0
	}
	
	.l-footer__copyright {
		width: calc(100% - 10mm) !important;
		max-width: 1600pt
	}
	
	.background-side,
	.l-footer .l-wrap {
		width: auto !important
	}
	
	img {
		object-fit: contain
	}
	
	.l-container {
		min-height: 100vh;
		overflow: hidden;
		position: relative;
		width: auto
	}
	
	main.l-main,
	.content-side,
	.u-distinguish {
		background-color: transparent;
		overflow-x: hidden
	}
	
	.l-header .l-wrap,
	.l-header__left,
	.l-header__tools {
		display: flex;
	}
	
	.l-header__left {
		flex: 1 1 auto;
	}
	
	.l-header__tools {
		flex: 0 0 auto;
	}
	
	.l-main {
		min-height: 100vh
	}
	
	.content-side {
		width: 100%;
		padding: 2mm 0 3mm
	}
	
	.l-content,
	.l-content__main,
	.l-content__side {
		display: inline-block
	}
	
	.l-content {
		width: calc(100% + 380px + 8vw)
	}
	
	.o-heading::before {
		display: none
	}
	
	.l-tabulate__nav+.l-tabulate__content {
		margin: 2mm 0 0
	}
	
	.l-tabulate__action,
	.l-appearArea__trigger {
		padding: 0;
		margin: 0
	}
	
	.gsc-results .gsc-cursor-box,
	.l-tabulate__action .l-pagination,
	.u-tac .l-pagination,
	.l-appearArea__trigger .l-pagination {
		position: fixed;
		right: 12%;
		bottom: 3mm;
		padding: 1.5mm 3mm;
		z-index: 3;
		border-radius: 2.5mm;
		opacity: .5;
		transition: .3s
	}
	
	.gsc-results .gsc-cursor-box:hover,
	.l-tabulate__action .l-pagination:hover,
	.u-tac .l-pagination:hover,
	.l-appearArea__trigger .l-pagination:hover {
		opacity: 1;
		backdrop-filter: blur(3mm);
		-webkit-backdrop-filter: blur(3mm)
	}
	
	.gsc-control-cse, .gsc-control-cse .gsc-table-result {
		font-size: 1rem !important;
	}
	
	.l-pagination__page {
		display: inline-block
	}
	
	.l-pagination__page.is-active a.c-pagination {
		cursor: default
	}
	
	.gsc-results .gsc-cursor-box {
		margin: 0
	}
	
	.gsc-results .gsc-cursor-box .gsc-cursor-page {
		display: inline-block;
		min-width: 5mm;
		padding: .2em 5px;
		margin: 0 3px
	}
	
	.gsc-results .gsc-cursor-box .gsc-cursor-page,
	.l-pagination a,
	.c-pagination {
		font: 400 12pt sans-serif;
		color: #080;
		background-color: rgba(240, 248, 240, .8);
		background-image: none !important;
		border: none;
		border-radius: 8px;
		box-shadow: inset 0 0 0 1px #8a8;
		text-align: center;
		transition: color .1s,
			background-color .1s
	}
	
	.gsc-results .gsc-cursor-box .gsc-cursor-page:last-child {
		margin: 0
	}
	
	.c-pagination--prev::before {
		display: block;
		content: "▲";
		transform: rotate(-90deg)
	}
	
	.c-pagination--next::before {
		display: block;
		content: "▲";
		transform: rotate(90deg)
	}
	
	.c-link,
	.l-jumpList li .c-link {
		position: relative;
		display: inline-block;
		color: #223;
		padding: 1px .5em;
		overflow: hidden;
		text-overflow: ellipsis;
		border-radius: 1em;
		vertical-align: middle;
		transition: color .2s,
			background-color .2s,
			box-shadow .2s
	}
	
	.l-jumpList li .c-link {
		max-width: 45vw;
	}
	
	.c-link:hover,
	.l-jumpList li .c-link:hover {
		z-index: 2;
		transition: color 60ms,
			background-color 60ms,
			box-shadow 60ms
	}
	
	.l-content__side .c-link:hover {
		background: none;
		box-shadow: none
	}
	
	.c-link:active,
	.l-jumpList li .c-link:active {
		background-color: rgba(225, 225, 215, .5);
		box-shadow: inset 0 0 0 1px #152,
			inset 0 1px 1mm rgba(0, 0, 0, .4)
	}
	
	.l-listTable .c-link {
		display: inline-block;
		max-width: calc(100% - 1em)
	}
	
	.o-gotop {
		right: 5mm !important;
		bottom: 27mm;
		z-index: 5;
		cursor: pointer
	}
	
	.l-toolBar__item {
		display: inline-block;
		line-height: 1;
		padding: 0 2mm;
		border-right: 1px solid #fff
	}
	
	.l-articlePage .o-fNotes {
		line-height: 1.2
	}
	
	.c-popUp__content {
		border-radius: 3mm
	}
	
	.c-popUp__title {
		background-color: #ddd;
		border-radius: 3mm 3mm 0 0
	}
	
	.fancybox-slide--html .fancybox-close-small {
		top: 0;
		right: 0;
		border-radius: 0 3mm 0 2mm;
		overflow: hidden
	}
	
	/* profile */
	.c-textStatistics {
		border: none;
		border-radius: 3mm;
		margin-top: 5px;
		padding: 3mm 0;
	}
	
	.l-promptBlock,
	.l-helpLink,
	.l-personalPanel {
		padding: .5em 1em;
		border-radius: 3mm;
	}
	
	.l-personalPanel.is-empty {
		height: auto;
		padding: 2em 1em;
	}
	
	.c-panel .c-panel__item {
		background-color: rgba(200, 200, 200, .8);
		margin: 0 -1px -1px 0;
		border: none;
		transition: .2s
	}
	
	.c-panel .c-panel__item:hover {
		background-color: rgba(255, 255, 255, .7);
		transition: 0s
	}
	
	.c-chartLegend {
		max-width: 20em;
	}
	
	.l-formRow__title--lg {
		padding: 9px 3mm 9px 0
	}
	
	.l-formRow__title {
		min-width: 8em;
		padding: .4em 4mm 0 0
	}
	
	.l-formRow+.l-formRow {
		margin-top: 0
	}
	
	.l-formRow__content {
		padding: 4px
	}
	
	.l-formText,
	.c-radio,
	.c-checkbox,
	.c-textbox,
	.c-iconBox .c-iconBox__input {
		font-size: calc(13px + .25vw) !important;
		border-radius: 2mm;
		padding: 5px
	}
	
	.is-error .c-textbox {
		color: #522;
	}
	
	.l-formText {
		height: auto
	}
	
	.u-gapV--md {
		margin: .2em 0;
		padding: .3em .5em;
	}
	
	/* top menu */
	.l-header {
		position: relative;
		width: 100vw;
		box-shadow: 0 0 2mm #888;
		z-index: 5;
	}
	
	.l-header:hover .l-header__main,
	.l-header:hover .l-menu {
		transition: background-color .3s
	}
	
	.l-header .l-header__main {
		border: none;
	}
	
	.l-header__left {
		position: relative;
		height: 2.3em
	}
	
	.o-logo {
		height: calc(100% - .2em);
		margin: .1em 8mm .1em 0;
	}
	
	.l-header__group {
		padding: .4em 2mm 0 0;
		white-space: nowrap
	}
	
	.l-headerTools {
		padding: 0;
		height: auto
	}
	
	.l-headerTools__search {
		height: auto;
		padding: calc(.2em + .1vw) 3mm calc(.1em + .2vw)
	}
	
	.ui-autocomplete {
		z-index: 5;
		border-radius: 3mm;
	}
	
	.l-headerTools__login {
		padding: 0 0 0 3mm;
		height: 100%;
		vertical-align: middle
	}
	
	.l-headerTools__login--signedIn {
		padding-left: 4mm;
	}
	
	.l-signedIn__funs {
		margin-left: 2mm
	}
	
	.c-textbox.c-searchBox__input,
	.c-searchBox__search {
		border-radius: 1em;
	}
	
	.c-searchBox.is-active .c-searchBox__input {
		width: 16em;
		max-width: 30vw;
		padding: .3em 10mm .3em 4mm;
		transition: box-shadow 1s
	}
	
	.c-searchBox.is-active .c-searchBox__input:focus {
		border: none;
		transition: box-shadow .3s
	}
	
	.c-searchBox.is-active .c-searchBox__search,
	.c-searchBox.is-active .c-searchBox__topic {
		background-color: transparent !important;
	}
	
	.c-searchBox.is-active .c-searchBox__search::before,
	.c-searchBox.is-active .c-searchBox__topic::before {
		display: inline-block;
		width: 1px;
		height: 100%;
		content: "";
	}
	
	.c-group__options {
		padding: 0;
		top: 2em;
		right: -.5em;
		backdrop-filter: blur(2mm);
		-webkit-backdrop-filter: blur(2mm);
	}
	
	.c-group__options--left::before {
		left: 8%
	}
	
	.c-group__options--right::before {
		right: 25%
	}
	
	.c-dropdown--sort .c-dropdown__select ul {
		top: 2em;
		background-color: rgba(255, 255, 255, .9);
		padding: 0
	}
	
	.c-group__options::before,
	.c-dropdown--sort .c-dropdown__select ul::before {
		top: -2px;
		box-shadow: -3px 3px 3px 0 #ccc
	}
	
	.c-dropdown--sort .c-dropdown__select ul li:first-of-type {
		border-radius: 2mm 2mm 0 0
	}
	
	.c-dropdown--sort .c-dropdown__select ul li:last-of-type {
		border-radius: 0 0 2mm 2mm
	}
	
	.c-dropdown__select ul li.selected {
		background: none
	}
	
	.c-group__options a:not(:last-of-type) {
		border-bottom: 1px solid #ddd
	}
	
	.c-group__options a:first-child {
		border-radius: 2mm 2mm 0 0
	}
	
	.c-group__options a:last-child {
		border-radius: 0 0 2mm 2mm
	}
	
	.c-dropdown__select ul li,
	.c-group__link,
	.c-menuLv1 a {
		color: #444;
		padding: .4em .5em
	}
	
	.sf-menu a {
		padding: .5em !important;
	}
	
	.sf-menu ul a:not(:only-child) {
		position: relative;
		padding-right: 1.5em !important
	}
	
	.sf-menu ul a:not(:only-child)::after {
		position: absolute;
		right: .5em;
		content: "▸"
	}
	
	.c-menuLv1 {
		display: flex;
		flex-wrap: wrap;
		font-size: 0;
		width: 100%;
		white-space: nowrap;
	}
	
	.c-menuLv1>li {
		float: none;
		flex: 1 1 auto;
		max-width: 8rem;
		text-align: center;
		transition: .1s
	}
	
	.c-menuLv1 ul li:first-child {
		border: none
	}
	
	.c-menuLv1 ul ul {
		left: 100%
	}
	
	.c-menuLv1 li:not(:hover) {
		z-index: 1;
		background-color: transparent;
		text-shadow: none;
		transition: color .2s,
			background-color .2s
	}
	
	.c-menuLv1 li:hover {
		z-index: 20;
		background-color: transparent;
		transition: color .1s,
			background-color .1s
	}
	
	.c-menuLv1>li:hover>a:last-child {
		border-radius: 2mm;
	}
	
	.c-menuLv1>li:hover>a:not(:last-child) {
		border-radius: 2mm 2mm 0 0
	}
	
	.c-menuLv1 ul {
		text-align: left;
		border-radius: 0 3mm 3mm 3mm;
		z-index: 2
	}
	
	.c-menuLv1 ul li:first-of-type a {
		border-radius: 0 3mm 0 0
	}
	
	.c-menuLv1 ul li:last-of-type a {
		border-radius: 0 0 3mm 3mm
	}
	
	.c-menuLv1 ul li:first-of-type:last-of-type a {
		border-radius: 0 3mm 3mm 0
	}
	
	.c-menuLv1 a {
		border: none !important
	}
	
	.c-menuLv1 ul {
		transition: color .2s, background-color .2s;
		backdrop-filter: blur(5mm);
		-webkit-backdrop-filter: blur(5mm)
	}
	
	.c-filter {
		padding: 3px 5px
	}
	
	.c-filter:first-child {
		overflow: hidden;
		padding: 4px 6px;
		border-radius: 2mm;
		opacity: .6;
		transition: opacity 1s
	}
	
	.c-filter:first-child:hover {
		opacity: 1;
		transition: opacity .2s
	}
	
	.c-filter li a {
		display: inline-block;
		padding: .15em .5em;
		margin: 1px;
		border-radius: 2em
	}
	
	.c-filter .c-iconLink--gn,
	.c-filter .c-link--gn {
		font-weight: 400;
	}
	
	/* forum title */
	.l-breadcrumb {
		margin: 0
	}
	
	.t1,
	.t2 {
		display: inline-block;
		line-height: 1.5;
		padding: calc(10px - .4vw) 0;
	}
	
	.t1,
	.o-heading {
		position: relative;
		display: inline-block;
		line-height: 1.25;
		padding: 0 .5em 1pt;
		margin: 2mm 0 1mm -.5em;
		z-index: 1;
	}
	
	.t1::after,
	.o-heading::after {
		position: absolute;
		width: 80%;
		max-width: 4em;
		height: 4mm;
		content: "";
		top: .6em;
		right: 0;
		z-index: -1;
		transform: rotate(-8deg);
	}
	
	.c-breadCrumb {
		display: inline-block;
		padding: 3px;
		margin: 0 0 1mm;
		border-radius: 8px 5mm 5mm 8px;
	}
	
	.c-breadCrumb li,
	.c-breadCrumb li a {
		line-height: 1.4
	}
	
	.c-breadCrumb li+li {
		padding-left: .8em
	}
	
	.c-breadCrumb li+li::before {
		content: "▸";
		top: 0;
		left: .1em;
		background: none
	}
	
	.c-breadCrumb li a {
		display: block
	}
	
	.c-breadCrumb li:first-child a {
		padding-left: .4em;
		border-radius: 7px 5mm 5mm 7px
	}
	
	.u-gapNextV--lg+.u-gapNextV--lg {
		margin: 0
	}
	
	.l-heading {
		margin: 0 .3em
	}
	
	.l-heading--lg {
		margin: 2px 0
	}
	
	.l-hotCardGroup {
		display: flex;
		margin: 0 0 2mm;
		padding: 1mm;
		overflow: hidden;
		border-radius: 2mm;
		background-color: rgba(100, 100, 100, .1);
		box-shadow: 0 0 0 1px #888,
			inset 0 0 5px 1px #666
	}
	
	.l-hotCardGroup li {
		flex: 1 1 20%;
		padding: 0;
		margin: 1mm;
	}
	
	.l-hotCardGroup .c-articleCard__thumbnail {
		padding-top: 0;
		height: 60px;
		transition: height .8s
	}
	
	.l-hotCardGroup:hover .c-articleCard__thumbnail {
		height: 10vw;
		transition: height .3s .4s
	}
	
	.l-hotCardGroup .c-articleCard {
		opacity: .5;
		transition: opacity .8s
	}
	
	.l-hotCardGroup:hover .c-articleCard {
		opacity: 1;
		transition: opacity .3s
	}
	
	.l-articleCardDesc {
		height: 2.8em;
	}
	
	.widget .thumbnail-wrap.zoom-in .thumbnail {
		transition: .3s
	}
	
	.c-articleCard {
		margin: 0;
		transition: .25s
	}
	
	.c-articleCard:hover {
		transition: .1s
	}
	
	.c-articleCard .c-articleCard__desc {
		padding: 1mm 3mm !important
	}
	
	.l-cardSwiper__item {
		text-align: center
	}
	
	.l-heading__title>.l-toolBar {
		float: right;
		margin-right: 4mm
	}
	
	.c-icon--social {
		border-radius: 50%;
		opacity: .75;
		transition: .5s
	}
	
	.c-icon--social:hover {
		margin: -1px 0 1px;
		opacity: 1;
		transition: .1s
	}
	
	.docking_share .c-icon--social:hover {
		margin-left: 10%;
		transform: scale(1.25);
	}
	
	.c-icon--social::before,
	.c-icon--social::after {
		background-size: 2rem;
		width: 2rem;
		height: 2rem;
		background-position: 50% 50%;
		border-radius: 50%
	}
	
	.c-textStatistics .c-icon::before,
	.c-textStatistics .c-icon::after {
		background-size: 10mm;
		width: 10mm;
		height: 10mm;
		padding: 2mm;
		background-position: 50% 50%;
		border-radius: 50%
	}
	
	.l-wrap>.u-gapNextV--lg:nth-of-type(2) {
		padding: 2mm 5mm;
		margin: 1.5mm 0;
	}
	
	/* right sidebar */
	.l-content .l-content__side {
		border-radius: 3mm;
	}
	
	.l-content__side .u-gapNextV--lg {
		margin-bottom: 4mm
	}
	
	.l-content__side .c-articleCard {
		margin-bottom: 2mm
	}
	
	.l-content .l-content__main {
		padding: 3.5mm 5mm 5mm;
		margin-right: 2mm;
		width: calc(100vw - 54mm);
	}
	
	/* forum topic list */
	.c-dropdown--sort .c-dropdown__select>li>span,
	.c-dropdown--sort .c-dropdown__select>li>span:hover {
		font-weight: 500;
		transition: .2s;
	}
	
	.c-dropdown__select ul {
		width: calc(100% - 4px);
		padding: 0;
		border: none;
		backdrop-filter: blur(3mm);
		-webkit-backdrop-filter: blur(3mm)
	}
	
	.c-dropdown__select ul li {
		min-width: 4em !important;
		background: none
	}
	
	.l-listTable {
		overflow: hidden;
	}
	
	.l-listTable__tr {
		border-bottom: none;
	}
	
	.l-listTable__thead .l-listTable__td {
		font-weight: 400;
	}
	
	.l-listTable__tbody .l-listTable__tr {
		transition: 160ms
	}
	
	.l-listTable__tbody .l-listTable__tr:hover {
		transition: 0s
	}
	
	.l-tab .l-listTable__tr>.l-listTable__td:nth-of-type(even) {
		background-color: transparent
	}
	
	.l-listTable .l-listTable__td {
		position: relative;
		padding: 0;
		overflow: hidden;
	}
	
	.l-listTable__td:first-child {
		padding: 0
	}
	
	.c-listTableTd--attach .c-listTableTd__attach {
		top: .55em;
		left: 3mm
	}
	
	.c-listTableTd--attach .c-listTableTd__title {
		padding-left: calc(12.5mm + .7vw)
	}
	
	.o-fNotes {
		margin: 0 1mm
	}
	
