	/* =============================================== */
	@keyframes clickPulse {
		0% {
			top: 0
		}
	
		10% {
			top: .6mm
		}
	
		30% {
			top: .3mm
		}
	}
	
	/* =============================================== */
	@media screen and (max-width: 1080px) {
	
		html,
		body,
		.l-container,
		.l-header,
		.l-main,
		.l-footer,
		.l-footerLinks {
			min-width: 550pt
		}
	
		.c-menuLv1 a {
			font-size: 4mm;
		}
	
		.t1,
		.t2 {
			font-size: 5mm !important
		}
	
		.l-wrap {
			width: calc(100% - 8mm);
		}
	
		.l-content,
		.l-content .l-content__main,
		.l-content .l-content__side {
			width: 100%;
			box-sizing: border-box;
		}
	
		.l-content .l-content__side {
			position: static;
			padding: 5mm 6mm;
			margin: 4mm 0 0;
		}
	
		.l-emCardGroup__main {
			width: auto;
		}
	
		.l-footerLinks {
			box-sizing: border-box;
			max-width: none;
		}
	}
	
	@media screen and (min-width: 1080px) {
		body {
			overflow-x: hidden;
			scrollbar-width: none;
			-ms-overflow-style: none
		}
	
		html,
		body,
		.l-container,
		.l-header,
		.c-menuLv1,
		.l-main,
		.l-footer,
		.l-footerLinks {
			min-width: 1080px
		}
	
		.l-wrap {
			width: calc(100% - 24mm);
			min-width: 1050px;
		}
	
		.l-content .l-content__main {
			min-width: 950px
		}
	
		.l-content .l-content__side {
			position: relative;
			left: 0;
			width: calc(360px + 8vw);
			padding: 5mm 10mm 5mm 6mm;
			z-index: 4;
			opacity: .6;
			transition: left .8s .2s,
				opacity 1s;
			backdrop-filter: blur(3mm);
			-webkit-backdrop-filter: blur(3mm)
		}
	
		.l-content__side:hover {
			left: calc(-8vw - 300px);
			opacity: 1;
			transition: left .8s .3s cubic-bezier(0.1, 0.9, 0.1, 1),
				opacity .2s
		}
	
		.t1,
		.t2 {
			font-size: calc(8pt + .8vw) !important
		}
	}
	
	@media screen and (min-width: 1600pt) {
	
		.t1,
		.t2 {
			font-size: 30px !important
		}
	
		.l-wrap {
			max-width: 1600pt
		}
	
		.l-content__main {
			max-width: calc(1600pt - 28mm)
		}
	}
	
	@media screen and (min-width: 1680pt) {
		.l-content__side:hover {
			left: calc(40vw - 1030pt)
		}
	}
	
	@media screen and (min-width: 1250pt) {
	
		[itemprop="articleBody"] .imagearea,
		[itemprop="articleBody"]>img:not([title]):not([data-src*="//attach2.mobile01.com/images/emotions/"]):not(.slick-arrow),
		[itemprop="articleBody"]>div:not(.imageset) img:not([title]):not([data-src*="//attach2.mobile01.com/images/emotions/"]):not(.slick-arrow) {
			display: block;
			zoom: 1.2
		}
	}
	
	@media screen and (min-width: 1500pt) {
	
		[itemprop="articleBody"] .imagearea,
		[itemprop="articleBody"]>img:not([title]):not([data-src*="//attach2.mobile01.com/images/emotions/"]):not(.slick-arrow),
		[itemprop="articleBody"]>div:not(.imageset) img:not([title]):not([data-src*="//attach2.mobile01.com/images/emotions/"]):not(.slick-arrow) {
			zoom: 1.4
		}
	}
	
	@media screen and (min-width: 1750pt) {
	
		[itemprop="articleBody"] .imagearea,
		[itemprop="articleBody"]>img:not([title]):not([data-src*="//attach2.mobile01.com/images/emotions/"]):not(.slick-arrow),
		[itemprop="articleBody"]>div:not(.imageset) img:not([title]):not([data-src*="//attach2.mobile01.com/images/emotions/"]):not(.slick-arrow) {
			zoom: 1.6
		}
	}
	
	@media screen and (min-width: 2575pt) {
		.l-content .l-content__side {
			position: static;
			opacity: 1;
		}
	}
}
