style.css:
@-moz-document domain("dpreview.com")
{
	/* ------------- Start of AD -------------
			Remove this section if you want to see the ADs.
			*/
	.ad,
	.buyboxContainer,
	.buyboxesLocationSwitch,
	#amazonBuyboxContainer {
		display: none !important;
	}
	
	/* ------------- End of AD ------------- */
	::-webkit-scrollbar,
	::-webkit-scrollbar-track,
	::-webkit-scrollbar-corner {
		background: none;
	}
	
	::-webkit-scrollbar {
		max-width: 1mm;
		max-height: 1mm;
	}
	
	::-webkit-scrollbar-thumb {
		background: #4bd;
		border-radius: 1mm
	}
	
	:root {
		--font-size1: calc(9pt + .33vw);
		--font-size2: calc(9pt + .24vw);
		--font-size3: calc(8pt + .2vw);
	}
	
	textarea {
		background-color: #b8babf;
		border-radius: 1mm;
		box-shadow: inset 0 0 4px #333;
	}
	
	#mainBody {
		position: relative;
		min-width: 1000px;
		max-width: 100vw;
		padding: 0 0 8mm;
		background: radial-gradient(circle at 50vw 30vh,
				rgba(105, 100, 90, .7) 0,
				rgba(105, 100, 90, .5) 40vw,
				rgba(105, 100, 90, .3) 60vw,
				rgba(105, 100, 90, .2) 100vw) !important;
		overflow-x: hidden;
	}
	
