style.css:
@-moz-document url-prefix("http://www.eldarya.fr/"), url-prefix("http://www.eldarya.com/"), url-prefix("http://www.eldarya.com.br/"), url-prefix("http://www.eldarya.de/"), url-prefix("http://www.eldarya.es/"), url-prefix("http://www.eldarya.fi/"), url-prefix("http://www.eldarya.hu/"), url-prefix("http://www.eldarya.it/"), url-prefix("http://www.eldarya.pl/"), url-prefix("http://www.eldarya.ru/"), url-prefix("http://eldarya.fr/") {
#episode-list .episode .episode-number, #episode-summary .episode-number {
    background: none;
    top: -8px;
}

#ranking-guards li .ranking-guard-caption p:not(.ranking-guard-caption-monthly) {
box-shadow : none;    
}
#episode-list .episode .episode-conditions li.on, #episode-list .episode .episode-rewards li.on {
    color: #aade6b;
    font-weight: normal;
}
#ranking-guards .ranking-guard-position{
    background : none;
}
#main-menu ul {
    background-color: transparent;
    margin-bottom: 0px;
    padding: 0px;
    box-shadow: none;
    border-radius: 0px;
}
#main-menu ul:after {
    border-radius: 0px;
    background-size: 0px;
    box-shadow : none;
}
#player-infos .player-name, #player-infos .player-currencies {
    border: solid 2px #34386f;
}
 #player-infos .player-name.guard-3 #player-guard
  {
    background-image: url("http://zupimages.net/up/16/23/ph9t.png");
    background-repeat: no-repeat;
  }

  #player-infos .player-name.guard-2 #player-guard
  {
    background-image: url("http://www.zupimages.net/up/16/41/3lnx.png");
    background-repeat: no-repeat;
  }

  #player-infos .player-name.guard-4 #player-guard
  {
    background-image: url("http://zupimages.net/up/16/41/vso7.png");
    background-repeat: no-repeat;
  }
.index-carousel, #index-ranking, #index-news, #ranking-top-players .ranking-top-players-number{
    border : none
}
#episode-list > li .episode-conditions li.on, #ranking-requirements div.on, .ranking-winning-guard, #pet-info-evolve div.on {
    color: #aade6b;
    font-weight : normal;
}
#minigames-list li h2, #malls-list li h2 {
    color: #343671;
}
.flavr-button.disabled, .button.button.button-disabled {
    background-color: #323470;
    border-color: #323470;
    font-size: 13px!important;
}
.marketplace-itemsForSale-quantity, .inventory-quantity
  {
    font-size: 11px;
    text-shadow: none;
    right: 2px;
    bottom: 0px;
  }
 .marketplace-itemsForSale-item
  {
    height: 100px;
    margin: 3px;
    padding: 0px;
  }
#ranking-top-players .ranking-top-players-nickname, ul#table-top-players {
    font-size : 11px;
}
#container h1 {
    background-image: url(http://zupimages.net/up/17/30/jg3h.png);
}
#container h1 .title-end {
    background-image: none;
}
#library-npc-crush-heart{
    display : none;
}
ul#library-container .library-npc{
    background-color: #323470;
    border: none;
    box-shadow: none;
}
#library-container li.library-npc .library-npc-loveometer-level {
    border-radius: 0px;
        background-color: #4958a6;
}
#library-container li.library-npc .library-npc-loveometer{
    border-radius : 0px
                                            
}
.library-npc-loveometer span{
    margin-top: 25px;
    color: black;
}
.library-npc p{
    height : 100px;
    font-size : 11px;
    overflow : hidden;
}
li.library-npc h3 {
    color: #ffffff;
}
#library-npc-crush div{
    background-color: rgba(255, 255, 255, 0.14);
    border: none;
}
#library-container li.library-illustration
  {
    margin: 3px;
    padding: 0px;
    border-radius: 0px;
    border: 0px;
}
ul#library-container li.comingsoon{
    background-color: #323470;
    border: none;
}
#pet-info-affection-level div{
    background-image: none;
    background-color: #759c2f;
    height: 13px;
}
#pet-info-affection-level{
    background-image: none;
    height: 13px;
        width: 100%;
    background-color: #34386f;
}
#pet-info-racegender, #pet-info-age {
    font-size: 12px;
    color: rgb(31, 34, 67);
    margin: 6px 0px -5px;
}
#pet-info-racegender img {
    max-height: 9px;
}
.tooltip-content {
    border: none;
    font-size: 11px;
    font-family: trebuchet ms;
    font-weight: normal;
    color: black;
}
.tooltip-content h3 {
    color: #607ce6;
}
.marketplace-menu li, .marketplace-submenu li, #library-menu li, #marketplace-pagination-container .pagination a, .marketplace-itemsForSale-pagination a, .purchasesPagination a, .salesPagination a {
    display: inline-block;
    padding: 2px 10px;
    margin-right: 5px;
    border-radius: 0px;
    border: solid 0px #C0C0C0;
    background-color: rgba(255, 255, 255, 0.21);
    color: rgb(255, 255, 255);
}
.marketplace-menu a, #library-menu a, #marketplace-pagination-container .pagination a, .marketplace-itemsForSale-pagination a, .purchasesPagination a, .salesPagination a {
    font-size: 13px;
    font-family: Rubik, cursive;
    color: rgb(255, 255, 255);
    text-align: center;
    margin-bottom: 3px;
    font-weight: normal;
    text-transform: uppercase;
    text-decoration: none;
}
.marketplace-menu li.selected, .marketplace-submenu li.selected, #library-menu li.selected, #marketplace-pagination-container .pagination a.selected, .marketplace-itemsForSale-pagination a.selected,  .purchasesPagination a.selected, .salesPagination a.selected {
    background: #323470;
}
#ranking-guards .ranking-guard-position {
    top: -60px;
    font-family: rubik;
}
.flavr-button, .button.button, .flavr-button.purple, .button.button.button-purple {
        background-color: rgba(43, 49, 123, 0.53);
    border: none;
    border-radius: 10px;
    font-family: Rubik, cursive;
    font-size: 13px;
    color: rgb(255, 255, 255);
    padding: 5px 7px;
    cursor: pointer;
    display: inline-block;
    line-height: 15px;
    margin: 0 5px;
}
#pet-info-energy-level, #event-bonus-progressbar-fill, #messageBarLevel{
background-color: #5c66a5;
}
#pet-info-energy-level, #event-bonus-progressbar-fill, #pet-info-energy, #messageBar, #messageBarLevel{
border-radius : 0px !important;
}
#pet-info-energy span, #event-bonus-progressbar > span, #messageBar > span {
    text-shadow: none;
    font-weight: normal;
    font-size: 11px;
}
#main-bonus-progressbar .open{
        opacity: 0.5;
}
#main-bonus-progressbar > div.current:not(.transition_bonus){
    border :2px solid #5c66a5
}
#main-bonus-progressbar > div.current > .label, #main-bonus-progressbar > div.transition_bonus.current{
    background-color : #5c66a5;
}
#pet-info-food img {
    border: none;
    background: none;
}
.mapLocation:hover, .mapLocation.selected {
    background: rgb(129, 189, 33);
}
.mapLocation {
    border : 3px solid #041944;
    box-shadow: none;
    background: rgb(52, 56, 111);
}
#episode-list > li .episode-number, #episode-summary .episode-number {
    background-image: none;
    background-color: #373b76;
    border-radius: 100%;
    height: 50px;
    line-height: 50px;
    top: 7px;
}
#episode-list > li h2 span.episode-name, #episode-summary h2 span.episode-name{
    left: 0px;
        text-shadow: none;
}
.mall-category::after, .mall-product::after, .mall-variation::after, .mall-quantity-max, .mall-stock-limit, .mall-event-tip, .mall-blackFriday-tip {
    border-top: none;
    border-bottom: none;
}
#marketplace-right, #marketplace-itemsForSale, #marketplace-abstract-purchases, #marketplace-abstract-sales{
    background : none;
    box-shadow: none;
}
#marketplace-itemDetail-info-bid, #marketplace-itemDetail-info-buy, .bid-count {
    color: rgb(0, 0, 0);
}
.marketplace-assistance {
    background: rgba(52, 56, 111, 0.64);
    font-weight: normal;
    font-size: 12px;
}
ul.marketplace-itemsForSale-items {
    height: 230px!important;
}
#marketplace-itemsForSale-list {
    width: 100%;
    padding: 0px;
    height: 94%;
    margin-left: 0px;
}
img.abstract-icon {
    background-color: transparent;
    border-right: 1px solid rgba(58, 67, 131, 0.32);
}
.marketplace-abstract .abstract-icon{
    box-shadow : none;
}
.marketplace-abstract .abstract-name {
    font-family: Rubik, cursive;
    font-weight: normal;
    font-size: 17px;
    color: rgb(0, 0, 0);
    padding: 6px;
}
.marketplace-abstract .abstract-container {
    width: 424px;
}
.bag-helper-small {
    background-color: #3a4483;
    border-radius: 0px 0px 0px 9px;
}
.marketplace-search-item:hover {
    background: #707fba;
}
}
