       border-bottom: 2px;
}

.appbar-nav-menu li {
       /* Spacing between buttons */
       
       margin-left: 0 !important;
}

#appbar-nav .epic-nav-item-heading {
       /* Text-style when active */
       
       height: 48px;
       font-size: 12px;
       font-weight: 500;
       text-transform: uppercase;
       padding: 0px 24px 3px !important;
       border-bottom: 2px solid;
       box-shadow: none;
}
/* Button Ripple*/

.appbar-nav-menu li a {
       position: relative;
       overflow: hidden;
}

.appbar-nav-menu li a:after {
       content: '';
       display: block;
       position: absolute;
       left: 50%;
       top: 50%;
       width: 150px;
       height: 150px;
       margin-left: -75px;
       margin-top: -75px;
       background: rgba(0, 0, 0, 0.15);
       border-radius: 100%;
       transform: scale(0);
}

.appbar-nav-menu li a:not(:active)::after {
       animation: ripple 0.55s ease-out;
}

.appbar-nav-menu li a:after {
       visibility: hidden;
}

.appbar-nav-menu li a:focus:after {
       visibility: visible;
}
/*----------------------------------*/
/* Keyframes 1 */

@keyframes ripple {
       0% {
              transform: scale(0);
              opacity: 1;
       }
       100% {
              opacity: 0;
              transform: scale(1.2);
       }
}
