style.css:
@-moz-document domain("vk.com") {
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -- -- -- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */

body {
	font-family: Open Sans, Helvetica Neue !important;
	letter-spacing: 0px !important;
}

body, a, b, a b, b a, h1, h2 {
	color: var(--color6);
}

.body_im #page_header, .body_im #page_layout {
    width: 960px !important;
}	

.body_im #page_body, .body_im #footer_wrap {
	width: 795px !important;
}

.page_square_photo, a.page_post_thumb_wrap, input.text.ts_input {
	border-radius: 2px;
}

/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -- -- -- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */

.online:after, .nim-peer.online:after, .friends_user_row .online:after, .chat_tab_imgcont.online:after, .layers_shown .chat_tab_imgcont.online:after, .ts_contact_photo.online:after, span.fc_contact_photo.online:after, a.fc_contact_over span.fc_contact_photo.online:after {
    border-color: var(--color7);
}

.online.mobile:after, .nim-peer.online.mobile:after, .friends_user_row .online.mobile:after, .chat_tab_imgcont.online.mobile:after, .layers_shown .chat_tab_imgcont.online.mobile:after, .ts_contact_photo.online.mobile:after, span.fc_contact_photo.online.mobile:after, a.fc_contact_over span.fc_contact_photo.online.mobile:after {
    background-color: var(--color7);
}

.chat_tab_wrap:hover .chat_tab_imgcont.online:after, .layers_shown .chat_tab_wrap:hover .chat_tab_imgcont.online:after, .nim-dialog:not(.nim-dialog_deleted):hover .nim-peer.online:after, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-peer.online:after, a.fc_contact_over span.fc_contact_photo.online:after, a.ts_contact.active .ts_contact_photo.online:after {
	border-color: var(--color8);
}

.chat_tab_wrap:hover .chat_tab_imgcont.online.mobile:after, .layers_shown .chat_tab_wrap:hover .chat_tab_imgcont.online.mobile:after, .nim-dialog:not(.nim-dialog_deleted):hover .nim-peer.online.mobile:after, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-peer.online.mobile:after, a.fc_contact_over span.fc_contact_photo.online.mobile:after, a.ts_contact.active .ts_contact_photo.online.mobile:after {
	background-color: var(--color8);
}

/*	.nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--photo .online:after, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--photo .online:after {
	background-color: #8ac176;
}	*/

/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -- -- -- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */

/* ------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------/
/------------------------------------------------------------------------------------------------------------- root -------------------------------------------------------------------------------------------------------------/
/------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------ */

	:root {
	--color1: rgba(09,09,10,.90);
	--color2: rgba(78,78,80,.30);
	--color3: rgba(29,29,30,.90);
	--color4: rgba(58,58,60,.30);
	--color5: #A7A7A9;
	--color6: #F7F7F9;
	--color7: #0A0A0A;
	--color8: #1D1D1D;
	--shadow: 0 1px 0 0 rgba(78,78,80,.30), 0 0 0 1px rgba(78,78,80,.30);
	--filter1: hue-rotate(34deg) brightness(1.66) grayscale(0.77);	/*	F7F7F9	*/
	--filter2: hue-rotate(30deg) brightness(1.00) grayscale(0.88);	/*	A7A7A9	*/
	--filter3: hue-rotate(30deg) brightness(2.65) grayscale(0.98);
	--filter4: brightness(0.66);
	--filter5: brightness(0.97);
	--filter6: invert(0);
}	

.scroll_fix:before {
	background: url(https://s20.postimg.org/muwxlipgb/bgdark.jpg) left top;
	content: '';
	position: fixed;
	bottom: 0;
	left: 0;
	right: 0;
	top: 0;
	z-index: -20;
}

.scroll_fix:after {
	background: url(https://s20.postimg.org/muwxlipgb/bgdark.jpg) left top;
	content: '';
	filter: blur(2px);
	position: fixed;
	bottom: 0;
	left: 0;
	right: 0;
	top: 0;
	z-index: -10;
}

/* ---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */

/* ------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------/
/------------------------------------------------------------------------------------------------------------ extras ------------------------------------------------------------------------------------------------------------/
/------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------ */

/*[[radius_img]]*/ 

/*[[radius_audio]]*/

/* ---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */

/* -------------------------------------------------/
/--------------------- sidebar ---------------------/
/------------------------------------------------- */

#ads_left {
	display: none !important;
}

#ads_left.ads_left_empty+.left_menu_nav_wrap {
	border-top: var(--color2) solid 1px;
	padding-top: 14px;
}

.side_bar_inner {
	background: var(--color1);
	border-radius: 2px;
	box-shadow: var(--shadow);
	margin: 57px 0 0 -8px !important;
	margin-top: 57px;
	padding: 6px 0 6px 8px;
	position: fixed !important;
}

#side_bar .more_div {
	border-color: var(--color2);
	margin: 9px 0 0 0;
}

#side_bar ol li .left_row {
	border-left: transparent solid 2px;
	border-radius: 0;
	color: var(--color5);
	padding: 2px 6px 2px 3px;
	transition: background 0.14s, border 0.14s, color 0.14s;
}

#side_bar ol li .left_row:hover {
	background: var(--color2);
	border-left: var(--color6) solid 2px;
	border-radius: 0;
	color: var(--color6);
}

#side_bar .left_icon {
	filter: var(--filter1) !important;
	opacity: 0.65;
	transition: opacity 0.14s;
}

#side_bar ol li .left_row:hover .left_count_wrap, .left_count_wrap {
	margin: 2px;
	padding: 4px 6px;
}

#side_bar ol li .left_row:hover .left_count_wrap {
	background: transparent;
	color: var(--color6);
}

.left_menu_nav_wrap {
	border-top: var(--color2) solid 1px;
}

.left_menu_nav, .left_menu_more:after {
	color: var(--color5);
	transition: color 0.14s, opacity 0.14s;
}

.left_menu_nav:hover {
	color: var(--color6);
	text-decoration: none;
}

.left_menu_more:after {
	filter: var(--filter1) !important;
	height: 6px;
	width: 13px;
}

.left_count_wrap {
	background: var(--color2);
	color: var(--color5);
	transition: background 0.14s, color 0.14s;
}

.left_count_wrap .inl_bl {
	margin-top: -1px;
}

.left_count_wrap .inl_bl:before {
	content: '+';
}

.left_settings {
	margin: -30px 0 0 -26px;
}

.left_settings_inner {
	filter: var(--filter1) !important;
}

/* - - - - - - - - - - - - - - - - - - - - - - - - */

.quick_login .label {
	color: var(--color5);
}

.quick_login input.dark {
	background: var(--color4);
	border-color: var(--color2);
	color: var(--color5);
	transition: background 0.14s, color 0.14s;
	width: 141px;
}

.quick_login input.dark:focus {
	background: var(--color2);
	color: var(--color6);
}

.quick_login .flat_button, .quick_login .forgot {
	width: 141px;
}

/* ----------------------------------------------- */

/* -------------------------------------------------/
/--------------------- header ----------------------/
/------------------------------------------------- */

#page_header_cont .back {
	background: var(--color1);
	box-shadow: var(--shadow);
}

input.text.ts_input {
	background-color: var(--color4);
	border-radius: 2px;
	filter: var(--filter2) !important;
	color: var(--color5);
	transition: filter 0.14s, color 0.14s;
}

input.text.ts_input::-webkit-input-placeholder {
	color: var(--color5);
}

input.text.ts_input~.placeholder .ph_content {
	color: var(--color5);
}

input.text.ts_input:focus {
	background-color: var(--color2);
	filter: var(--filter1) !important;
	color: var(--color6);
	opacity: 1;
}

.top_home_link .top_home_logo {
	filter: var(--filter4) !important;
}

.top_nav_btn .top_nav_btn_icon {
	filter: var(--filter3) !important;
}

.top_nav_btn#top_notify_btn:hover .top_nav_btn_icon {
    background-position: -5px -23px;
}

.top_nav_btn#top_audio:hover .top_nav_btn_icon {
    background-position: -5px -53px;
}

.top_nav_btn#top_notify_btn.active .top_nav_btn_icon {
	background-position: -65px -23px;
	filter: var(--filter5) !important;
}

.top_nav_btn#top_audio.active .top_nav_btn_icon {
	background-position: -65px -53px;
	filter: var(--filter5) !important;
}

.top_nav_btn, .top_audio_player, .top_nav_link {
	transition: background 0.14s;
}

.top_nav_btn.active, .top_nav_link.active {
	background: var(--color2);
}

.top_nav_btn:hover, .top_audio_player:hover, .top_nav_link:hover {
	background: var(--color2);
}

.top_audio_player {
	max-width: 462px;
}

.top_audio_player .top_audio_player_title/*, .top_nav_link*/ {
	color: var(--color6) !important;
}

.top_audio_player .top_audio_player_btn .top_audio_player_btn_icon {
	background-image: url(https://s20.postimg.org/9f9v2ksf1/head_icons.png);
	filter: var(--filter) !important;
	transition: opacity 0.14s;
}

.top_profile_arrow {
	opacity: 0.65;
}

/* - - - - - - - - - - - - - - - - - - - - - - - - */

.ts_wrap .input_back_content {
	color: var(--color5);
}

.ts_cont_wrap {
	background: var(--color1);
	box-shadow: var(--shadow);
	left: 171px;
}

.ts_contact_name {
	color: var(--color6);
}

.ts_contact_info {
	color: var(--color5);
}

a.ts_contact, a.ts_search_link {
	transition: background 0.14s;
}

a.ts_contact.active, a.ts_search_link.active {
	background: var(--color2);
}

a.ts_contact.active em.ts_clist_hl, a.ts_search_link.active em.ts_clist_hl {
	background: transparent;
}

em.ts_clist_hl {
	background: var(--color2);
	transition: background 0.14s;
}

.ts_search_sep {
	border-color: var(--color2);
	color: var(--color5);
}

.ts_contact_status {
	filter: var(--filter1) !important;
	opacity: 0;
	transition: opacity 0.14s;
}

/* - - - - - - - - - - - - - - - - - - - - - - - - */

#top_notify_wrap {
	background: var(--color1);
	border: none;
	border-radius: 2px;
	box-shadow: var(--shadow);
}

.no_rows, .top_notify_prefs_lnk {
	color: var(--color5);
	transition: color 0.14s;
}

.top_notify_prefs_lnk:hover {
	color: var(--color6);
	text-decoration: none;
}

.top_notify_header {
	background-color: transparent;
	border-bottom: 1px solid var(--color2);
	border-radius: 2px 2px 0 0;
	color: var(--color6);
}

.top_notify_show_all {
	background-color: transparent;
	border-radius: 0 0 2px 2px;
	border-top: 1px solid var(--color2);
	color: var(--color5);
	text-align: center;
	transition: background 0.14s, color 0.14s;
}

.top_notify_show_all:hover {
	background: var(--color2);
	color: var(--color6);
}

.top_notify_count, .top_nav_btn:hover .top_notify_count {
	border: 2px solid #111111;
	color: var(--color6);
}

.top_notify_cont .feedback_sticky_rows {
	border-left: 2px solid var(--color6);
}

/*.top_notify_cont .feedback_img, .feedback_rphoto, .notify_tt_img, .notify_tt_thumb, .feedback_sticky_img, .feedback_group_photo_img {
	border-radius: 2px;
}*/

.top_notify_cont {
	color: var(--color5);
}

.feedback_sticky_text .group_link, .feedback_sticky_text .mem_link {
	color: var(--color6);
}

.feedback_sticky_row.unread {
	background-color: var(--color2);
}

.feedback_sticky_gift_btn {
	background: url(https://s20.postimg.org/azdmsp80t/feedback_gift.png) no-repeat 50% 50%;
	filter: var(--filter) !important;
	opacity: 0.65;
}

.feedback_row_menu_icon {
	background: url(https://s20.postimg.org/pu286vhlp/feedback_arrow.png) no-repeat 50% 50%;
	filter: var(--filter) !important;
	opacity: 0.65;
}

.tt_w.top_notify_tt {
	background: var(--color1);
	border: none;
	border-radius: 2px;
	box-shadow: 0 1px 0 0 var(--color2), 0 0 0 1px var(--color2);
}

.feedback_row_wrap.unread:not(.feedback_row_touched), .feedback_row_clickable:not(.feedback_row_touched):hover {
	background-color: transparent;
}

.feed_row+.feed_row_fb_hidden .feedback_row:first-child, .feed_row~.feed_row .feedback_row, .feed_row~.feed_row .feedback_sticky_row, .feedback_sticky_rows:not(:empty)+.feed_row .feedback_row {
	border-top: 1px solid var(--color2);
}

.feedback_row_wrap.unread .feedback_photo_icon {
	background-color: var(--color6);
	border-color: var(--color6);
}

.feedback_photo_icon, .feedback_row_wrap.unread .feedback_photo_icon {
	border-color: var(--color7);
	transition: border 0.14s;
}

.top_notify_cont .feedback_row:not(.dld).hover .feedback_photo_icon, .top_notify_cont .feedback_row:not(.dld):hover .feedback_photo_icon {
	border-color: var(--color8);
}

.top_notify_cont .feedback_row:not(.dld) {
	transition: background 0.14s;
}

.top_notify_cont .feedback_row:not(.dld):hover, .feedback_sticky_row.hover, .feedback_sticky_row:hover {
	background: var(--color2);
}

.top_nav_btn a b, .top_nav_btn b, .feedback_header .author, .feedback_header .group_link, .feedback_header .mem_link {
	color: var(--color6);
}

.top_notify_cont .feedback_header, .feedback_date {
	color: var(--color5);
}

.top_notify_cont .feedback_row_menu .ui_actions_menu:before, .ui_actions_menu_dummy_wrap.feedback_row_menu .ui_actions_menu:before, .eltt.eltt_bottom:before {
	border-bottom-color: var(--color2);
}

.top_notify_cont .feedback_row_menu .ui_actions_menu:after, .ui_actions_menu_dummy_wrap.feedback_row_menu .ui_actions_menu:after {
	border-bottom-color: var(--color1);
}

/* - - - - - - - - - - - - - - - - - - - - - - - - */

.eltt {
	background: var(--color1);
	border: 1px solid transparent;
	border-radius: 2px;
	box-shadow: 0 1px 0 0 var(--color2), 0 0 0 1px var(--color2);
}

.eltt.top_audio_layer {
	border-radius: 2px;
}

.eltt.eltt_bottom:after, .eltt.top_audio_layer:after {
	border-bottom-color: var(--color1);
	right: 22px;
}

.audio_page_player {
	background-color: transparent;
	border-bottom: 1px solid var(--color2);
}

.audio_row .audio_title_inner, .audio_row .audio_duration_wrap {
	color: var(--color5);
}

.audio_row.lyrics .audio_title_inner, .audio_layout, .audio_page_player .audio_page_player_title {
	color: var(--color6);
}

.audio_row.audio_row_current, .audio_row.audio_row_playing, .ui_gridsorter_cont .ui_gridsorter_moveable, .audio_row.audio_row_current:hover, .audio_row.audio_row_playing:hover, .audio_row:not(.audio_row_current):hover {
	background: var(--color2);
	transition: background 0.14s;
}

.audio_page_player .audio_page_player_duration {
	color: var(--color5);
	transition: color 0.14s;
}

.audio_page_player .audio_page_player_duration:hover {
	color: var(--color6);
}

.audio_page_player .audio_page_player_play .icon {
	background-color: #F7F7F9;
	background-image: url(https://s20.postimg.org/giyjsvyl9/audio_player_controls.png);
	filter: var(--filter) !important;
}

.audio_page_player .audio_page_player_next .icon, .audio_page_player .audio_page_player_prev .icon, .audio_page_player .audio_page_player_btns .icon {
	background-image: url(https://s20.postimg.org/giyjsvyl9/audio_player_controls.png);
	filter: var(--filter) !important;
}

.audio_play {
	background-color: #F7F7F9;
	background-image: url(https://s20.postimg.org/gwzvshiot/audio_playpause.png);
	filter: var(--filter) !important;
}

.audio_row.audio_row_current .audio_duration {
	color: var(--color5);
	transition: color 0.14s;
}

.audio_row.audio_row_playing.audio_row_current .audio_duration {
	color: var(--color6);
}

.audio_page_player .audio_page_player_play, .audio_play {
	opacity: 0.65;
	transition: opacity 0.14s;
}

.audio_page_player ._audio_page_player_play.audio_playing, .audio_row.audio_row_playing .audio_play, .audio_row.audio_row_playing:hover .audio_play, .audio_row.audio_row_current:hover .audio_play, .audio_row.audio_row_playing:hover .audio_play, .audio_play:hover {
	opacity: 1;
}

.audio_page_player .audio_page_player_next, .audio_page_player .audio_page_player_prev, .audio_page_player .audio_page_player_btns .icon {
	opacity: 0.65;
	transition: opacity 0.14s;
}

.audio_page_player .audio_page_player_btns .icon:hover {
	opacity: 1;
}

.audio_hq_label_show .audio_row.hq .audio_hq_label, .audio_search_rows .audio_row.hq .audio_hq_label {
	background-image: url(https://s20.postimg.org/mmg4csov1/audio_hq.png);
	filter: var(--filter) !important;
}

.audio_row .audio_acts .audio_act>div, .audio_delete_current_btn, .audio_album_edit_btn .audio_album_btn_icon {
	background-image: url(https://s20.postimg.org/j6o038rml/common.png);
	filter: var(--filter) !important;
}

.audio_album_recom_btn .audio_album_btn_icon {
	background-image: url(https://s20.postimg.org/giyjsvyl9/audio_player_controls.png);
	filter: var(--filter) !important;
}

.downloadAllNew.audio_album_btn {
	background: url(https://s20.postimg.org/rz4yqxcrh/icon_download.png) 5px 4px no-repeat;
	filter: var(--filter) !important;
}

a.downloadButton {
	background: url(https://s20.postimg.org/rz4yqxcrh/icon_download.png) no-repeat !important;
	filter: var(--filter) !important;
}

.audio_row .audio_acts .audio_act {
	opacity: 0.65;
	transition: opacity 0.14s;
}

.audio_inline_player .slider .slider_amount, .audio_page_player .slider .slider_amount, .audio_inline_player .slider .slider_handler, .audio_page_player .slider .slider_handler {
	background-color: var(--color6);
}

.audio_inline_player .slider .slider_back, .audio_page_player .slider .slider_back {
	background-color: var(--color5);
}

.audio_inline_player .slider .slider_slide, .audio_page_player .slider .slider_slide {
	background-color: rgba(78,78,80,.50);
}

.audio_layer_bottom {
	background-color: transparent;
	border-top: 1px solid var(--color2);
	color: var(--color6);
}

.audio_rows_header .ui_search {
	background: transparent;
	border-bottom: 1px solid var(--color2);
	transition: background 0.14s;
}

.audio_rows_header.fixed {
	background: var(--color1);
}

/*input.ui_search_field {
	background: url(https://s20.postimg.org/j6slgzh0d/search_icon.png) no-repeat 0;
	border-left: 24px solid transparent;
	filter: var(--filter) !important;
	color: var(--color6);
	transition: background 0.14s;
}

input.ui_search_field:focus {
	background: url(https://s20.postimg.org/c4unusdel/search_icon_focus.png) no-repeat;
	background-position: 0;
	filter: var(--filter) !important;
}*/

input.ui_search_field::-webkit-input-placeholder {
	color: var(--color5);
}

input.ui_search_field~.placeholder .ph_content {
	color: var(--color5);
}

input.ui_search_field:focus::-webkit-input-placeholder {
	color: var(--color6);
}

input.ui_search_field:focus~.placeholder .ph_content {
	color: var(--color6);
}

.ui_suggester_results_list {
	background: var(--color1);
	border: none;
	border-bottom: 1px solid var(--color2);
	border-top: 1px solid var(--color2);
}

.ui_suggester_results_list ul li {
	color: var(--color5);
	transition: color 0.14s, background 0.14s;
}

.ui_suggester_results_list ul li.active, .ui_suggester_results_list ul li:hover {
	background: var(--color2);
	color: var(--color6);
}

.ui_search_filters_pane {
	border-top: 1px solid var(--color2);
}

.ui_search_filters_reset {
	background: url(https://s20.postimg.org/gs0pwk0rh/cross.png) no-repeat 50%;
	filter: var(--filter) !important;
}

.ui_multiselect_cnt .token, .ui_search_filters_pane .token {
	background-color: var(--color2);
}

.ui_multiselect_cnt .token .token_title, .ui_search_filters_pane .token .token_title {
	color: var(--color5);
}

.ui_multiselect_cnt .token .token_del, .ui_search_filters_pane .token .token_del {
	background-image: url(https://s20.postimg.org/gs0pwk0rh/cross.png);
	filter: var(--filter) !important;
}

.ui_search_fltr_control {
	background: url(https://s20.postimg.org/yzxjxg3x9/profile_arrow.png) no-repeat right 21px;
	filter: var(--filter) !important;
	color: var(--color5);
	transition: background 0.14s, color 0.14s;
}

.ui_search_fltr_control:hover {
	background: url(https://s20.postimg.org/dozzt6lst/profile_arrow_focus.png) no-repeat right 21px;
	filter: var(--filter) !important;
	color: var(--color6);
	text-decoration: none;
}

.audio_search_focused .audio_performer>em, .audio_search_focused .audio_title>em {
