style.css:
@-moz-document url-prefix("http://forumwar.herokuapp.com/room/") {
body {
    background:#282B30;
    font: 13px Verdana;
    margin-top:40px;
}
::-webkit-scrollbar { width:6px; height:6px; }
::-webkit-scrollbar-track { background-color:transparent; }
::-webkit-scrollbar-thumb { background-color:#2E3136; border-radius:5px;}
::-webkit-scrollbar-button { background-color:transparent; }
::-webkit-scrollbar-corner { background-color:transparent; }
p {
    color:#FFFFFF;
}
p {
    color:#FFFFFF;
}
a#connection {
    color:#2691c2;
    text-transform:capitalize;
    text-decoration:none;
}
a {
   color:#2691c2;
   text-decoration:none;
}
a:hover { /*ici*/
    cursor:pointer;
    text-decoration:underline;
}
label{
    color:#2691c2;
}
#chat {
    background-color:#36393E;
}

#msg_list>li.m10 { /* Normal*/
    color:#c3c4c5;
    font-size: 13px;
}

#msg_list>li.mi { /* Jaune*/
    color: #ffff7c;
    font-size: 13px;
    font-style: italic;
}

#msg_list>li.ma { /* Bleu*/
    color: #569FFF;
    font-size: 13px;
}

#msg_list>li.mg { /*Vert*/
    color: #30ff98;
    font-size: 13px;
}


#msg_list>li.m13 { /*Spectateur*/
    color: #fa8072;
    font-size: 13px;
}

#msg_list>li.m12{ /* Mort*/
	color: #070707;

}

#msg_list>li.mg>h2{ /*Spécial*/
    color:#ff0a80;
}

#right {
    background-color: #2E3136;
  position:relative;
  width:155px;

}

#player_list {
    background-color:transparent;
    width:210px;
}

li {
    color:#C4C4C4;
    font-size: 13px;
}

#dead_players {
    text-decoration:line-through;
    color:#FFFF00;
    font-size:13px;
    font-style:italic;

}

h3 {
    color:#FFFFFF;
    font-size: 13px;
    text-decoration: underline; 
}

#left {
    background-color: #2E3136;
}

#compo {  
    background-color: #2E3136;
}

#chat_text_input {
    background-color:#36393E;
    color:#c3c4c5;
    border-style:none solid none solid;
    border-color:#1c1e21;
    border-width:1px;
    outline:none;
    overflow:auto;
    width:300px;
    height:27px;
}

#chat_input {
    background-color:#2E3136;
    border-style:solid solid none none;
    border-color:#1c1e21;
    border-width:1px;

}

#content {
    border-style:solid;
    border-color:#1c1e21;
    border-width:1px;
    background-color:#2E3136;
}

#chat_text {
    border-style:none solid none solid; 
    border-width:1px;
    border-color:#1c1e21;
    height:95.3%;
}
button#chat_button {
    color: #fff;
    background: #7289da;
    font-size: 12px;
    border: none;
    padding: 1px 10px;
    border-radius: 3px;
    cursor: pointer;
    -webkit-transition: background .2s;
    transition: background .2s;
    display: inline-block;
	outline:none;
}
button#chat_button:hover {   
    color: #fff;
    background: #5a75c6;
}
button#chat_button:active {
    background-color: #49b482;
}
button#B_button {
    color: #fff;
    background: #7289da;
    font-size: 12px;
    border: none;
    padding: 1px 10px;
    border-radius: 3px;
    cursor: pointer;
    -webkit-transition: background .2s;
    transition: background .2s;
    display: inline-block;
    outline:none;
}
button#B_button:hover {   
    color: #fff;
    background: #5a75c6;
}
button#quit {
    font-size:12px;
    color: #fff;
    background: #7289da;
    border: none;
    padding: 1px 10px;
    border-radius: 3px;
    cursor: pointer;
    -webkit-transition: background .2s;
    transition: background .2s;
    display: inline-block;
    margin-left:5px;
	outline:none;
}
button#quit:hover {
    color: #fff;
    background: #5a75c6;
}
button#quit:active {
    background-color: #ed494c;
}
button#start_button {
    color: #fff;
    background: #7289da;
    font-size: 12px;
    border: none;
    padding: 1px 10px;
    border-radius: 3px;
    cursor: pointer;
    -webkit-transition: background .2s;
    transition: background .2s;
    display: inline-block;
    margin-left:5px;
	outline:none;
}
button#start_button:hover {
    color: #fff;
    background: #5a75c6;
}
button#join_button {
    color: #fff;
    background: #7289da;
    font-size: 12px;
    border: none;
    padding: 1px 10px;
    border-radius: 3px;
    cursor: pointer;
    -webkit-transition: background .2s;
    transition: background .2s;
    display: inline-block;
    margin-left:5px;
	outline:none;
}
button#join_button:hover {
    color: #fff;
    background: #5a75c6;
}
button#play{
    color: #fff;
    background: #7289da;
    font-size: 12px;
    border: none;
    padding: 1px 10px;
    border-radius: 3px;
    cursor: pointer;
    -webkit-transition: background .2s;
    transition: background .2s;
    display: inline-block;
    margin-left:5px;
	outline:none;
}
button#play:hover{
    color: #fff;
    background: #5a75c6;
}
button#S1_button{
    color: #fff;
    background: #7289da;
    font-size: 12px;
    border: none;
    padding: 1px 10px;
    border-radius: 3px;
    cursor: pointer;
    -webkit-transition: background .2s;
    transition: background .2s;
    display: inline-block;
    margin-left:5px;
	outline:none;
}
button#S1_button:hover{
    color: #fff;
    background: #5a75c6;
}
select#S1{
    outline:none;
    margin-left:4px;
}
button#S2_button {
    font-size:12px;
    color: #fff;
    background: #7289da;
    border: none;
    padding: 1px 10px;
    border-radius: 3px;
    cursor: pointer;
    -webkit-transition: background .2s;
    transition: background .2s;
    display: inline-block;
	outline:none;
}
button#S2_button:hover {
    color: #fff;
    background: #5a75c6;
}
p#B {
    color:white;
}
#compopane{
    background-color:#2E3136;
    color:white;
}
p#isranked{
    text-align:center;
}
#time>h3 { text-decoration: none; }
#edit_compo {
    background-color:#2E3136; 
    width: 468px;
    height: 546px;
    padding:3px;
    margin-top:-1px;
    position: absolute;
    border-style: solid;
    border-color: #1c1e21;
    border-width: 1px;
    z-index:10;
}
button#close:hover {
    color: #fff;
    background: #5a75c6;
}
button#close{
    color: #fff;
    background: #7289da;
    font-size: 12px;
    border: none;
    padding: 1px 10px;
    border-radius: 3px;
    cursor: pointer;
    -webkit-transition: background .2s;
    transition: background .2s;
    display: inline-block;
	outline:none;
}
button#confirmation_compo{
    color: #fff;
    background: #7289da;
    font-size: 12px;
    border: none;
    padding: 1px 10px;
    border-radius: 3px;
    cursor: pointer;
    -webkit-transition: background .2s;
    transition: background .2s;
    display: inline-block;
	outline:none;
}
button#confirmation_compo:hover{
    color: #fff;
    background: #5a75c6;
}
.block {
    margin-left:2px;
}
.simple_block {
        filter: brightness(50%);
        -webkit-filter: brightness(50%);
        -moz-filter: brightness(50%);
        -o-filter: brightness(50%);
        -ms-filter: brightness(50%);
}
.simple_block:hover {
    border-style: solid;
    border-color: #000000;
    border-width: 2px;
}
.simple_block_selected {
    
    border-width: 1px;
    border-color: #000000;
}
button#close {
    float: right;
}
button#confirmation_compo {
    float: right;
}
