	    transition: all 200ms linear;
}

input:focus{
	border-color:#7289DA;
    border-width:1px;
    border-style: none none solid none;
    border-radius:0px;

	box-shadow:none;

	padding:4px;
	width:25%;
	height:34px;

	font-family: 'Verdana';
		-webkit-transition: all 200ms linear;
	    -moz-transition: all 200ms linear;
	    -o-transition: all 200ms linear;
	    -ms-transition: all 200ms linear;
	    transition: all 200ms linear;
}

input.creer{
