}


/* PARAGRAPHS */ 

p{
	font-family: 'Verdana';
}

p#connectes{
	font-size:23px;
    font-family:Verdana;

	position:absolute;
		right:40;
		bottom:18; /* Centrer Verticalement */
	    top:32;
        color:#e6e8ed;

	    text-transform:capitalize;
