style.css:
@-moz-document domain("pocketcasts.com") {
#audio_player #audio_player_wrapper .player_podcast_title {
    width: 100%;
    min-width: 0px;
}
#players .player_controls {
    width: 100%;
}
#main {
    min-width: 0px;
}
#header {
    min-width: 0px;
}
#audio_player {
    min-width: 0px;
}
@media screen and (max-width:800px) {
    #main #content_container #content_row #content_middle .scrollable {
        width: 100%;
    }
    #content_left {
        display: none;
    }
    #content_right {
        display: none;
    }
    #main #content_container #content_row #content_middle .scrollable {
        width: 100%;
        margin-left: 0px;
    }
    .episodes_list .episode_row .episode_row_info {
        right: 10px;
    }
    #episodes_page .episode_row .episode_row_info .episode_more {
        display: none;
    }
    #episodes_page .episode_row .episode_row_info .episode_star {
        display: none;
    }
    #episodes_page .episode_row .podcast_title {
        margin: 0 60px 0 105px;
    }
    #episodes_page .episode_row .episode_title {
        margin: 0 60px 0 105px;
    }
    #episodes_page .episode_row .episode_button {
        left: 0px;
    }
    .episodes_list .episode_row .episode_image {
        left: 40px;
    }
    #episodes_page .episode_row .mark_as_played {
        display: none;
    }
    .episodes_list .episode_row .episode_row_info .episode_video_icon {
        display: none;
    }
    #players .player_controls .play_pause_button {
        margin-left: 0px;
    }
    #players .player_controls .skip_back_button {
        margin-left: 0px;
    }
    #players .player_controls .skip_forward_button {
        margin-left: 0px;
    }
    #players .player_progress {
        margin-left: 132px;
        margin-top: 0px;
        margin-right: 0px;
    }
    #players .player_progress .player_details .remaining_time {
        display: none;
    }
    #players .other_controls .playback_speed {
        display: none;
    }
    #players .volume_slider_panel {
        display: none;
    }
    #audio_player #audio_player_wrapper #audio_chromecast_image {
        display: none;
    }
    #players .seek_bar_container {
        padding-top: 0px;
        padding-right: 20px;
        padding-bottom: 0px;
        padding-left: 69px;
    }
}
@media screen and (max-width:500px) {
    #episodes_page {
        padding: 10px;
    }
    .episodes_list .episode_row .episode_row_info .episode_time {
        display: none;
    }
    #episodes_page .episode_row .podcast_title {
        margin: 0 0 0 105px;
    }
    #episodes_page .episode_row .episode_title {
        margin: 0 0 0 105px;
    }
    #players .player_episode_date {
        display: none;
    }
    #audio_player #audio_player_wrapper .player_podcast_title {
        padding: 0 0 0 72px;
    }
    #audio_player #audio_player_wrapper .player_episode {
        padding: 0 0 0 72px;
    }
}
@media screen and (max-width:400px) {
    #episodes_page {
        padding: 5px;
    }
    #episodes_page .episode_row .podcast_title {
        margin: 0 0 0 40px;
    }
    #episodes_page .episode_row .episode_title {
        margin: 0 0 0 40px;
    }
    #players .player_controls .skip_back_button {
        display: none;
    }
    #players .player_controls .skip_forward_button {
        display: none;
    }
    #players .player_progress .player_details .current_time {
        display: none;
    }
    #players .seek_bar_container {
        padding: 0 20px 0 60px;
    }
    #players .player_progress {
        margin-left: 0px;
        margin-top: 0px;
        margin-right: 0px;
    }
    .episodes_list .episode_row .episode_image {
        display: none;
    }
}
}
