style.css:
@-moz-document domain("ticktick.com") {
body,
.g-center,
.task, 
.td-header, .td-body, .td-body textarea, .td-footer,
.g-right .empty-detail-wrapper {
    background-color: #34373c;
    color: #eee;
}

.task.active {
    background-color: rgba(255,255,255,.1);
}

.text-def, 
.text-primary, 
.task .t-date,
.td-body .link-views {
    color: #eee;
}

.text-secondary {
	color: rgba(255,255,255,.54);
}

.t-line, .task:last-child .t-line.bottom {
    background-color: transparent;
}

.i-4, .i-2-1, 
.td-footer .i-2-1,
.dark-theme .active .i-5 {
    fill: rgba(255,255,255,.24);
}
.i-4, .i-4-1, .i-5,
.dark-theme .active .i-5 {
    color: rgba(255,255,255,.8);
}

.tl-quick div.input-like {
    background-color: rgba(255,255,255,.1);
}
div.input-like div.placeholder {
    color: rgba(255,255,255,.5);
}
}
