style.css:
@-moz-document domain(netvibes.com) {
li.item.simple-item.read.nv-liveViewItem a{color:#888!important}
.no-touch .rssItemList .item:hover,.no-touch .nv-feedList .item:hover,.uwa-tab.open > a,.uwa-tab.open > a:hover,.uwa-tab.selected > a,.no-touch .nv-condensed-item-renderer:hover,dl.nv-emailList dt a:hover{background-color:/*[[HoverColor1]]*/!important;color:#ccc}
li.uwa-tab a{background-color:#333!important;color:#ccc!important}
.no-touch .uwa-tab > a:hover{background-color:#444!important;color:/*[[HoverColor2]]*/!important}
.uwa-tab .unread,.EmailWidget .msgNumber .unread{color:#ff9595!important}
#header a:hover,.moduleHeader .title a:hover,#alertBar div.actions a:hover,#emailUsDialog .emailus-form .emailus-visit a:hover,#emailUsDialog .emailus-form .emailus-signin a:hover,.moduleContent a:hover{color:/*[[HoverColor2]]*/!important}
.linkOver{border-bottom:1px solid /*[[HoverColor2]]*/!important;color:/*[[HoverColor2]]*/!important}
div.rssreader-container.tickerView .rss-display{background-color:#39827d}
.blankMagazine .item,.borderlessMagazine .item,.blankMagazine .right,.borderlessMagazine .right,.blankMagazine .left,.borderlessMagazine .left{border:none!important}
.moduleContent a:visited{color:#999}
.gripper{opacity:0.5!important}
.detailed-item:nth-child(2n),.nv-thumbnailedList .even,.nv-richList .even,.nv-productList .even{background-color:#3b3b3b!important}
.status-edit,.twitter .description,.twitter .uwa-tab{opacity:0.6!important}
.twitter .updateForm .status-edit .inn textarea{background-image:url(http://www.netvibes.com/modules/twitter/img/background-dark.png?v=1)!important;color:#fff!important}
.twitter .updateForm.status-focus .status-edit .inn textarea{background-image:none!important}
.module.loading .moduleContent .module-loading-inner > .module-spinner{background:none}
.uwa-searchform input.search{background:none;background-color:#111;color:#ccc}
input.submit,button.submit,a.submit,.buttonClean{background:none;color:#ccc}
div.container.yellow_note{background:#222!important;background-color:#222!important}
.uwa-calendar span.title,.uwa-calendar div.monthView,.ical .ical-widget .weekView .event{background:#222!important;color:#ccc!important}
.ical .ical-widget .day,.ical .ical-widget .top .title,.ical .ical-widget{background-color:#222!important;color:#ccc!important}
.ical .ical-widget .currentMonth .day,.ical .ical-widget .weekView .currentMonth .event{background-color:#444!important;color:#ccc!important}
.ical .view-selector span,.ical .ical-widget .top .previous,.ical .ical-widget .top .next,.WikipediaWidget .entete{background-color:#111!important;color:#ccc!important}
.uwa-calendar .previous,.uwa-calendar .next{border-left:1px solid #222;border-right:1px solid #222}
div.Calc{filter:invert(100%)}
ul.uwa-tablist{background-color:transparent!important}
.WikipediaWidget div.result,.WikipediaWidget div.see_on_wiki{background:#333;background-color:#222;color:#ccc}
.WikipediaWidget{background:#333!important}
.WikipediaWidget .result a:link{color:/*[[WikipLinkColor]]*/}
.WikipediaWidget .result a.external{color:/*[[WikipLinkColor]]*/!important}
.WikipediaWidget .result a:visited{color:/*[[WikipLinkColor]]*/!important;filter:opacity(20%)}
.WikipediaWidget .result a.new{color:#e60000!important}
.WikipediaWidget .result h2{font-size:1.1em!important}
.RedditWidget div.head,a.back-link.box,div.score.box,a.comments.box{background-color:#222!important;color:#ccc}
div.item.autoclear.video-app.read.odd,div.item.autoclear.video-app.unread.odd{background-color:#444}
.moduleContent,div.nv-thumbnailedList,.rssreader-container,.galleryView,.galleryView .content,.galleryView .content .top,.galleryView .content .pager,.galleryView .content .pager div.thumbnail,.galleryView .content .crop,.galleryTwoView .content .middle .title span,.magazineView,.carouselView,.blankCarousel .title span,.blankCarousel .description,.borderlessCarousel .description,.carouselView .crop,.magazineView .crop,.scoopView .crop,.galleryView .crop,.blankMagazine .article h3,.borderlessMagazine .article h3,.blankMagazine .article p,.blankMagazine .share a,.borderlessMagazine .article p,.borderlessMagazine .share a,.blankMagazine .item h3,.borderlessMagazine .item h3,.blankMagazine .description p,.borderlessMagazine .description p,.scoopView .top,.loading-overlay,.module.loading .moduleContent,.nv-pager,a.main.link,body.nv-loading,.nv-smallmessage > *,.uwa-tab-panel,.uwa-button.light-grey,select,input,.WikipediaWidget div.search.nv-search.uwa-searchform.uwa-searchform-root{background-color:#333;color:#ccc}
li.item.simple-item.unread.nv-liveViewItem a,.twitter .count,.gmail a:link,.postIt textarea{color:#ccc!important}
.nv-feedList .item .age,.nv-thumbnailedList .item .age,.nv-richList .item .age,.nv-productList .age,.nv-thumbnailedList .read,.nv-thumbnailedList .read h3,.nv-thumbnailedList .read h3 a{color:#777!important}
.nv-thumbnailedList .item,.nv-richList .item,.nv-productList .item,.uwa-popup-menu-item,li.uwa-tab.uwa-tab-dropdown a{background-color:#222}
.read,.read h3,.read h3 a,.item .age,.nv-productList .age,.blankCarousel .description small,.borderlessCarousel .description small,.blankMagazine .item small,.borderlessMagazine .item small{color:#555!important}
h2.title a[href*="mail.google.com"],.ical .ical-widget .event span,.ical .ical-widget .event span.label{color:#00cbd8!important}
.ical .ical-widget .currentDay .day,.ical .view-selector span.selected{background-color:/*[[HoverColor1]]*/!important;color:#ccc!important}
.nv-condensed-item-renderer .item-time{box-shadow:0 0 10px 5px #333}
.nv-condensed-item-list-renderer.white-shadows{border-top:10px solid #333}
.nv-item-list-renderer.white-shadows{border-bottom:10px solid #333}
.nv-item-list-renderer.white-shadows .uwa-themed-scroller-shadow{border-color:#333;box-shadow:0 0 12px 10px #333}
.nv-condensed-item-renderer.focus .item-time,.no-touch .nv-condensed-item-renderer:hover .item-time{box-shadow:0 0 10px 5px /*[[HoverColor1]]*/}
.no-touch .nv-line-item-renderer:hover{background-color:/*[[HoverColor1]]*/;border-top:1px solid /*[[HoverColor1]]*/}
.nv-line-item-renderer:after{border-bottom:1px solid /*[[HoverColor1]]*/}
.nv-line-item-renderer:not(.build-image):not(.this-class-does-not-exist) .item-preview-wrapper:after{background-image:linear-gradient(90deg,rgba(255,255,255,0),#333)}
.nv-line-item-renderer.focus:not(.build-image):not(.display-inner-item) .item-preview-wrapper:after,.no-touch .nv-line-item-renderer:not(.build-image):not(.open):hover .item-preview-wrapper:after{background-image:linear-gradient(90deg,rgba(237,237,237,0),/*[[HoverColor1]]*/)}
.weather a:hover,.weather div,.weather span,.nv-condensed-item-renderer,.nv-line-item-renderer{background-color:#333}
.moduleContent a,.moduleContent a:link,.weather span.infos.day,.weather span.infos.temp,.nv-condensed-item-renderer .item-header,.nv-condensed-item-renderer .item-header:link,.nv-condensed-item-renderer .item-header:visited,.nv-line-item-renderer .item-title,.nv-line-item-renderer.build-image .item-title{color:#ccc}
.nv-condensed-item-renderer .item-preview,.nv-line-item-renderer.build-image .item-preview,.nv-line-item-renderer .item-preview-wrapper{color:#888}
.EmailWidget p{opacity:1!important}
}
