style.css:
@-moz-document regexp("http(s?)://us.bbcollab.com/collab/ui/session/(.*)") {
.side-panels, .side-panel div header, .side-panel div section, .side-panel .scroll-content, .side-panel .role-header, .button.appshare, .sharing-title span, .side-panel .participant-details, .side-panel div footer, div#panel-controls ul li, .accordion a, .accordion ul, .accordion li, .accordion button, .columns.checkbox-wrap label, textarea#message-input, div.notifications div div {
    background-color: #262626 !important;
    color: white !important;
}
textarea#message-input, div.notifications div div {
    border-radius: 10px;
}

div.notifications div div.notification {
    border: solid 1px white;
}

.participant-list-item {
    padding-left: 0px !important;
}
.activity-chat.user {
    background-color: #323232 !important;
}
.side-panel {
    border-left: solid 2px white;
}
div#panel-controls ul li:hover {
 	background-color: #262626 !important;   
}
}
