style.css:
/* ==UserStyle==
@name           ニコニコ動画カスタム：HTML5
@namespace      https://userstyles.org/styles/134653/html5
@version        0.4.6
@description    NicoVideo Style
@author         https://userstyles.org/styles/134653/html5
==/UserStyle== */

@-moz-document url-prefix("http://www.nicovideo.jp/watch"), url-prefix("https://www.nicovideo.jp/watch"){
  /*----------------------------------------------------------------
    ver 0.4.6 - https://userstyles.org/styles/134653/html5
  ----------------------------------------------------------------*/
  
  .BottomContainer-adContainer,
  .SideFollowAdContainer,
  .NicoSpotAdContainer,
  .MiniBannerAdContainer{
    display: none !important;
  }
  
  .is-middle .WatchAppContainer-main,
  .is-middle .BottomContainer,
  .is-middle .FooterContainer-content{
    width: 996px !important;
  }
  .WatchAppContainer-switchToFlash{
    min-width: 996px !important;
  }
  
  
  
  .BottomContainer-main{
    padding-right: 14px !important;
  }
  body.is-large .WatchAppContainer-main{
    width: 1180px !important;
  }
  
  .is-middle .FooterContainer{
    width: 100% !important;
    min-width: 996px !important;
  }
  
  /* Search */
  .HeaderContainer-searchBox{
    /*z-index: 3;*/
  } 
  
  
  

  /* detail */
  .VideoDescriptionContainer{
    border: 2px solid #444;
    padding: 6px 8px;
    min-height: 130px !important;
  }
  .VideoDescriptionContainer .VideoDescription{
    min-height: 114px !important;
  }
  .VideoDescriptionContainer .VideoDescriptionContainer-expander{
    left: auto !important;
    right: 2px !important;
    bottom: 2px !important;
    transform: translateX(0) !important;
  }
  .VideoDescriptionContainer .VideoDescriptionContainer-expander span{
    color: #888;
    font-size: 12px;
  }
  .VideoDescriptionExpander .VideoDescriptionExpander-switchCollapse::before{
    border-color: transparent transparent #888;
  }
  .VideoDescriptionExpander .VideoDescriptionExpander-switchExpand::before{
    border-color: #888 transparent transparent;
  }
  .VideoDescriptionExpander .VideoDescriptionExpander-button{
    box-shadow: none !important;
    border: 0px solid #444 !important;
    /*background: #444 !important;
    color: #fff !important;*/
    border-radius: 3px !important
  }
  /*.VideoDescriptionExpander .VideoDescriptionExpander-buttonCollapse::before{
    border-color: transparent transparent #fff !important;
  }
  .VideoDescriptionExpander .VideoDescriptionExpander-buttonExpand::before{
    border-color: #fff transparent transparent !important;
  }*/
  .is-large .VideoDescriptionContainer{
     width: 870px;
  }
  
  .OwnerNicoliveBanner{
    width: 700px;
  }
  .is-large .OwnerNicoliveBanner{
    width: 870px;
  }
  
  
  /* video meta */
  .WatchAppContainer-main .HeaderContainer{
    position: relative !important;
    
  }
  .VideoDescriptionContainer + .HeaderContainer-row{
    width: 290px !important;
    position: absolute !important;
    top: 64px !important;
    right: 0 !important;
    bottom: 0 !important;
    display: block !important;
    /*z-index: 0;*/
    min-height: 168px;
    margin: 0 !important;
  }
  .VideoMetaContainer p span,
  .VideoMetaContainer p{
    color: #888 !important;
  }
  .VideoMetaContainer time,
  .VideoMetaContainer span{
    color: #555 !important;
  }
  
  .VideoDescriptionContainer + .HeaderContainer-row .GridCell:first-child{
    position: absolute;
    top: 0 !important;
    right: 0;
    width: 290px;
    border: 1px solid #ddd !important;
    padding: 6px;
    background: #f9f9f9;
  }
  .VideoDescriptionContainer + .HeaderContainer-row .GridCell:last-child{
    position: absolute;
    bottom: 0px !important;
    right: 0;
    display: block !important;
    width: 100px;
    padding: 6px;
    background: #f9f9f9;
  }
  .VideoMetaContainer{
    display: block !important;
    height: auto !important;
  }
  .VideoMetaContainer > div{
    display: block !important;
    /*border: 0 !important;*/
    box-shadow: none !important;
  }
  .VideoMetaContainer > div > p,
  .VideoMetaContainer > div > span,
  .VideoMetaContainer > div > time{
    display: inline-block !important;
    /*min-width: 4.8em;*/
    margin: 0 !important;
  }
  .VideoMetaContainer > div > p{
    min-width: 4.8em;

  }
  .VideoMetaOverflowMenuContainer{
    text-align: right;
    position: absolute !important;
    bottom: -0px !important;
    right: 5px !important;
    z-index: 43 !important;
  }
  .VideoMetaOverflowMenuContainer .VideoMetaOverflowMenuOpenButton svg{
    fill: #4D4D4D !important;
  }
  .VideoMetaOverflowMenuContainer-menu{
    z-index: 4 !important;
    top: auto !important;
    bottom: 44px !important;
  }
  .VideoMetaOverflowMenuContainer .VideoMetaOverflowMenuOpenButton{
    height: 22px !important;
    padding: 0 !important;
    width: 20px !important;
  }
  .VideoMetaOverflowMenuContainer-menuBlock .VideoMetaOverflowMenuContainer-dicTextLink{
    padding-right: 1.2em;
  }
  
  .ChannelInfo-breadcrumbContainer{
    margin-bottom: 6px;
  }
  .VideoOwnerInfo-links,
  .ChannelInfo-links{
    width: 232px;
    max-width: 232px !important;
  }
  .VideoOwnerInfo .LoginRequirer.is-inline,
  .ChannelInfo .LoginRequirer.is-inline{
    position: absolute;
    top: 40px !important;
    right: 0px;
    text-align: right;
    /*width: 220px;*/
  }
  .VideoOwnerInfo .FollowButton,
  .ChannelInfo .FollowButton{
    margin: 0;
    width: 80px;
    display: inline-block;
  }
  .VideoOwnerInfo .FollowButton > div,
  .ChannelInfo .FollowButton > div{
    display: inline-block;
    margin-left: 4px;
    margin-top: 0;
    vertical-align: middle;
  }
  .FollowButton-button{
    width: 14px;
    height: 14px;
  }
  

  .GenreRankMeta,
  .VideoGenreMeta,
  .VideoViewCountMeta,
  .CommentCountMeta,
  .MylistCountMeta,
  .CategoryRankMeta{
    position: absolute !important;
    left: -198px !important;
    bottom: -33px !important;
    background: #2a2a2a!important;
    background: -webkit-linear-gradient(top, #2a2a2a, #0a0a0a, #2a2a2a)!important;
    background: linear-gradient(#2a2a2a, #0a0a0a, #2a2a2a) !important;
    border: 1px solid #000000 !important;
    display: block !important;
    padding: 2px 4px !important;
    width: 132px !important;
    z-index: 6 !important;
    height: 21px;
  }
  .is-autoResize .VideoViewCountMeta,
  .is-autoResize .CommentCountMeta,
  .is-autoResize .MylistCountMeta{
    left: -278px !important;
    width: 184px !important;
  }
  .CommentCountMeta{
    bottom: -53px !important;
  }
  .MylistCountMeta{
    bottom: -73px !important;
  }
  .VideoViewCountMeta,
  .CommentCountMeta,
  .MylistCountMeta{
    white-space: nowrap;
  }
  .VideoViewCountMeta span, .VideoViewCountMeta p,
  .CommentCountMeta span, .CommentCountMeta p,
  .MylistCountMeta span, .MylistCountMeta p{
    color: #ffffff !important;
    margin: 0 !important;
    font-size: 11px !important;
    font-weight: normal;
    vertical-align: middle;
  }
  .VideoViewCountMeta span,
  .CommentCountMeta span,
  .MylistCountMeta span{
    font-size: 12px !important;
  }
  .VideoViewCountMeta > span,
  .CommentCountMeta > span,
  .MylistCountMeta > span{
    width: 67px !important;
    text-align: right !important;
    display: inline-block !important;
  }
  .CommentCountMeta > span{
    width: 64px !important;
  }
  .MylistCountMeta > span{
    width: 54px !important;
  }
  .is-autoResize .VideoViewCountMeta > span,
  .is-autoResize .CommentCountMeta > span,
  .is-autoResize .MylistCountMeta > span{
    width: 115px !important;
  }
  .is-autoResize .CommentCountMeta > span{
    width: 112px !important;
  }
  .is-autoResize .MylistCountMeta > span{
    width: 102px !important;
  }
  
  .GenreRankMeta,
  .VideoGenreMeta,
  .CategoryRankMeta{
    left: auto !important;
    bottom: -75px !important;
    right: 3px !important;
    width: 164px !important;
    height: 61px !important;
    padding: 4px 4px 2px !important;
    text-align: center !important;
    background: none !important;
    border: 0 !important;
    z-index: 6 !important;
  }
  .CategoryRankMeta .CategoryRankMeta-title{
    width: 100% !important;
    margin: 0 0 1px !important;
    white-space: nowrap;
    overflow: hidden;
  }
  .CategoryRankMeta > span + span{
    margin: 0 0 3px 3px !important;
  }
  .VideoMetaContainer .CategoryRankMeta p,
  .VideoMetaContainer .CategoryRankMeta span{
    color: #bbb !important;
    font-size: 13px;
  }
  .CategoryRankMeta-categoryName{
    font-size: 11px !important;
  }
  .VideoMetaContainer p.CategoryRankMeta-title{
    color: #aaa !important;
    font-size: 11px !important;
  }
  .VideoGenreMeta a,
  .VideoMetaContainer .CategoryRankMeta a{
    color: #C3DFE4 !important;
  }
  
  .GenreRankMeta .GenreRankMeta-yesterdayRank{
    color: #aaa !important;
  }
  
  .GenreRankMeta{
    bottom: -92px !important;
  }
  .GenreRankMeta-highestPast{
    font-size: 11px;
    margin: 0;
    width: 10px;
    overflow: hidden;
    display: inline-block;
    position: relative;
    text-align: center;
    white-space: nowrap;
  }
  .GenreRankMeta-highestPast::after{
    content: "?";
    display: inline-block;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    padding-top: 2px;
    color: #C3DFE4;
    text-align: right;
    font-size: 12px;
    background: #0a0a0a !important;
  }

  .VideoUploadDateMeta{
    position: absolute !important;
    bottom: -73px !important;
    right: 1px !important;
    width: 166px !important;
    z-index: 6 !important;
    text-align: center !important;
    height: 61px !important;
    padding: 40px 4px 2px !important;
    background: #2a2a2a!important;
    background: -webkit-linear-gradient(top, #2a2a2a, #0a0a0a, #2a2a2a)!important;
    background: linear-gradient(#2a2a2a, #0a0a0a, #2a2a2a) !important;
    border: 1px solid #000000 !important;
  }
  .VideoUploadDateMeta-dateTimeLabel{
    font-size: 12px;
    font-weight: normal;
    color: #777;
    margin: 0;
  }
  .is-autoResize .VideoGenreMeta,
  .is-autoResize .GenreRankMeta,
  .is-autoResize .CategoryRankMeta,
  .is-autoResize .VideoUploadDateMeta{
    width: 192px !important;
  }
  .VideoUploadDateMeta p.VideoUploadDateMeta-title{
    display: none !important;
  }
  .VideoUploadDateMeta time.VideoUploadDateMeta-dateTimeLabel{
    color: #888 !important;
    font-weight: normal !important;
    font-size: 12px !important;
  }
  
  
  /* tag */
  .TagContainer{
    /*padding-right: 138px !important;*/
    padding-left: 0 !important;
    margin: 0 -0 8px !important;
    z-index: 6;
    width: 854px;
  }
  .is-large .TagContainer{
    width: 1030px;
  }
  .TagContainer-area{
    position: relative !important;
    z-index: 3;
  }
  .TagContainer-editButton{
    border-radius: 4px !important;
    position: relative;
    background-color: #069 !important;
  }
  .TagContainer-editButton:after{
    position: absolute;
    content: "登録タグ";
    background: #111;
    color: #fff;
    top: 0; left: 0; right: 0; bottom: 0;
    border-radius: 4px !important;
    line-height: 1.8;
  }
  .TagContainer-editButton:hover:after,
  .is-editing .TagContainer-editButton:after{
    display: none;
  }
  .TagContainer-area:first-child{
    padding-left: 1px !important;
    margin-right: 6px !important;
  }
  .TagItem{
    background: none !important;
    border: 0 none !important;
    padding-right: 24px !important;
    padding-left: 6px !important;
  }
  .TagItem-name{
    color: #222 !important;
    font-weight: normal !important;
  }
  .NicoDicIcon{
    border-radius: 0px !important;
    /*vertical-align: middle !important;*/
    margin-top: 1px;
  }
  .NicoDicIcon svg{
    display: none !important;
  }
  .NicoDicIcon.is-available{
    background: url(https://live.nicovideo.jp/img/2012/watch/tag_icon002.png) no-repeat 0px 1px rgba(0, 0, 0, 0) !important;
  }
  .NicoDicIcon.is-unavailable {
    background: url(https://live.nicovideo.jp/img/2012/watch/tag_icon003.png) no-repeat 0px 1px rgba(0, 0, 0, 0) !important;
  }
  .TagContainer.is-editing{
    padding-right: 6px !important;
    z-index: 8;
    box-shadow: 0 0 1px rgba(0, 0, 0, 0.3) !important;
  }
  .TagContainer.is-editing .TagItem.is-locked{
    padding-right: 6px !important;
    padding-left: 24px !important;
  }
  .TagContainer.is-editing .TagItem.is-locked.is-category{
    padding-right: 24px !important;
    padding-left: 12px !important;
  }
  


  
  /* video contents */
  .MainContainer{
    border: 2px solid #333;
    background: #666;
    /*padding: 2px;*/
    margin-top: 10px !important;
  }
  .MainContainer-player{
    width: calc(100% - 296px);
  }
  .is-autoResize .MainContainer-player{
    width: calc(100% - 376px);
  }
  
  
  .MarqueeContainer .Marquee{
    /*background-image: url(data:image/jpeg;base64,);*/ 
