style.css:
@-moz-document domain("kenfm.de") {
body {
    background: currentColor !important;
}
.post-article .post-article-inner {
    background-color: dimgrey !important;
}
#secondary {
    background: dimgrey !important;
}
p {
    color: white !important;
}
h1, h2, h3, h4, h5, h6 {
    color: white !important;
}
ol.comment-list li {
    background: dimgrey !important;
}
.content-post {
    background: dimgrey;
}
.content-post a h3,
.content-post a h4 {
    color: white !important;
    font-weight: 600 !important;
}
h2.line a {
    color: lightgrey;
}
h2.line span,
h3.line span {
    background: dimgrey !important;
    color: lightgrey !important;
}
h2.line:before,
h3.line:before {
    background: dimgrey !important;
}
.content-text {
    background-color: dimgrey !important;
}
a {
    color: lightblue !important;
}
#header-teaser {
    background: currentColor !important;
}
.teaser-post-02 {
    border-right: 15px solid dimgrey !important;
}
#mp3_download {
    background: transparent !important;
}
#mp3_download a {
    background: lightcoral !important;
}
#mp3_download a:hover {
    text-decoration: none;
    background: #9f1a00 !important;
}
.thumbnail-excerpt {
    color: white !important;
}
.post-article {
    background: dimgrey !important;
}
div.wprss-feed-meta > span {
    color: white !important;
}
.archive-post {
    background: dimgrey !important;
}
.wp-paginate a {
    background: transparent !important;
}
.post-stats {
    color: white !important;
}
}
