style.css:
@-moz-document domain("itmedia.co.jp") {
html#masterChannel-news div#masterBodyInner,
html#masterChannel-business div#colBoxTopStories.colBox.colBoxTopStories,
html#masterChannel-tf div#masterBodyInner,
html div#wrapper div.whitepaper div.wp-wrapper,
html div#wrapper div.whitepaper div.wp-wrapper div.wp-main--full,
div#tmplBody div#tmplContent div#tmplContentIn,
html#masterChannel-executive div#localPankuzu {
    min-width: 990px;
    width: 100%;
}
#g_nav,
.dir-fav #g_nav {
    --base_w: unset;
    --base_h: unset;
}

/* ITmedia news */
div#masterMain div#tmplNews.tmplArticle div#tmplNewsIn.tmplArticleIn div#cmsTitle,
div#masterMain div#tmplNews.tmplArticle div#tmplNewsIn.tmplArticleIn div#cmsBody div.inner {
    width: auto;
}
html#masterChannel-news body#masterType-subtop.dir-news.dir-subtop.dir-lifestyle div#masterBody {
    width: auto;
}
/*@media screen and (min-width: 990px) {*/
#masterBody,
#masterBodyOut {
    min-width: 990px;
    width: 100%;
}

#masterContents,
#masterBodyOuter {
    min-width: 960px;
    width: 100%;
}

/* Business online */
#masterChannel-business div#masterMain {
    min-width: 640px;
    width: calc(100% - 350px);
}
#masterChannel-business div.colBoxLeft {
    width: calc(100% - 350px);
}
#masterMain {
    min-width: 630px;
    width: calc(100% - 350px);
}

html#masterChannel-top div#masterSub,
html#masterChannel-mm div#masterBodyOuter {
    margin-right: 20px;
}

div.kuratekuMain,
#masterChannel-news div#masterMain {
    width: calc(100% - 330px);
}

html#masterChannel-news div.kuratekuContents div.colBoxIndexRight {
    width: calc(100% - 220px);
}

html#masterChannel-news div#tmplNews.tmplArticle {
    min-width: 660px;
    width: 100%;
}

/* PC User */
div.colBoxTopStoriesTop div.colBoxIndex,
div#colBoxTopStories.colBox.colBoxTopStories-og.colBoxTopStories-og-TypeB div.colBoxTopStoriesTop div.colBoxIndex,
html#masterChannel-enterprise div.colBoxIndex.GTLeft {
    min-width: 325px;
    width: calc(50% - 20px);
}

div.colBoxTopStoriesBtm div.colBoxIndex {
    min-width: 210px;
    width: calc(33% - 10px);
}

div.colBoxTopStoriesBtm div.colBoxIndex div.colBoxIcon a,
div.colBoxTopStoriesBtm div.colBoxIndex div.colBoxIcon a img {
    min-width: 210px;
    width: 100%;
    /*height: auto;*/
}
html#masterChannel-enterprise div.GTRightBox {
    min-width: 300px;
    width: calc(50% - 20px);
}
html#masterChannel-enterprise div.colBoxIndex.SeriesUpdate,
html#masterChannel-enterprise div#colBoxTopSeriesIndex.colBoxInner div.colBoxIndex {
    min-width: 320px;
    width: calc(50% - 20px);
}
/* PC-User mac, akiba */
html#masterChannel-pcuser body#masterType-subtop #masterMain {
    min-width: 680px;
    /* mac */
}
html#masterChannel-pcuser body#masterType-subtop.dir-pcuser.dir-subtop div#masterMainRight {
    /*min-width: 460px;*/
    width: calc(100% - 220px);
    /*margin-left: 20px;*/
}
html#masterChannel-pcuser body#masterType-subtop.dir-pcuser.dir-subtop div#educationMain {
    min-width: 644px;
    width: calc(100% - 330px);
}
#masterChannel-pcuser div.colBoxIndexRight {
    min-width: 440px;
    width: calc(100% - 190px);
}

/* Tech Factory */
#masterChannel-tf #masterMain,
#masterChannel-mm #masterMain,
html div#wrapper div.whitepaper div.wp-wrapper div.wp-main {
    min-width: 672px;
    width: calc(100% - 340px);
}
html#masterChannel-tf div#colBoxTopStories.colBox.colBoxTopStories div.colBoxInner.colBoxTopStoriesLeft,
html#masterChannel-mm div.colBox.colBoxTopStories div.colBoxInner.colBoxTopStoriesLeft {
    min-width: 260px;
    width: calc(100% - 440px);
}
#masterChannel-tf .colBoxTitle,
#masterChannel-mm .colBoxTopStoriesTitleBox {
    clear: both;
    /* tech factory, marketing*/
}

html#masterChannel-tf div#colBoxTopPickUp.colBox.colBoxTopPickUp div.colBoxIndex {
    min-width: 200px;
    width: calc(33% - 20px);
}
html#masterChannel-tf .colBoxTopCategory,
html#masterChannel-mm div.colBox.colBoxTopCategory {
    min-width: 320px;
    width: calc(50% - 20px);
}
html#masterChannel-tf div.colBoxIndex.colBoxTopCategoryItem {
    min-width: 140px;
    width: calc(50% - 20px);
}

/* Tech target Japan */
div#tmplContentIn div#tmplMain div#tmplL700 {
    min-width: 680px;
    width: calc(100% - 330px);
}

div#tmplContentIn div#tmplMain div#tmplL700 div#tmplCol1 {
    min-width: 470px;
    width: calc(100% - 220px);
}

/* ITMedia Executive */
#masterChannel-executive div#masterMain {
    min-width: 687px;
    width: calc(100% - 320px);
}
#masterChannel-executive div.colBox.colBoxBorder.colBoxExecCont,
#masterChannel-executive div.colBox.colBoxBorder.colBoxExecSeminar {
    min-width: 320px;
    width: calc(50% - 20px);
}
/* Fav log */
html#masterChannel-fav div#masterMain {
    min-width: 720px;
    width: 100%;
    margin: 0 10px;
}
.colBoxTopStoriesTop--wide .colBoxIndex,
.colBoxTopStoriesBtm--wide .colBoxIndex {
    width: calc(33% - 10px);
}
.colBoxTopStoriesTop--wide .colBoxIcon,
.colBoxTopStoriesBtm--wide .colBoxIcon {
    background: #FFF;
}

/* atmark IT */
html#masterChannel-ait div.GTLeftBox div.colBoxIndex.GTLeft {
    width: calc(50% - 10px);
}
html#masterChannel-ait div#colBoxGT.colBox.colBoxGT div.GTRightBox {
    width: calc(50% - 10px);
}

@media screen and (max-width: 1010px) {
    #educationMain {
        /* PC User education */
        min-width: unset !important;
        width: calc(100% - 200px) !important;
    }
    #newArticleTop {
        /* PC User education */
        width: 100%;
    }
    #masterChannel-pcuser .colBoxNewArticle .colBoxIndexRight {
        float: right;
    }
}
@media screen and (max-width: 900px) {
    /* hide sidebar */
    #masterBody,
    #masterBodyOut {
        min-width: unset !important;
        width: 100%;
    }
    #masterBodyOuter {
        min-width: unset !important;
        width: 100% !important;
    }
    #masterContents {
        min-width: unset;
        padding: unset;
    }
    #masterSub {
        display: none;
    }
    #masterMain,
    #masterBodyInner,
    .tmplArticle {
        min-width: unset !important;
        width: 100% !important;
        padding: unset;
    }
    #masterChannel-tf #masterMain,
    #masterChannel-mm #masterMain {
        min-width: 400px !important;
    }
    #globalHeaderBottom {
        width: 100% !important;
    }
    #cmsTitle,
    #cmsDate,
    #cmsByline,
    #cmsBody .inner {
        width: 100% !important;
        margin-left: unset;
    }
    .kuratekuMain {
        width: 100% !important;
        /* くらテク */
    }
    #globalContents {
        width: 97%;
        /* ねとらぼ, tech factory */
        padding: unset;
        margin: 0 auto;
    }
    #masterChannel-executive div#masterMain {
        min-width: 687px !important;
    }
    #educationMain {
        /* PC User education */
        min-width: unset !important;
        width: 100% !important;
    }
    #masterChannel-pcuser body#masterType-subtop div#masterBody {
        min-width: 680px !important;
        /* PC User education */
    }
    #g_nav_i {
        width: 100% !important;
        /*ねとらぼ*/
        height: unset;
    }
    .wp-block-image img {
        max-height: 400px;
        /* ねとらぼ */
    }
    .article-main-vidual .bg .bg-thumb {
        background-position: 50%;
        /* ねとらぼ */
        background-size: 100px;
    }
    .tmplArticleIn {
        margin: 0 10px;
    }
}
}
