/**** editor and general ****/

/*[[transitions]]*/

/*** main editor ***/
.CodeMirror {
    border: solid #484848 1px;
    background: #000;
    color: #f00;
    min-height: 400px;
}
.CodeMirror-linenumber {
    color: #ffed00;
}
.CodeMirror-gutters {
    border-right: 2px solid #484848;
    background-color: #920000;
}
/** selection and search **/
.CodeMirror-selected, .CodeMirror-focused .CodeMirror-selected {
    background: rgba(100, 100, 100, .25);
}
.cm-searching {
    background: rgba(248, 217, 0, 0.7);
}
.CodeMirror div.CodeMirror-cursor {
    border-left: 1px solid #ffed00;
}
/** syntax highlighting **/
.cm-s-default .cm-qualifier {
    color: #c30000;
}
.cm-s-default .cm-tag {
    color: #169C00;
}
.CodeMirror-activeline-background {
    background: #2D0000;
}
.cm-s-default .cm-atom {
    color: #3114FF;
}
.cm-s-default .cm-keyword {
    color: #9900AF;
}
.cm-s-default .cm-number {
    color: #1F9A69;
}

/** sections **/
#sections>h2 {
	margin-bottom: 0;
}
#sections>div:first-of-type {
	padding-top: 0;
}
#sections > div:not(:first-of-type) {
    border-color: #444;
}

/*** buttons, inputs, dropdowns ***/
#name, select, .applies-type, .applies-value, #options [type="number"] {
    background-color: #ffe800;
	border-color: #a49500;
    border-style: solid;
    border-width: 2px;
    border-radius: 7px;
    height: 25px;
}
#name {
    width: 100%;
    font-size: 18px;
}
select, .applies-type, .applies-value, #options [type="number"] {
    font-size: 14px;
}
button {
	background: #fb9000;
    border: 2px solid #b96a00;
    border-radius: 5px;
    font-weight: 600;
}
button:disabled {
	color: #444;
	background: #bc6c00;
    border: 2px solid #854c00;
}
.remove-applies-to, .add-applies-to {
	margin-left: 3px;
}
/* spin buttons (arrows next to tab size input) */
input[type="number"]::-webkit-outer-spin-button,
input[type="number"]::-webkit-inner-spin-button {
  -webkit-appearance: none;
}
input[type="number"] {
  -moz-appearance: textfield;
}
#options [type="number"] {
	text-align: center;
}

/** focus, hover **/
:focus {
    outline: none;
}
input:focus, button:focus, select:focus,
input:hover, button:hover, select:hover {
	-webkit-filter: drop-shadow(0px 0px 2px #ff8900);
	-moz-filter: drop-shadow(0px 0px 2px #ff8900);
	filter: drop-shadow(0px 0px 2px #ff8900);
}
.CodeMirror-focused {
	outline: none;
	-webkit-box-shadow: 0px 0px 4px #ff8900;
	-moz-box-shadow: 0px 0px 4px #ff8900;
	box-shadow: 0px 0px 4px #ff8900;
}

/** checkboxes **/
input[type=checkbox] {
	-webkit-appearance: textfield;
	position: relative;
	background-color: #2d2d2d;
	border-radius: 100%;
	color: #fff;
	display: block;
	margin: 0 5px 0 0 !important;
	padding: 0;
	outline: transparent !important;
	width: 16px;
	height: 16px;
	float: left;
	border: 2px solid #444;
}
input[type=checkbox]:checked {
	background-color: #f08a00;
	border: 2px solid transparent;
	color: #2d2d2d;
}
input[type=checkbox]:checked:before {
	-webkit-filter: invert(1);
	-moz-filter: invert(1);
	filter: invert(1);
	content: '';
	display: inline-block;
	height: 16px; width: 16px;
    background-size: 13px !important;
}
input[type=checkbox]:hover {
	-webkit-filter: drop-shadow(0px 0px 2px #fff);
	-moz-filter: drop-shadow(0px 0px 2px #fff);
	filter: drop-shadow(0px 0px 2px #fff);	
}

/*** icons ***/
img[id$="-help"], img[class$="-help"] {
	margin-left: 3px;
    margin-bottom: -2px;
    padding: 8px 8px;
	width: 0px; height: 0px;
	background-size: 16px !important;
	-webkit-filter: invert(1);
	-moz-filter: invert(1);
	filter: invert(1);
	opacity: 0.5;
}
img[id$="-help"]:hover, img[class$="-help"]:hover {
	border-radius: 8px;
	opacity: 0.7;
	-webkit-filter: invert(1) drop-shadow(0px 0px 4px #fff);
	-moz-filter: invert(1) drop-shadow(0px 0px 4px #fff);
	filter: invert(1) drop-shadow(0px 0px 4px #fff); 	
}
img#keyMap-help {
	padding: 10px 12px;
    margin-bottom: -7px;
}
img[src*="world_go.png"] {
	padding: 10px 10px;
	width: 0px; height: 0px;
	background-size: 20px !important;
	-webkit-filter: invert(1);
	-moz-filter: invert(1);
	filter: invert(1);
	opacity: 0.5;
}
body#stylish-manage img[src*="world_go.png"] {
	padding: 8px 8px;
	background-size: 16px !important;
	-webkit-margin-after: 0.3em;
}
img[src*="world_go.png"]:hover {
	opacity: 0.7;
	-webkit-filter: invert(1) drop-shadow(0px 0px 4px #fff);
	-moz-filter: invert(1) drop-shadow(0px 0px 4px #fff);
	filter: invert(1) drop-shadow(0px 0px 4px #fff);	
}

/*** options padding ***/
#options>.option {
	padding-bottom: 3px;
}
#basic-info-enabled {
	padding-top: 4px;
	padding-bottom: 3px;
}
#options>.option:nth-of-type(3) {
	padding-bottom: 6px;
}
h1#heading, h2#mozilla-format-heading, #basic-info, h2#options-heading {
	-webkit-margin-after: 0.2em;
}
div.option.aligned>label {
	vertical-align: middle;
}
.applies-to>label {
	margin-top: 0.25rem;
}
.applies-to>label>img {
	padding-bottom: 7px;
	padding-top: 9px;
}
/** for small window **/
@media(max-width: 737px) {
    #options {
    	height: 80px;
	}
    #options .aligned > *:not(img) {
    	vertical-align: top;
	}
    #options .aligned > img#keyMap-help {
    	margin-top: 5px;
	}
    #options #keyMap-label, #options #theme-label {
    	padding-top: 4px;
	}
    #actions > div:nth-child(2):before {
        visibility: visible;
        content: "Mozilla Format";
        padding: 0 5px 0 10px;
    	font-size: 13px;
    }
}

/*** help messages ***/
#help-popup {
	background-color: #333;
}
#help-popup>.title {
	background-color: rgba(0, 0, 0, 0.25);
	color: #ddd;
}
#help-popup>.contents {
	color: #aaa;
}
#help-popup>.contents::-webkit-scrollbar-thumb {
    background: #444 !important;
}
.keymap-list th input {
	background: #444;
