	color: #fff;
}
.keymap-list th input::-webkit-search-cancel-button {
	filter: hue-rotate(180deg) brightness(2);
}
.keymap-list tbody {
    color: #ffc800;
}
.keymap-list tr:nth-child(odd) {
    background-color: rgba(0, 0, 0, 0.12);
}
#help-popup>.close-icon {
	-webkit-filter: invert(.7);
	-moz-filter: invert(.7);
	filter: invert(.7);
}
#help-popup>.close-icon:hover {
	-webkit-filter: invert(.9);
	-moz-filter: invert(.9);
