style.css:
@-moz-document domain("accounts.google.com") {
html
{background: #222222
url('https://lh3.googleusercontent.com/rM3YfvMohH9JoXMBqKyKy--_YT_8z-uc-3-N1-XXwdXiKwDw4y2M5mjJZhbDzphGi0pmVTmOzg=w1366-h768-rw-no') no-repeat !important;
background-size: 40% !important;
background-position: 804px 8px  !important
}
body {background: #222222 url('https://lh3.googleusercontent.com/rM3YfvMohH9JoXMBqKyKy--_YT_8z-uc-3-N1-XXwdXiKwDw4y2M5mjJZhbDzphGi0pmVTmOzg=w1366-h768-rw-no') no-repeat !important;
background-size: 40% !important;
background-position: 804px 8px  !important
}
table
{
    border-collapse: collapse;
}
.card 
{
    background: transparent !important;
}
    
.card-mask 
{
    background-color: #222222;
    background-position: 50% 0;
    background-repeat: no-repeat;
    background-image: none !important
}
*
{
    box-shadow: none !important;
    border-radius: 0px !important;
    text-shadow: none !important;
    font-family: 'Arial, sans-serif' !important;
    font-size: 17px !important;
    color: #e7e5e8 !important;

}    
input[type=email], input[type=number], input[type=password], input[type=tel], input[type=text], input[type=url] {
    -moz-appearance: none;
    -webkit-appearance: none;
    appearance: none;
    display: inline-block;
    height: 36px;
    padding: 0 8px;
    margin: 0;
    background: #222222;
    border: 1px solid #e7e5e8;
    border-top: 1px solid #c0c0c0;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    -moz-border-radius: 1px;
    -webkit-border-radius: 1px;
    border-radius: 1px;
    font-size: 15px;
    color: #e7e5e8;
}

.rc-button-submit, .rc-button-submit:visited {
    border: 1px solid #32506c;
    color: #222222 !important;
    text-shadow: 0 1px rgba(0,0,0,0.1);
    background-color: #4d90fe;
    background-image: -webkit-linear-gradient(top,#e7e5e8,#e7e5e8);
    background-image: -moz-linear-gradient(top,#e7e5e8,#e7e5e8);
    background-image: -ms-linear-gradient(top,#e7e5e8,#e7e5e8);
    background-image: -o-linear-gradient(top,#e7e5e8,#e7e5e8);
    background-image: linear-gradient(top,#e7e5e8,#e7e5e8);
}
.google-footer-bar 
{
    position: absolute;
    bottom: 0;
    height: 35px;
    width: 100%;
    border-top: none !important;
    overflow: hidden;
}
select {
    -webkit-appearance: menulist;
    box-sizing: border-box;
    align-items: center;
    white-space: pre;
    -webkit-rtl-ordering: logical;
    color: black;
    border: #e7e5e8;
    background-color: #222222;
    cursor: default;
    border-width: 1px;
    border-style: solid;
    border-image: initial;
}
}
