style.css:
@-moz-document domain("discordapp.com") {
.form .btn-primary {
	background: linear-gradient(-90deg, #24CCAA, #A2DB95);
	border-radius: 100px;
	text-transform: uppercase;
	font-size: 15px;
	font-weight: 600;
	transition: .3s all linear;
	letter-spacing: 1px
}
.form .btn-primary:hover {
	background: linear-gradient(-90deg, #22B495, #8DC184)
}
@keyframes opacity {
	from {
		opacity: 0
	}
}
.scroller-wrap .scroller::-webkit-scrollbar-track-piece {
	background-color: transparent !important;
}
.scroller-wrap .scroller::-webkit-scrollbar-thumb {
	background-color: #4ABF9E !important;
}
.scroller-wrap .scroller::-webkit-scrollbar-thumb:hover {
	background-color: #4ABF9E !important;
}
.scroller-wrap .scroller::-webkit-scrollbar-thumb:active {
	background-color: #78CFB7 !important;
}
.tab-bar.TOP .tab-bar-item.selected {
	border-color: #44696C;
	color: #44696C;
}
.tab-bar.TOP .tab-bar-item {
	color: #A4B6B7;
}
.user-popout .header {
	background: url(https://discordguide.us/assets/bg.svg);
}
.user-popout {
	background: linear-gradient(-90deg, #2E767A, #44A781);
}
.guild-header header {
	box-shadow: none;
}
.settings .settings-header, .guild-header header:hover, .guild-header.popout-open header, .guild-header header {
	background: linear-gradient(-90deg, #2E767A, #44A781);
}
.guilds-wrapper .guilds {
	background: linear-gradient(-90deg, #4aad87, #44A781);
}
.guild-channels,
.private-channels {
	background: linear-gradient(-90deg, #2E767A, #44A781);
}
.private-channels .search-bar {
	background: linear-gradient(-90deg, #2E767A, #44A781);
	box-shadow: 0 1px 0 0 transparent,inset 0 -1px 0 0 transparent;
}
.account, #voice-connection {
	background: linear-gradient(-90deg, #2E767A, #44A781);
	border-top: transparent;
}
.tab-bar.SIDE .tab-bar-item {
	color: #fff;
	opacity: 0.8;
	font-weight: 600;
	transition: all 100ms ease;
}
.tab-bar.SIDE .tab-bar-item:hover {
	color: #fff;
	opacity: 0.8;
	font-weight: 600;
	background: rgba(255, 255, 255, .2);
}
.tab-bar.SIDE .tab-bar-item.selected,
.guild-channels .channel-text.selected,
.guild-channels .channel-text:hover {
	color: #fff;
	opacity: 1;
	font-weight: 600;
	background: rgba(255, 255, 255, .25);
}
.guild-channels .channel-text.selected,
.guild-channels .channel-text:hover,
.private-channels .channel.selected ,
.private-channels .channel:hover {
	color: #fff;
	opacity: 1;
	font-weight: 600;
	background: linear-gradient(to right,rgba(255, 255, 255, .25) 85%,transparent);
}
.tab-bar.SIDE .tab-bar-item:before {
	display: none
}
.checkbox .checkbox-inner span,
.checkbox .checkbox-inner input[type=checkbox]:checked+span {
	border-radius: 50%;
	border: 2px solid #2E767A;
	background: transparent;
}
.guild-channels .channel-text.selected:before, .guild-channels .channel-text:hover.channel-muted:before, .guild-channels .channel-text:hover:not(.unread):before, .private-channels .channel.selected:before {
	border-left: 3px solid #2f787a;
}
.private-channels .channel:hover:before {
	border-left: 3px solid #2f787a;
	opacity: 0.6;
}
.guilds-wrapper .guilds .guild.unread:before {
    background: #fff;
    border: 2px solid #2f787a;
    border-radius: 50px;
    left: -23px;
}
.checkbox .checkbox-inner input[type=checkbox]:checked+span:after {
	border-color: #2E767A;
	transform: scale(0.7)rotate(45deg);
	animation: opacity 150ms ease
}
.guilds-wrapper .guilds .guild.selected:before {
    border: 2px solid #2f787a;
    left: -24px;
}
.checkbox:active .checkbox-inner span {
	opacity: 0.7
}
.form .Select-control {
	border: none!important;
}
.Select-arrow {
	border-top-color: #44696C
}
.Select-placeholder {
	color: #44696C!important;
	font-weight: 600;
}
.form .control-group label {
	color: #95AAAB;
}
.user-settings-modal a {
	color: #44696C;
	opacity: .9;
}
.user-settings-modal a:hover {
	opacity: 1;
}
.slider-handle {
	border: none;
	background: #2f787a;
}
.slider-bar {
	background: #E8F0E1;
}
.slider-bar-fill {
	background: linear-gradient(-90deg, #2E767A, #44A781);
}
.guild-channels .channel-text .channel-name:before {
    color: #ffffff!important;
}
a.change-log-button {
    color: #fff !important;
    opacity: 1 !important;
}
.radio .radio-inner span:after {
    background: #2E767A;
}
.radio-theme input[type=radio]:checked~label {
    border-color: #2E767A;
}
.guilds-wrapper .guilds .guild .guild-inner {
    background: #2E767A !important;
}
.account .btn-group {
    border: 0px solid transparent;
}
.btn-group > .btn, #voice-connection .btn {
	background: #2E767A;
	border-right: 0px solid #1c1e22;
	border-radius: 20px;
	box-shadow: inset 1px 0 0 transparent;
}
.private-channels .channel.selected .icon-friends, .private-channels .channel .icon-friends {
	background-color: #2E767A;
}
.tooltip {
	background-color: #2E767A;
}
.tooltip.tooltip-right:after {
	border-right-color: #2E767A;
}
.tooltip.tooltip-top:after {
    border-top-color: #2E767A;
}
.account .btn-deafen, .account .btn-settings {
    -webkit-box-shadow: inset 1px 0 0 transparent;
    box-shadow: inset 1px 0 0 transparent;
}
.account .btn-mute:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_mic_white_24px.svg) !important;
    opacity: 0.4;
    zoom: 1.2;
}
.account .btn-deafen:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_volume_up_white_24px.svg) !important;
    opacity: 0.4;
    zoom: 1.2;
}
.account .btn-settings:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_settings_white_24px.svg) !important;
    opacity: 0.4;
	zoom: 1.2;
}
.account .btn-mute.disabled:hover:after {
    opacity: 1;
    -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=100)";
    filter: alpha(opacity=100);
}
.account .btn-settings:hover:after {
    opacity: 1;
    -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=100)";
    filter: alpha(opacity=100);
}
.account .btn-deafen:hover:after {
    opacity: 1;
    -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=100)";
    filter: alpha(opacity=100);
}
#voice-connection .btn-info:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_info_white_24px.svg) !important;
    opacity: 0.4;
	zoom: 2;
}
#voice-connection .btn-disconnect:after {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_call_end_white_24px.svg);
    opacity: 0.4;
	zoom: 1.3;
}
#voice-connection .btn-disconnect:hover:after, #voice-connection .btn-info:hover:after {
	opacity: 1;
}
.account .btn:active, #voice-connection .btn:active {
    background: #2E767A;
    -webkit-box-shadow: inset 0 0 4px transparent;
    box-shadow: inset 0 0 4px transparent;
}
.alert.form .form-inner .btn {
    border: 0px solid #697ec4;
}
#voice-connection .btn-group {
    border: 0px solid #1c1e22;
}
#voice-connection .voice-connection-channel {
    opacity: 1;
}
#voice-connection .voice-connection-status-connected {
    color: #00ff8b;
}
.user-popout .avatar-wrapper .avatar-popout {
    border: 4px solid #2f787a;
    background-color: #2f787a;
}
.guilds-wrapper .btn-download-apps {
    display: none;
}
.guilds-wrapper .guilds-add {
    background: #2E767A;
    color: #fff;
    border: 0px dashed #fff;
}
.guilds-wrapper .guild-separator:after {
    background: #2E767A;
}
.guilds-wrapper .guilds .friends-online {
    background: #2E767A;
    border-radius: 3px;
    padding: 2px;
}
#friends .friends-table .friends-table-header .friends-column,
#friends .friends-table .friends-row .friends-column-name,
#friends .friends-table .friends-row .friends-column-status{color: #fff!important;}

#friends .friends-table{padding-top: 10px;}

#friends .friends-table .friends-table-header{display: none;}

#friends .friends-table .friends-table-body > span{display: flex;flex-flow: row wrap;justify-content: center;}

#friends .friends-table .friends-row{background: linear-gradient(-90deg, #2E767A, #44A781) !important;box-shadow: 2px 2px 7px rgb(67, 165, 129);border-radius: 5px;margin: 15px;padding: 0;height: 120px!important;flex-direction: column;flex-basis: 300px;position: relative;opacity: 1!important;transition: opacity 300ms ease-ins;}

#friends .friends-table .friends-row:hover{background: linear-gradient(-90deg, #2E767A, #44A781) !important; box-shadow: 2px 2px 7px rgb(67, 165, 129);border-radius: 5px;margin: 15px;padding: 0;opacity: 1!important;}

#friends .friends-table .friends-row .avatar-small{border-radius: 5px;box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.3);}

#friends .friends-table .friends-row + .friends-row{border-top: none!important;}

#friends .friends-table .friends-row .friends-column{position: absolute;}

#friends .friends-table .friends-row .friends-column + .friends-column{margin: 0;}

#friends .friends-table .friends-row .friends-column-name .avatar-small{top: 25px;left: 15px;width: 85px;height: 85px;background-size: 85px 85px;}

#friends .friends-table .friends-row .friends-column-name .discord-tag{display:block;position: absolute;top: 25px;left: 115px;text-shadow: 1px 1px 1px rgba(0, 0, 0, 0.3);}

#friends .friends-table .friends-row .friends-column-name .discord-tag .discriminator{visibility: visible;}

#friends .friends-table .friends-row .friends-column-name .discord-tag span{clear: left;float: left;}

#friends .friends-table .friends-row .friends-column-name .username{transition: color 300ms;}

#friends .friends-table .friends-row:hover .username{color: #fff;}

#friends .friends-table .friends-row .friends-column-status{top: 0;width: 100%;justify-content: center;background-color: #2E767A;height: 18px;}

#friends .friends-table .friends-row .friends-column-status .status{width: 8px;height: 8px;}

#friends .friends-table .friends-row .friends-column-status .status-text{font-size: 12px;line-height: normal;transition: color 300ms;}

#friends .friends-table .friends-row:hover .friends-column-status .status-text{color: #fff;}

#friends .friends-table .friends-row .friends-column-guilds{top: 80px;left: 115px;width: 150px;display: flex;}

#friends .friends-table .friends-row .friends-column-guilds a{flex-shrink: 2;flex-basis: 10%;}

#friends .friends-table .friends-row .friends-column-guilds a:nth-child(4){pointer-events: none;display: none;}

.theme-dark #friends .friends-table .friends-row .friends-column-guilds .more-mutual-guilds-btn:before{content: "";background-color: rgba(0, 0, 0, 0.38);background-image: url(data:image/svg+xml;utf8;base64,);background-size: 18px 18px;background-position: center center;background-repeat: no-repeat;border-radius: 5px;width: 30px;height: 30px;box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);position: absolute;top: 0;}

#friends .friends-table .friends-row .friends-column-guilds .more-mutual-guilds-btn{border-color: transparent !important;color: transparent !important;}

#friends .friends-table .friends-row .friends-column-guilds a:nth-child(4) .avatar-small,
#friends .friends-table .friends-row .friends-column-guilds a:nth-child(n + 5){display: none;}

#friends .friends-table .friends-row .friends-column-actions{flex-direction: column;align-items: flex-end;top: 25px;right: -10px;opacity: 0;transition: opacity 500ms;visibility: visible;}

#friends .friends-table .friends-row:hover .friends-column-actions{opacity: 1;}

#friends .friends-table .friends-row .friends-column-actions .friends-action{font-size: 10px;transform: translateX(120px);transition: transform 500ms, background 500ms;}

#friends .friends-table .friends-row:hover .friends-column-actions .friends-action{transform: translateX(0px);}

#friends .friends-table .friends-row .friends-column-actions .friends-action:first-child{margin-bottom: 5px;}

#friends .friends-table .friends-row .friends-column-actions .friends-action:nth-child(2){transition-delay: 200ms;}

.guilds-wrapper .guilds .friends-online {
    color: #fff;
}
#friends .friends-header .tab-bar .tab-bar-item.tab-bar-item-primary, #friends .friends-header .tab-bar .tab-bar-item.tab-bar-item-primary:hover:not(.selected) {
    background-color: #2e767a;
}
#friends .friends-header .tab-bar .tab-bar-item.selected {
    background-color: #6cc7a5;
}
#friends .friends-header .tab-bar .tab-bar-item:hover:not(.selected) {
    background-color: rgba(100, 206, 172, 0.3);
}
#friends .btn {
    background-color: #6cc7a5;
	transition: .3s all linear;
}
#friends .btn:hover {
    background-color: #47c194;
}
.user-popout .username-wrapper.has-nickname .discriminator, .user-popout .username-wrapper.has-nickname .username {
    color: #e5fff7;
}
.highlight:hover {
    background-color: #4abf9e !important;
}
.highlight {
    color: #4abf9e !important;
    background: #f1fff7 !important;
	padding: 2px;
    border-radius: 3px;
    transition: .3s all linear;
}
.message-group .edit-message .channel-textarea-inner {
    border-color: #4abf9e;
}
.message-group .edit-message .edit-operation>a {
    color: #4abf9e;
}
.form .control-group label a:hover {
    color: #44a681;
}

.form .control-group label a {
    color: #30797b;
}
.user-popout .username-wrapper .activity {
    color: #e5fff7;
}
.chat .new-messages-bar {
    background-color: #2e767a;
}
.chat .new-messages-bar button:last-child {
    color: #fff;
}
.chat .new-messages-bar:hover {
    background-color: #26676b;
}
}
