style.css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
  EOServ Nightly | https://userstyles.org/styles/135329/eoserv-nightly
*/
/* AGENT_SHEET */
@-moz-document regexp('(^https?)://eoserv\\.(net)/.*') {
  body, th {
    background-color: #111 !important;
    color: #c0c0c0 !important;
  }

  h, h1, h2, h3, h4, h5, input, select, .newpostdot, .new  {
    color: #c0c0c0 !important;
  }

  tr, td, th, ul, div, span, caption, dt, dd, d1, h1, h2, h3, h4, h5, blockquote, select, .rte-button {
    border-color: #343434 !important;
    border-top-color: rgb(52, 52, 52) !important;
    border-right-color: rgb(52, 52, 52) !important;
    border-bottom-color: rgb(52, 52, 52) !important;
    border-left-color: rgb(52, 52, 52) !important;
  }
  
  div, td, .pagination, ul, th, span, select {
    background-color: #222 !important;
  }

  tr:hover .topicdesc {
    color: #c0c0c0 !important;
  }

  a {
    color: #C4C4C4 !important;
  }

  a:hover {
    background-color: #444 !important;
  }

  caption {
    background-color: #333 !important;
  }
  
  .rte-button {
    background-color: #444 !important;
    color: #c0c0c0 !important;
  }

  #mainmenu, #forummenu {
    background-color: #222 !important;
  }

  ul#mainmenu > li {
    filter: grayscale(1) brightness(1.5) !important;
   -webkit-filter: grayscale(1) brightness(1) !important; /* Safari 6.0 - 9.0 */
  }

  ul#forummenu > li {
    filter: grayscale(1) brightness(1) !important;
   -webkit-filter: grayscale(1) brightness(1) !important; /* Safari 6.0 - 9.0 */
  }

  .admincolor {
    filter: grayscale(1) brightness(1) !important;
   -webkit-filter: grayscale(1) brightness(1) !important; /* Safari 6.0 - 9.0 */
  }

  .modcolor {
    filter: grayscale(1) brightness(1) !important;
   -webkit-filter: grayscale(1) brightness(1) !important; /* Safari 6.0 - 9.0 */
  }

  img[src="//eoserv.net/pub/logo.png"],
  img[src="./pub/EOSERV.png"],
  img[src="./pub/Client Editing.png"],
  img[src="./pub/Announcements.png"],
  img[src="./pub/EO Server Building.png"],
  img[src="./pub/Game Development.png"],
  img[src="./pub/Lounge 2.0.png"],
  img[src="./pub/Wiki Page Discussion.png"],
  img[src="./pub/Artists.png"],
  img[src="./pub/Programming.png"],
  img[src="./pub/Lounge.png"],
  img[src="./pub/Seose.png"],
  img[src="./pub/WebCP.png"],
  img[src="./pub/EOMap.png"],
  img[src="./pub/topic-locked.png"],
  img[src="./pub/topic-locked-sticky.png"],
  img[src="./pub/topic-active.png"],
  img[src="./pub/topic-new.png"],
  img[src="pub/eoserv/reply.gif"],
  img[src="pub/eoserv/msg_newpost.gif"],
  img[src="pub/eoserv/icon_edit.gif"],
  img[src="pub/eoserv/icon_quote.gif"]
  {
    filter: grayscale(1) brightness(0.7) !important;
   -webkit-filter: grayscale(1) brightness(0.7) !important; /* Safari 6.0 - 9.0 */
  }
}

  /* forum.eoserv.net */
@-moz-document regexp('(^https?)://forum.eoserv\\.(net)/.*')
{
  
}
