style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* large toolbar icon */
#gm-toolbar-button[unread]:not([unread="0"]) .gm-icon[status|="gm-account-state-logged-in"] {
  -moz-image-region: rect(0px, 81px, 20px, 54px) !important;
}
#gm-toolbar-button[unread="0"] .gm-icon[status|="gm-account-state-logged-in"] {
  -moz-image-region: rect(0px, 108px, 20px, 81px) !important;
}


/* small toolbar icon, statusbar icon, menuitem */
toolbar[iconsize="small"] #gm-toolbar-button[unread]:not([unread="0"]) .gm-icon[status|="gm-account-state-logged-in"],
[unread]:not([unread="0"]) .gm-icon[iconsize="small"][status|="gm-account-state-logged-in"] {
  -moz-image-region: rect(0px, 48px, 12px, 32px) !important;
}
toolbar[iconsize="small"] #gm-toolbar-button[unread="0"] .gm-icon[status|="gm-account-state-logged-in"],
[unread="0"] .gm-icon[iconsize="small"][status|="gm-account-state-logged-in"] {
  -moz-image-region: rect(0px, 64px, 12px, 48px) !important;
}
