style.css:
@-moz-document 
domain("www.last.fm"),
domain("secure.last.fm") {

/*Заголовок*/
.header .header-background {z-index: -1;} /*Удаляет Фон*/
.header--overview {min-height: 100px;}  /*Отступ на Странице Тега*/
/*h1, .h1, .h3, h4, .h4 {color: black !important;}*/

.grid-items-cover-image a, .grid-items-cover-image a:focus, .grid-items-cover-image a:hover {text-overflow: ellipsis; overflow: hidden; display: block;} /*Исправляет Проблему с Заголовками в Чартах*/

.header-new-title {color: white;} /*Цвет Имени Артиста*/
.header-title {color: black;} /*Цвет Имени Юзера*/

.full-bleed-ad-container.open-ad-container-desktop {display: none;}
.sidebar-ad-container.open-ad-container-desktop {display: none;}
/*h3.text-18 {display: none;}*/

.lazy-features-page-bottom.async-content-loaded {display: none;}
.lazy-features-page-bottom.async-content-loading {display: none;}
.related-content.trc_related_container.trc_spotlight_widget.trc_elastic.trc_elastic_thumbnails-g {display: none;}
.buffer-standard.section-with-hover-control.section-with-separator.section-with-separator--xs-only {display: none;}
.trending-tracks-section-wrapper.async-content-loaded {display: none;}
.trending-tracks-section-wrapper.async-content-loading {display: none;}
.container.page-content {padding-bottom: 36px;}
/*.section-with-separator:after {background-color: white;}*/

.header-crumb {color: black;} /*Имя Исполнителя на Странице Трека*/
.header {color: #212121;}
.header-scrobble-since {color: #171717;}
.metadata-title {color: #171717;}
.metadata-display {color: #171717;}
.two-column-layout .content-top {background: none;}
.player-bar {background: black;}
.top-bar {color: black;}
.header {color: black; background-size: 100% 100%;}
.dropdown:hover .dropdown-menu {background: #FFFFFF;}
.dropdown:focus .dropdown-menu {background: #FFFFFF;}
.dropdown .dropdown-menu-item:hover {background: #FFFFFF;}
.dropdown .dropdown-menu-item:focus {background: #FFFFFF;}
.dropdown-menu.dropdown--open {background: #FFFFFF;}

/*Layout*/
.two-column-layout .content-top .container {background: white;}
.two-column-layout .content-top {background: white;}
.two-column-layout .container {background-size: 0px 0px,0% 0px;}
.two-column-layout .main-content {background-size: 0px 0px,0% 0px;}

/*Плеер*/
.player-bar-artwork.js-artwork.remove-focus {height: 0px;}
.player-bar-artwork.js-artwork {height: 0px;} /*Удаляет Обложку*/
/*.player-bar.inactive .player-bar-btn {opacity: 1 !important;}*/
.video-player {display: none !important;} /*Удаляет Окно с Видео*/

/*Рупор Ответы*/
/*.shout-reply:before {width: 0px;} /*Удаляет Стрелку
/*.shout-reply {padding-left: 0px;} /*Смещает Текст*/

/*Рупор Лайки*/
/*.vote-button.vote-button--unvoted {padding-left: 33px;}
.vote-button.vote-button--voted {padding-left: 33px;} /*Смещает Текст*/
/*.vote-button.vote-button--voted {color: #41b700 !important;} /*Перекрашивает Нажатый Лайк*/
/*.vote-button-toggle .vote-button:before {background-image: url();}

/*"Друзья Слушают"*/
.kerve.widget .widget_title, .kerve.widget h2 {font-size: 15px;} /*Размер Текста*/
i, cite, em, var, address, dfn {font-style: normal;} /*Убирает Курсив*/
.kerve.widget {border: 1px solid #ffffff;} /*Удаляет Серые Границы*/

/*Not Found Page*/
.trc_rbox_header_span {display: none;}
.trc_elastic_thumbnails-b .trc_rbox_outer {display: none;}
.thumbnails-b .trc_rbox_header .logoDiv {display: none;}

/*Реклама*/
.lazy-features-footer.lazy-loading {display: none !important;}
.lazy-features-footer.async-content-loading {display: none !important;}
.lazy-features-footer.lazy-loading {height: 0px;} /*Круг Загрузки*/
.features-footer {display: none !important;}
.features-footer {height: 0px;} /*Банер Событий*/
.leader_bottom.lastfm-ad {height: 0px;}
.leader_bottom.lastfm-ad {display: none !important;} /*Нижний Блок Рекламы*/
.leader_top.lastfm-ad {display: none !important;}
.leader_top.lastfm-ad {height: 0px;} /*Верхний Блок Рекламы*/
/*.related-ads {display: none !important;}*/
.leader_top, .leader_bottom, .mpu_top, .mpu_bottom {display: none !important;}
.tonefuze--desktop {display: none !important;}
.mpu_top.lastfm-ad {display: none !important;}
.mpu_top.lastfm-ad {height: 0px;} /*Правый Блок Рекламы*/
.mpu-subscription-upsell.mpu-subscription-upsell--mpu {display: none !important;} /*Реклама Подписки*/

/*Лайк и Плей Треков */
/*.header .love-button {display: none !important;} /*Лайк на Старнице Трека*/
/*.library-track-metadata .love-button {display: none !important;} /*Лайк в Библиотеке*/
/*.chartlist .chartlist-play-button:before {background-image: url();}
.chartlist .chartlist-play-button:focus:before {background-image: url();}
.chartlist .chartlist-play-button:hover:before {background-image: url();}
.chartlist .love-button:before {background-image: url();}
.chartlist button.love-button--loved:focus:before {background-image: url();}
.chartlist button.love-button--loved:hover:before {background-image: url();}*/

/*Кнопка Рекомендаций*/
.recommended-next-button {display: none !important;}
.js-link-block.recommended-next-page.recommended-next-page--artist {display: none !important;}
.js-link-block.recommended-next-page.recommended-next-page--track {display: none !important;}
.recommended-next-page-image {display: none !important;}
.recommended-next-page-details {display: none !important;}
.js-link-block-cover-link.recommended-next-page-cover-link {display: none !important;}
.js-link-block.recommended-next-page.recommended-next-page--tag {display: none !important;}

/*Последний.Год*/
.user-dashboard-banner-heading {display: none !important;}
.user-dashboard-banner.link-block.js-link-block {display: none !important;}
.clearfix.content-form.section-with-settings.content-form {margin-top: 0px;}

/*Подписки на Пользователя*/
.follower-btn .follower-btn-following:hover {font-size: 0px;} 
.follower-btn .follower-btn-following {font-size: 0px;} 
.follower-btn .follower-btn-add-follower {font-size: 0px;} /*Удаляет Текст*/

/*Почта*/
.header .header-message-user:before {background-image: url(https://i.imgur.com/iCCG7wp.png);}
.header .header-message-user:before {width: 163px; height: 43px; margin-left: -147px; margin-top: -25px;}
.header .header-message-user:hover:before, .header .header-message-user:focus:before {background-image: url(https://i.imgur.com/ygsIGyC.png);}
.header--overview .header-info-secondary {padding-right: 175px;}

/*Заполнение Форм*/
.tag-cta, .tag-cta:hover {background: white !important;}
.tag-cta, .tag-cta:hover {border-color: #ebebeb !important;} /*Теги*/
.upload-image-cta, .upload-image-cta:hover {background: white !important;}
.upload-image-cta, .upload-image-cta:hover {border-color: #ebebeb !important;} /*Фото*/
.upload-video-cta, .upload-video-cta:hover {background: white !important;}
.upload-video-cta, .upload-video-cta:hover {border-color: #ebebeb !important;} /*Видео*/
.wiki-cta, .wiki-cta:hover {background: white !important;}
.wiki-cta, .wiki-cta:hover {border-color: #ebebeb !important;} /*Вики*/
.upload-artwork-cta, .upload-artwork-cta:hover {background: white !important;}
.upload-artwork-cta, .upload-artwork-cta:hover {border-color: #ebebeb !important;} /*Альбом*/ 

/*Test*/ 
.tonefuze--mobile+.featured-items-row {margin-top: 0px}
}
