style.css:
@-moz-document domain(4chan.org) {
@font-face {
    font-family: 'Source Sans Pro';
    font-style: normal;
    font-weight: 400;
    src: local('Source Sans Pro Regular'), local('SourceSansPro-Regular'), url(https://fonts.gstatic.com/s/sourcesanspro/v10/ODelI1aHBYDBqgeIAH2zlNV_2ngZ8dMf8fLgjYEouxg.woff2) format('woff2');
}
body {
    background: #1c1c1c;
    color: #C3C3C3;
    font-family: 'Source Sans Pro', sans-serif;
    font-size: 14px;
    font-weight: 400;
    line-height: 20px;
    margin: 0;
    padding: 0;
}
.expandedWebm,
.expanded-thumb {
    max-width: 96% !important;
}
.name,
.post-author {
    color: #7c2d2d!important;
}
span.postertrip,
.post-tripcode {
    color: #3e7157!important;
    font-weight: 700!important;
}
div.post div.postInfo span.nameBlock span.postertrip {
    font-weight: 700!important;
}
.post.reply:not(.preview) {
    width: 75%!important;
    min-width: 75%!important;
    max-width: 75%!important;
}
.post.reply {
    overflow: hidden!important;
    display: block!important;
    background-color: #232323!important;
    border-radius: 3px 3px 3px 3px!important;
    border: 0!important;
    padding-right: 0!important;
    padding-left: 7px!important;
    outline: 0!important;
}
table.postForm,
#quickReply {
    width: 55%!important;
}
#postForm {
    display: table!important;
}
table.postForm > tbody > tr > td:first-child {
    background-color: #282828!important;
    background: #0f0f0f;
    color: #cdcdcd!important;
    border: 0!important;
    width: 15%!important;
}
.qrWindow {
    width: 55%!important;
    border: 0!important;
    background-color: #1c1c1c!important;
}
#boards .column {
    width: 11em!important;
}
a,
#absbot a,
.button {
    color: #5f5f82;
}
#ft li,
#ft ul,
#options input:not([type='checkbox']):not([type='radio']),
#updater input:not([type='checkbox']):not([type='radio']),
.box-outer,
.boxbar,
.deletebuttons input[value=Delete],
.recaptcha_image_cell > center > #recaptcha_image,
[name='recaptcha_response_field'],
.top-box,
h2,
input:not([type="checkbox"]):not([type='radio']),
input:not([type='radio']),
input[type='file'] > input[type='button'],
input[type='submit'],
select,
#settings select,
textarea,
.button-group button {
    -webkit-appearance: none;
    -moz-appearance: none;
    background-color: #282828!important;
    color: #cdcdcd!important;
    outline: none!important;
    border-width: 0!important;
}
.boxcontent input,
.boxcontent input:not([type='checkbox']):not([type='radio']),
.boxcontent input:not([type='radio']),
.boxcontent textarea,
.boxcontent button,
#filter-list select {
    -webkit-appearance: none;
    -moz-appearance: none;
    background-color: #1e1e1e!important;
    color: #cdcdcd!important;
    outline: none!important;
    border-width: 0!important;
}
input[type="checkbox"] {
    -moz-appearance: checkbox!important;
    -webkit-appearance: checkbox!important;
}
#settingsMenu button {
    -moz-appearance: button!important;
    -webkit-appearance: button!important;
}
.qrHeader,
.qrMessage {
    background: none!important;
    border: 0!important;
    color: #c3c3c3!important;
}
#quickReply {
    width: 329px !important;
}
#quickReply form .postForm {
    width: 100%!important;
    padding-left: 15px!important;
    padding-bottom: 5px!important;
}
#qrHeader,
#qrepHeader,
div.postHover {
    background-color: #191919!important;
    color: #c3c3c3!important;
    border: none!important;
}
.postForm td span.desktop {
    opacity: 0!important;
}
.postarea form td:not(.postblock):not(.rules) {
    color: rgba(0, 0, 0, 0)!important;
}
.postarea form[name^='post'] {
    top: auto;
    left: auto;
    bottom: -4px;
    z-index: 5;
    margin: 0!important;
}
.postNum a:hover,
div.post div.postInfo span.postNum a:hover,
.posteruid .hand:hover,
.ws .postNum a:hover {
    color: #f5f5f5!important;
}
.capcode.id_mod:hover {
    color: purple!important;
}
a.replylink:hover {
    color: #5f5f82!important;
}
hr {
    border: 0;
    border-bottom: 1px solid #ddd;
    display: none;
    border-color: #000!important;
}
.abbr,
.sideArrows {
    color: #646464!important;
}
.fileText {
    color: #646464!important;
    font-style: italic!important;
    font-size: 12px;
    margin-top: 2px;
}
span.capcodeAdmin * {
    color: red!important;
}
span.capcodeMod * {
    color: #808!important;
}
.bottomCtrl {
    width: 100%!important;
    display: none;
}
.board {
    background: #0f0f0f;
    border-top: 1px solid #000;
    margin-bottom: 50px;
    padding-bottom: 25px;
    padding-left: 18%;
}
.is_index .board {
    padding-bottom: 50px;
}
a:hover,
.quoteLink:hover,
.quotelink:hover,
.deadlink:hover,
.summary a.replylink:hover,
.persistentNav .pageJump a:hover,
div#boardNavMobile .pageJump a:hover,
div.post div.postInfo span.postNum a:hover,
.posteruid .hand:hover {
    color: #5f5f82!important;
    text-decoration: underline!important;
}
div.postContainer {
    display: block;
    width: 100%!important;
    margin: 15px auto 0;
}
.thread {
    margin-top: 30px!important;
    padding-top: 15px;
}
.thread:first-child {
    padding-top: 15px;
    border-top: none;
}
.thread {
    margin-top: 30px !important;
    margin: 8px;
    background: #0f0f0f;
    padding-bottom: 15px;
    padding-top: 15px;
    border-radius: 2px;
}
.teaser {
    overflow: hidden;
    max-height: 120px;
}
.md-plus-btn {
    border-radius: 50%;
    font-size: 24px;
    height: 56px;
    min-width: 56px;
    width: 56px;
    overflow: hidden;
    box-shadow: 0 1px 1.5px 0 rgba(0, 0, 0, .12), 0 1px 1px 0 rgba(0, 0, 0, .24);
    line-height: normal;
    position: fixed;
    right: 25px;
    bottom: 25px;
    margin: auto;
    padding: 0;
}
div.boardBanner > div.boardTitle {
    font-size: 40px;
    color: #cdcdcd !important;
    font-family: Graphik-Regular-Web, sans-serif;
    font-weight: 700;
    letter-spacing: 0;
    padding-top: 10px;
    padding-bottom: 28px;
}
#togglePostFormLink,
.ad-plea,
#blotter tfoot td,
#ctrl-top,
div.post div.postInfo span.postNum,
div.pagelist div.pages {
    color: transparent;
}
.postInfo .backlink a.quotelink,
.postInfo .backlink a.quotelink:hover {
    color: #536dfe!important;
}
div.backlink {
    font-size: 0.75em !important;
}
.ws input[type="text"],
.nws input[type="text"] {
    border: none!important;
    border-bottom: 1px solid rgba(0, 0, 0, .12)!important;
    font-size: 14px!important;
    font-family: Graphik-Regular-Web, sans-serif;
    -webkit-transition: border-bottom-color .2s;
    transition: border-bottom-color .2s;
    padding: 4px!important;
}
.ws input[type="text"]:focus,
.ws #quickReply input[type="submit"]:focus {
    border-bottom: 1px solid #536dfe!important;
}
.nws input[type="text"]:focus,
.nws #quickReply input[type="submit"]:focus {
    border-bottom: 1px solid #f44336!important;
}
.nws input[type="submit"],
.nws button {
    background: #f44336;
}
#qrCaptchaContainerAlt {
    margin-bottom: 8px;
}
#postForm input[type="text"] {
    margin: 4px 0;
}
#postForm td:first-child {
    text-align: right;
    padding-right: 10px;
}
table#postForm td {
    background: transparent;
    border: 0;
    font-weight: 400;
}
#toggleMsgBtn {
    margin-left: 5px!important;
    display: none;
}
.rules {
    text-align: center;
}
#qrHeader {
    background: #676767!important;
    color: #fff!important;
}
#qrHeader a {
    color: #fff!important;
}
#search-box {
    height: inherit;
    line-height: inherit;
    margin: 0;
    padding: inherit;
}
#ctrl-top {
    padding-bottom: 10px;
    padding-top: 10px;
    text-align: center;
}
.reply:target,
.reply.highlight {
    background: #363636!important;
    border: 0!important;
    padding: 10px!important;
}
.navLinks {
    padding-bottom: 8px;
    color: transparent;
    width: 56%;
    margin: auto;
}
.navLinks label {
    color: #536dfe;
}
.navLinksBot {
    margin-top: 30px;
    text-align: left;
}
div#boardNavDesktop,
div#boardNavDesktopFoot,
div#boardNavMobile {
    background: #0f0f0f;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .08), 0 3px 1px -2px rgba(0, 0, 0, .06), 0 1px 5px 0 rgba(0, 0, 0, .04);
    margin-bottom: 25px;
    color: transparent!important;
    font-size: 14px;
    padding: 5px 10px;
}
div#boardNavMobile {
    color: #000;
    border: 0;
}
div#boardNavDesktopFoot {
	display: none;
}
div#boardNavDesktop a,
div#boardNavDesktopFoot a {
    margin-left: -3px;
    margin-right: -3px;
    padding: 0;
}
.persistentNav .pageJump a {
    padding-right: 5px;
    margin: 0;
}
div.pagelist {
    background: #0f0f0f;
    margin-top: -27px;
    margin-bottom: 25px;
    border: 0;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    margin-left: 25px;
    border-radius: 2px;
    padding: 8px;
    margin-left: 37%;
}
div#absbot #footer-links a {
    text-decoration: none!important;
    color: #536dfe!important;
}
div#absbot #footer-links a:hover {
    text-decoration: underline!important;
}
div.post,
.extPanel,
div.reply,
.dd-menu ul {
    background: #1c1c1c;
    border-radius: 2px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    border: none;
}
div.pagelist div.cataloglink {
    border: 0;
}
div.post {
    max-width: 75%!important;
    margin: 0;
    padding: 10px;
}
.dd-menu li {
    border: 0!important;
    padding: 6px 10px!important;
}
.dd-menu li:hover {
    background-color: #eee!important;
}
#quote-preview {
    border: 0!important;
    padding: 10px!important;
}
div.op {
    display: block;
    max-width: none;
}
.op .fileText {
    margin-top: -5px;
    margin-bottom: 5px;
}
.fileText a {
    text-decoration: none;
}
.posteruid,
.dateTime {
    color: #999;
}
div.post:after {
    display: block;
    content: ' ';
    clear: both;
}
div.post div.file .fileThumb img {
    object-fit: cover;
}
.postMessage {
    margin-left: 20px;
    margin-top: 5px;
}
#boardNavDesktop::after {
    content: ' ';
    display: block;
    clear: both;
}
span.summary {
    border-radius: 0 0 2px 2px;
    background-color: #292929;
    width: 68.75%;
    display: block;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    border-top: 1px solid rgba(0, 0, 0, 0.1);
    text-indent: 10px;
    margin: -1px 5% 0;
    padding: 10px 0;
}
#togglePostFormLink > a {
    border: none;
    border-radius: 2px;
    position: relative;
    height: 28px;
    min-width: 64px;
    display: inline-block;
    text-transform: uppercase;
    outline: none!important;
    cursor: pointer;
    background: #2489DA!important;
    color: #fff!important;
    font-size: 14px;
    font-family: Graphik-Regular-Web, sans-serif;
    line-height: 28px;
    padding: 5px;
}
.yotsuba:root #header-bar.dialog {
    background-color: rgba(255, 255, 255, 0.85);
}
.yotsuba:root .qr-link {
    background: linear-gradient(#ffffff, #e1e0e0) repeat scroll 0 0 transparent;
    border-color: #ccc;
}
.yotsuba:root .qr-link:hover {
    background: #fff;
}
.yotsuba:root .dialog {
    background-color: #e5e5e5;
    border-color: #000;
}
a.qr-link {
    border: none;
    border-radius: 2px;
    position: relative;
    height: 28px;
    min-width: 64px;
    display: inline-block;
    text-transform: uppercase;
    outline: none!important;
    cursor: pointer;
    background: #2489DA!important;
    color: #fff!important;
    font-size: 14px;
    font-family: Graphik-Regular-Web, sans-serif;
    line-height: 28px;
}
 :root.yotsuba #header-bar.dialog {
    background-color: #e5e5e5!important;
}
 :root.yotsuba #header-bar,
:root.yotsuba #notifications {
    color: #CCC!important;
}
 :root.yotsuba .dialog {
    background-color: #E5E5E5!important;
    border-color: #CCC!important;
}
 :root.yotsuba div.boardTitle {
    text-shadow: 2px 2px 2px #AFAFAF!important;
}
 :root.yotsuba .suboption-list > div:last-of-type {
    background-color: #F5F5F5!important;
}
#fourchanx-settings {
    background: #F5F5F5!important;
}
 :root.yotsuba #fourchanx-settings fieldset,
:root.yotsuba .section-main div::before {
    border-color: #C1C1C1!important;
}
input[type=text]:focus,
input[type=password]:focus,
input:not([type]):focus,
textarea:focus {
    border: 1px solid #1CBAEA!important;
}
 :root.yotsuba .field:focus,
:root.yotsuba .field.focus {
    border-color: #1CBAEA!important;
}
.board > .thread > div.postContainer {
    width: 90%!important;
}
.catalog-link {
    overflow: hidden!important;
}
.ad-cnt.center.topad,
.jlist-link.ad-plea-top.ad-plea,
.globalMessage,
.center.middlead,
.abovePostForm,
.belowLeaderboard,
.adg-rects,
.aboveMidAd,
.ad-plea,
.ad-plea-top #toggleMsgBtn,
#op,
#mpostform,
form[name="post"],
.navLinks + hr,
.open-qr-wrap,
.board hr,
#bannerCnt,
#ctrl-top + hr,
#ctrl-top > hr,
.ad-plea-bottom + hr,
.postInfo input[type="checkbox"],
div.sideArrows,
div#absbot {
    display: none;
}
a.useremail span,
a,
a:visited,
.quoteLink,
.quotelink,
.deadlink,
div#boardNavMobile .pageJump a,
.persistentNav .pageJump a,
.summary a.replylink,
div.post div.postInfo span.postNum a:visited,
div.post div.postInfo span.postNum a.replylink {
    color: #6e6e95!important;
    text-decoration: none!important;
}
a.useremail:hover span,
.postInfo a.postMenuBtn,
.postInfo a.postMenuBtn:hover {
    color: #5f5f82!important;
    text-decoration: none!important;
}
.useremail:not(:hover) .name,
.useremail:not(:hover) .postertrip,
a.replylink {
    color: #6e6e95!important;
}
.recaptchatable,
.c-thread img,
input[type="text"]:focus,
input[type="password"]:focus,
input:focus:not([type]),
textarea:focus,
.tomorrow .extPanel,
.recaptcha_input_area #recaptcha_response_field,
.ws textarea,
.nws textarea,
.ws textarea:focus,
.nws textarea:focus {
    border: 0!important;
}
#doc #bd div[style="text-align:center;margin: 10px 0"],
#styleSwitcher,
#togglePostFormLink,
#content .center,
.open-qr-wrap,
ul.rules,
#recaptcha_logo,
#recaptcha_tagline,
#blotter,
body > div.center,
body > form > div.center,
.click-me,
.postForm td label,
td[style="padding: 18px 7px 18px 7px;"],
td[style="padding: 4px 7px 12px 7px;"],
span.filter-preview,
.stylechanger,
.deleteform,
input[value="Report"],
input[value="delete"],
.postMenuBtn {
    display: none!important;
}
.post,
.postNum a,
.ws .postNum a,
table.postForm > tbody > tr > td:first-child,
.thread-stats,
.navLinks label + span {
    color: #c3c3c3!important;
}
.replyContainer,
.qrForm form table {
    width: 100%!important;
}
input[name="name"],
input[name="email"],
textarea[name="com"],
body:not(.is_index):not(.is_catalog) input[name="sub"],
#qrForm input[name="email"] {
    width: 90%!important;
}
input[name="sub"],
body:not(.is_index):not(.is_catalog) input[name="email"],
form[action="https://sys.4chan.org/b/post"] input[name="email"],
form[action="https://sys.4chan.org/soc/post"] input[name="email"] {
    width: 70%!important;
}
input[type="submit"],
form[action="https://sys.4chan.org/b/post"] input[type="submit"],
form[action="https://sys.4chan.org/soc/post"] input[type="submit"] {
    width: 19%!important;
}
.postarea td small,
.postarea td.rules,
.subject,
.post-subject {
    color: #aaa!important;
}
td[style="padding: 10px 7px 7px 7px;"],
.postingMode {
    background: none!important;
}
.boardTitle,
.boardSubtitle,
.pages strong,
.pagelist strong a {
    color: #cdcdcd!important;
}
 :-ms-input-placeholder,
::-webkit-input-placeholder,
:-moz-placeholder,
::-moz-placeholder {
    color: rgba(0, 0, 0, .35);
}
.ws input[type="submit"],
.nws input[type="submit"],
button,
.pageSwitcherForm > input[type="submit"] {
    border: none;
    border-radius: 2px;
    position: relative;
    height: 28px;
    min-width: 64px;
    display: inline-block;
    text-transform: uppercase;
    outline: none;
    cursor: pointer;
    background: #f44336;
    color: #fff;
    font-size: 14px;
    font-family: Graphik-Regular-Web, sans-serif;
    line-height: 28px;
    margin-left: 8px;
}
#togglePostFormLink > a:hover,
a.qr-link:hover {
    text-decoration: none!important;
}
}
