style.css:
@-moz-document domain("youtube.com") {
/*FADING*/

.resume-playback-background {
    opacity: 0.7 !important;
    height: 100% !important;
    width: 100% !important;
    background: #FFF !important;
}

.resume-playback-progress-bar {
    opacity: 0.5 !important;
}


/*STYLE*/
/*Search Bar*/
#masthead-search {
    margin: auto !important;
}

/*Links*/
.g-hovercard.yt-uix-sessionlink.spf-link,
.yt-uix-expander-head,
.yt-uix-sessionlink,
.yt-uix-sessionlink.yt-ui-ellipsis.yt-ui-ellipsis-2.spf-link

{
     color: #EFEFEF !important;
}

/*Text*/
#pl-header .pl-header-title,
#watch-description,
.branded-page-module-title,
.branded-page-module-title-text,
.comment-renderer-text,
.comment-simplebox-text,
.epic-nav-item-heading,
.guide-item,
.upload-option-description,
.tip-card-message,
.title,
.upload-option-text,
.upload-sidebar-header,
.watch-sidebar-head,
.watch-title,
.yt-uix-button-content,
.yt-ui-ellipsis,
.yt-masthead-picker-name,
.yt-uix-button.yt-uix-button-size-default.yt-uix-button-opacity.yt-uix-button-has-icon.no-icon-markup.yt-uix-button-toggled.yt-uix-post-anchor.yt-uix-tooltip span,
.yt-uix-button.yt-uix-button-size-default.yt-uix-button-opacity.yt-uix-button-has-icon.no-icon-markup.yt-uix-post-anchor.yt-uix-tooltip span,
.subscription-manager-title,
#yt-masthead-notifications-title


{
    color: #EFEFEF !important;
}

/*Buttons*/
#upload-btn.yt-uix-button-default {
    background: #444 !important;
    border-color: #444 !important;
}

.yt-uix-button-default {
    background: #666 !important;
    border-color: #444 !important;
}

.yt-ui-menu-content {
    background: #666 !important;
}

#yt-masthead-user .yt-uix-button-icon-bell {
    left: 5px !important;
}

.yt-uix-checkbox-on-off input[type=checkbox]:checked+label {
    background-color: #cc181e !important;
}

.yt-uix-button-subscribed-branded {
    background: #444 !important;
    border-color: #444 !important;
}

.yt-subscription-button-subscriber-count-branded-horizontal {
    color: #EFEFEF !important;  
    background: #444 !important;
	border-color: #444 !important;
}

.yt-uix-button-subscribe-unbranded {
    color: #EFEFEF !important;  
    background: #444 !important;
	border-color: #444 !important;
}

.yt-uix-button .yt-uix-button-size-default .yt-uix-button-default .yt-uix-menu-trigger {
    background: #444 !important;
}

.yt-uix-button.yt-uix-button-size-default.yt-uix-button-opacity.yt-uix-button-empty.yt-uix-button-has-icon.yt-uix-tooltip {
    background: #444 !important;
}

.standalone-collection-badge-renderer-text .yt-uix-sessionlink.spf-link {
    background: #666 !important;
}

.compact-shelf .yt-uix-button-shelf-slider-pager {
    background: #444 !important;
    border-color: #444 !important;
}

.pl-header-privacy-only-me {
    background-color: #666 !important;
    border-radius: 2px !important;
}

.yt-uix-button-subscribed-branded, .yt-uix-button-subscribed-branded[disabled], .yt-uix-button-subscribed-branded[disabled]:hover, .yt-uix-button-subscribed-branded[disabled]:active, .yt-uix-button-subscribed-branded[disabled]:focus, .yt-uix-button-subscribed-unbranded, .yt-uix-button-subscribed-unbranded[disabled], .yt-uix-button-subscribed-unbranded[disabled]:hover, .yt-uix-button-subscribed-unbranded[disabled]:active, .yt-uix-button-subscribed-unbranded[disabled]:focus {
    background-color: #878787 !important
}

/*Icons*/
.yt-sprite {
    color: #EFEFEF !important;
}

.yt-badge {
    color: #444 !important;
    border: 1px solid #444;
}

#action-panel-overflow-button:before,
.addto-button:before,
.action-panel-trigger-overflow:before,
.action-panel-trigger-share:before,
.like-button-renderer-dislike-button:before,
.like-button-renderer-like-button:before,
.yt-uix-button-icon-view-list
.yt-uix-button-icon-view-module

{
    background-color: #EFEFEF !important;
    border-radius: 2px;
}

/*Backgrounds*/
#body,
#footer-container,
#page,
#masthead-positioner-height-offset,
#yt-picker-country-footer,
#yt-picker-language-footer,
#yt-picker-safetymode-footer,
.comment-simplebox,
.comment-simplebox-text,
.watch-playlist,
.yt-masthead-account-picker-option,
.yt-masthead-account-picker-user-option,
.yt-masthead-picker-body,
.yt-masthead-picker-footer,
.yt-uix-clickcard-card-body,
.yt-uix-expander-ellipsis,
.yt-uix-sessionlink.yt-ui-ellipsis.yt-ui-ellipsis-2.spf-link

{
    background: #111 !important;
}

#yt-masthead-notifications-content .item-section>li>.yt-lockup-tile

{
    background: #333 !important;
}

#yt-masthead-notifications-content

{
    background: transparent !important;
}

#browse-items-primary .item-section>li>.yt-lockup-tile,
.branded-page-v2-subnav-container,
.feed-header,
.pl-video,
.search-header

{
    border-bottom: none !important;
}

#yt-masthead-container {
    background: #111 !important;
    border-bottom: none !important;
}

#masthead-appbar {
    background: #111 !important;
    border-bottom: none !important;
}

#appbar-guide-menu {
    background: #111 !important;
    border-right: none !important;
}

#player-playlist
.playlist-videos-list  {
    background-color: #111 !important;
}

.yt-card {
    background: #111 !important;
    box-shadow: none !important;
}

.yt-ui-ellipsis,
.yt-ui-ellipsis:hover {
    background: transparent !important;
}


/*Only when hovering*/
.yt-lockup-meta {
    color: #111 !important;
}

.yt-lockup-meta:hover {
    color: #FFF !important;
}

.yt-lockup-description {
    color: #111 !important;
}

.yt-lockup-description:hover {
    color: #EFEFEF !important;
}

.yt-uix-redirect-link {
    color: #111 !important;
}

.yt-uix-redirect-link:hover {
    color: #EFEFEF !important;
}

a.yt-uix-redirect-link {
     color: #111 !important;
}

a.yt-uix-redirect-link:hover {
     color: #EFEFEF !important;
}

/*Removing*/
#logo-container {
    display: none !important;
}

.yt-lockup-badges {
    display: none !important;
}


#yt-masthead-container {
    border-bottom: 8px solid #333 !important;
    top: -48px !important;
    transition: all 0.5s ease 0s !important;
 }

#yt-masthead-container:hover  {
    top: 0px !important;
  }


#appbar-guide-menu {
    border-right: 8px solid #333 !important;
    left: -222px !important;
    transition: all 0.5s ease 0s !important;
 }

#appbar-guide-menu:hover  {
    left: 0px !important;
  }

#page {
    padding-left: 0px !important;
}

/*Collections Extension*/
li.guide-section h3 a

{
    color: #EFEFEF !important;
}
}

