style.css:
/**
  * Google Play Music Black Style
  * @author Mauritz Zachrisson
  * @url https://github.com/macfeaster/play-music-black
  * @version 20190414
  */

@-moz-document url-prefix("https://play.google.com/music/listen") {

/* Global Styles */
body, .material, .material .cluster-text-protection, .material .cluster-text-protection::before { background: #111; }
body { background: #111 !important; }
.material-card { background: #303030; }
.cluster .header .cluster-title { color: white; }
body #main div .lane-content .fade-out:after { background: linear-gradient(to right,rgba(0,0,0,0),#111); }
body #main div .lane-content .material-shadow-z1 .fade-out:after { background: linear-gradient(to right,rgba(48,48,48,0),rgba(48,48,48,1)); }
.material-card .title { color: white; }
.material-card .sub-title { color: rgba(255, 255, 255, .7); }
.material-card .details paper-icon-button.menu-anchor { color: rgba(255, 255, 255, .7); }
.cluster .header .subtitle { color: rgba(255, 255, 255, .7); }
h2.title { color: white; }
#loading-overlay { background-color: rgba(0, 0, 0, 0.7)!important; }
.gpm-detail-page-header-0 div.gpm-detail-page-header h2, .gpm-detail-page-header-0 div.gpm-detail-page-header > [slot="buttons"] { color: #ddd !important; }
.gpm-detail-page-header-0 div.gpm-detail-page-header a, .gpm-detail-page-header-0 div.gpm-detail-page-header span { color: #aaa !important; }

/* App Bar */
#material-app-bar { background: #333; /* ff5722*/ border: none !important; }
.paper-tabs-0 #selectionBar.paper-tabs { background: white; }
#tabsContainer .tab-content.style-scope.paper-tab { color: white; }
body.qp #material-app-bar #material-one-left .music-logo { background-image: url('https://cdn.rawgit.com/macfeaster/3f2d97676bc1901cdd041dd0212cb390/raw/8cf75fb0ceb3f385c70c9567016edd11974ea9a1/play_music_white.svg'); }
#material-app-bar #countSummary, #material-app-bar #action-bar-container { color: white; }
#left-nav-open-button:after, #playlist-drawer-button:after { filter: invert(1) !important; opacity: 1 !important; }
#playlist-drawer-button.paper-icon-button-0 iron-icon.paper-icon-button { color: white; }
#left-nav-open-button.paper-icon-button-0 iron-icon.paper-icon-button { color: white; }
body.qp #content-container.has-hero-image #material-app-bar #material-one-left .music-logo-link { display: block; }
body.qp #content-container.has-hero-image.transparent #material-app-bar #material-one-left .music-logo-link { display: none; }
.sj-search-box-1 { background-color: #383838; }
body.qp #sliding-action-bar-container { background-color: #333; color: rgba(255, 255, 255, .7); }

/* Search Box */
.sj-search-box-0 { background: rgba(255, 255, 255, .15); }
.sj-search-box-0 #searchIcon.sj-search-box { color: white !important; }
.sj-search-box-0 #input.sj-search-box::-webkit-input-placeholder { color: rgba(255, 255, 255, .75); }
.sj-search-box-0 #input.sj-search-box { color: white !important; }
.sj-search-box-0[opened], .sj-search-box-1[opened], .sj-search-box-0[has-query], .sj-search-box-1[has-query] { background: #333; color: white; }
.sj-search-box-0 #clearButton.sj-search-box { color: rgba(255, 255, 255, .5); }
.sj-search-box-0[opened]:not([num-suggestions="0"]) #input.sj-search-box, .sj-search-box-1[opened]:not([num-suggestions="0"]) #input.sj-search-box { border-bottom: 1px solid #424242; }
sj-search-suggestion:hover, sj-search-suggestion.query-selected { background: #424242; }
sj-search-suggestion #queryText.sj-search-suggestion .sub-match.sj-search-suggestion { color: rgba(255, 255, 255, .7); }
.sj-search-box-1 #input.sj-search-box { color: white; }
.sj-search-suggestion-0:hover, .sj-search-suggestion-0.query-selected {
    background-color: #444;
}
.sj-entity-suggestion-0 #title.sj-entity-suggestion { color: #eee; }
.sj-search-box-1 sj-entity-suggestion.sj-search-box:last-of-type { border-bottom: 7px solid #282828; box-shadow: none; }
.sj-search-suggestion-0 #queryText.sj-search-suggestion .sub-match.sj-search-suggestion { color: #eee; }
.sj-entity-suggestion-0:hover, .sj-entity-suggestion-0.query-selected { background: #444; }

/* Home */
.column .material-card .title, .recommended-header { color: white !important; }
.column .material-card .sub-title { color: rgba(255, 255, 255, .7) !important; }
.material-card .reason .reason-text { color: rgba(255, 255, 255, .5) !important; }
.sj-home-0 #gradient.sj-home { background: linear-gradient(to top, #111, rgba(0,0,0,.3)), linear-gradient(to right, #111, rgba(0,0,0,.3)) !important; }
sj-home #gradient.sj-home { background: linear-gradient(to top, #111, rgba(0,0,0,.3)), linear-gradient(to right, #111, rgba(0,0,0,.3)) !important; }
sj-home h2 span.home-palette-id-1.tooltip { color: white !important; }
.sj-home-0 #backgroundColor.sj-home { background: #111 !important; }
sj-home #backgroundColor.sj-home { background: #111 !important; }
gpm-card-grid .sj-card-0[card-aspect-ratio="wide"] { background: #333 !important; }
.sj-card-0[card-aspect-ratio="wide"] #details.sj-card > [slot="title"] { color: #ccc !important; }
.sj-card-0 #details.sj-card > [slot="title"] { color: #fff !important; }
.sj-card-0 #details.sj-card > [slot="subtitle"] { color: #ccc !important; }
.sj-card-0 #details.sj-card > [slot="description"], .gpm-headline-header-0 > [slot="subtitle"] { color: #999 !important; }
sj-home[selected="6"] .home-palette-id-2 { color: #999 !important; }
.gpm-headline-header-0 h2.gpm-headline-header > a { color: #ccc !important; }
.sj-page-indicator-0 .sj-page-tab.sj-page-indicator { color: rgba(200, 200, 200, 0.5); }
.gpm-card-grid-1 sj-card .card-title { color: white !important; }
.gpm-card-grid-1 sj-card .card-description { color: rgba(255, 255, 255, .7) !important; }
.gpm-now-card-grid-0 .buttons.gpm-now-card-grid paper-icon-button.gpm-now-card-grid { background-color: rgba(255, 255, 255, 0.2); }
sj-home[selected="0"] .home-palette-id-2, sj-home[selected="1"] .home-palette-id-2, sj-home[selected="2"] .home-palette-id-2, sj-home[selected="3"] .home-palette-id-2, sj-home[selected="4"] .home-palette-id-2,
sj-home[selected="5"] .home-palette-id-2, sj-home[selected="6"] .home-palette-id-2, sj-home[selected="7"] .home-palette-id-2, sj-home[selected="8"] .home-palette-id-2,
sj-home[selected="9"] .home-palette-id-2, sj-home[selected="10"] .home-palette-id-2, .home-palette-id-2 { color: #777 !important; }

/* Recent Activity, Top Songs */
.recent-view #gridTitle { color: white !important; }
.recent-view .details .card-title { color: white !important; }
.recent-view .details .card-subtitle { color: rgba(255, 255, 255, .7) !important; }
.gpm-card-grid-3 > [slot="title"] { color: white !important; }
.top-tracks { background: #222; }
.top-tracks .top-tracks-info .top-tracks-header { color: white; }

/* Playlists, albums etc */
#music-content.material { background: transparent; }
.material-playlist-container, .material-album-container, .songlist-container, .material-detail-view .artist-details { background: #212121 !important; }
.material .song-row td, .material .upload-progress-row td { background: #212121; color: rgba(255, 255, 255, .7); }
.material .song-row.selected-song-row td, .material .song-row.currently-playing td { background-color: #272727 !important; }
.song-row .title-right-items { color: rgba(255, 255, 255, .7); }
.material .song-table [data-col="title"], .material .song-table.mini [data-col="song-details"] .song-title { color: rgba(255, 255, 255, .7); }
tr.song-row a { color: rgba(255, 255, 255, .7); }
.song-table th { color: rgba(255, 255, 255, .5); }
.song-row:hover > td { background-color: #282828 !important; }
.material-container-details .info .title .title-text { color: white; }
.sub-details a { color: white; }
.material-detail-view .material-container-details .info .container-stats-container .container-stats, .material-detail-view .material-container-details .info .container-stats-container .container-stats span { color: rgba(255, 255, 255, .7); }
.material-container-details .info .sub-details paper-icon-button { color: white; }
gpm-action-buttons { border-top: 1px solid #333; color: rgba(255, 255, 255, .7); }
.song-table th iron-icon { color: rgba(255, 255, 255, .7); }
.rating-container.thumbs li { -webkit-filter: brightness(500%); }
.fade-out.gray:after { background: linear-gradient(to right,rgba(17,17,17,0),rgba(17,17,17,1)); }
.song-table.tight[data-type="ap"] .song-indicator[data-playback-status="playing"], .song-table.tight[data-type="sarts"] .song-indicator[data-playback-status="playing"], .queue-song-table .song-indicator[data-playback-status="playing"] { background-image: url('https://play.google.com/music/ani_equalizer_white.gif')!important; background-size: 24px 24px!important; background-color: rgba(0,0,0,.8)!important; transform: none; }
.song-table.tight[data-type="ap"] .song-indicator[data-playback-status="paused"], .song-table.tight[data-type="sarts"] .song-indicator[data-playback-status="paused"], .queue-song-table .song-indicator[data-playback-status="paused"] { filter: invert(84.8%)!important; background-color: rgba(255,255,255,.95)!important; transform: none; }
.song-table.tight[data-type="ap"] .song-indicator[data-playback-status="loading"], .song-table.tight[data-type="sarts"] .song-indicator[data-playback-status="loading"], .queue-song-table .song-indicator[data-playback-status="loading"] { background-image: url('https://play.google.com/music/ani_loading_white_x2.gif')!important; background-size: 24px 24px!important; background-color: rgba(0,0,0,.8)!important; transform: none; }
.songlist-container .song-indicator[data-playback-status="playing"] { background-image: url('https://play.google.com/music/ani_equalizer_white.gif')!important; transform: scale(.6) translateX(-32px); }
.songlist-container .song-indicator[data-playback-status="paused"] { filter: invert(84.8%)!important; background-color: #fff!important; transform: translateX(-18.5px) translateY(.8px); }
.songlist-container .song-indicator[data-playback-status="loading"] { background-image: url('https://play.google.com/music/ani_loading_white_x2.gif')!important; transform: scale(.6) translateX(-32px); }
.songlist-container iron-icon.hover-button { color: #ddd !important; transform: translateX(-26px); }
.songlist-container iron-icon.hover-button[data-id="play"] { transform: translateX(0); }
.song-table.tight[data-type="ap"] iron-icon.hover-button, .song-table.tight[data-type="sarts"] iron-icon.hover-button { transform: none; }
.material-detail-view .material-container-details .info .title { color: white; }
.playlist-view .editable:hover { background: rgba(255, 255, 255, .1); }
h2.section-header { color: #fff; }
.gpm-detail-page-header-0[description-overflows] #descriptionWrapper.gpm-detail-page-header:hover { background: #333; }
.song-table.tight .song-row td, .song-table.tight .song-row td:hover { border-bottom: none !important; }
.song-table.tight .song-row td .title-right-items { height: 40px !important; }

/* Artist Pages */
.material-detail-view .artist-details { color: white; }
.material-detail-view .artist-details .bio-wrapper .bio { color: rgba(255, 255, 255, .7); }
.material-detail-view .material-container-details .read-more-button:hover { background: #333; }
body.qp .material .cluster-text-protection::before, body.qp .material .cluster-text-protection { background: #111; }

/* Player */
#player { background: #212121; }
#currently-playing-title { color: white; }
#player-artist, .player-album, .player-dash { color: rgba(255, 255, 255, .7); }
#player .now-playing-actions paper-icon-button { color: rgba(255, 255, 255, .7); }
#player-bar-forward iron-icon, #player-bar-rewind iron-icon, paper-icon-button[data-id="cast"] iron-icon, #material-volume-indicator, #material-volume-low, #material-volume-high { fill: rgba(255, 255, 255, .7); }
.paper-progress-0 #progressContainer.paper-progress, .paper-progress-0 .indeterminate.paper-progress::after { }
#player paper-icon-button, #queue { color: rgba(255, 255, 255, .7); }
.paper-icon-button-0 #ink.paper-icon-button { color: white; }
#player:hover #material-player-progress:not([disabled]) #progressContainer { background: #424242; }
#player paper-icon-button { color: rgba(255, 255, 255, .7); }
#player-bar-play-pause #icon > svg > g > path:nth-child(2) { fill: #222!important; }

/* Left Drawer */
#nav-container .nav-toolbar, .paper-drawer-panel-0 iron-selector.paper-drawer-panel > #drawer.paper-drawer-panel { background: #161616; }
#nav-container { border: none !important; }
.nav-item-container { color: #fff; }
#nav .nav-section.material, #nav.material { background: #161616; }
#nav_collections .nav-item-container:focus, .nav-item-container:focus, .nav-item-container:hover { background: #212121; }
.new-playlist-drawer .nav-section-divider { border-bottom: 1px solid #333; }
.nav-item-container.selected { background: #333; }
.new-playlist-drawer #nav-container .nav-toolbar .menu-logo { content: url('https://cdn.rawgit.com/macfeaster/3f2d97676bc1901cdd041dd0212cb390/raw/8cf75fb0ceb3f385c70c9567016edd11974ea9a1/play_music_white.svg'); }
.paper-drawer-panel-1 iron-selector.paper-drawer-panel > #drawer.paper-drawer-panel, .paper-toolbar-2 { background-color: #161616; }

/* Right Drawer */
sj-right-drawer #drawer.sj-right-drawer { background: #161616; }
#playlist-drawer paper-header-panel[at-top] paper-toolbar:not([style-scope]):not(.style-scope) { border-bottom: 1px solid #333; }
.paper-toolbar-1, .paper-toolbar-2 { background: #161616; color: white; }
#playlist-drawer #playlist-drawer-header { color: white; }
#playlist-drawer a { color: white; }
#playlist-drawer .autoplaylist-section, #playlist-drawer #recent-playlists-container { border-bottom: 1px solid #333; }
#playlist-drawer .playlist-drawer-item .playlist-wrapper:hover, #playlist-drawer .playlist-drawer-item .playlist-wrapper:focus, #playlist-drawer .playlist-drawer-item sj-play-button:hover~.playlist-wrapper, #playlist-drawer .playlist-drawer-item iron-icon:hover~.playlist-wrapper, #playlist-drawer .playlist-drawer-item.playlist-drop-target:not(.subscribed) .playlist-wrapper { background: #272727; }
#playlist-drawer:not([style-scope]):not(.style-scope) paper-header-panel[at-top]:not([style-scope]):not(.style-scope) paper-toolbar:not([style-scope]):not(.style-scope) { border-bottom: 1px solid #333; }

/* Queue */
#queue-overlay::after { border: 8px solid #212121; }
.sj-mini-queue-0 #queueContainer.sj-mini-queue { background: #212121; }

/* Modals */
.iron-overlay-backdrop-0 { background: black; }
.paper-dialog-0 { background: #333; color: #fff; }
.paper-input-container-0 .input-content.paper-input-container  input, .paper-input-container-0 .input-content.paper-input-container  textarea, .paper-input-container-0 .input-content.paper-input-container  iron-autogrow-textarea, .paper-input-container-0 .input-content.paper-input-container  .paper-input-input { color: white; }
.share-buttons .share-button .button-label { color: white; }
.visualizercard { background: #212121; }
.visualizercard .label { color: white; }
.material-share-options #sharing-option-label { color: #fff; }
.paper-input-container-0 .input-content.paper-input-container .label-and-input-container.paper-input-container > [slot="input"] { color: #ddd; }

/* Listen Now etc */
.situations-filter { background: #212121; color: white; }
.filter-description { color: rgba(255, 255, 255, .7) !important; }
.material-list li:not(:last-child) .li-content { border-bottom: 1px solid #333; }
.material-list a { color: white; }
.material-list li a:hover, .material-list li a:focus { background: #272727; }
.radio-selection.material { background: transparent; }
.material-detail-view .station-container-content-wrapper .material-container-details { background: #212121; }
.sub-details { color: rgba(255, 255, 255, .7); }
.material-detail-view .material-container-details .info .description { color: white; }

/* Top Tracks, New Releases etc */
.material-detail-view .top-tracks { background: #212121; color: white; }
.more-songs-container { border-top: 1px solid #333; }

/* Context Menus */
.playlist-menu, .goog-menuitem, .goog-menuitem-content, .now-playing-menu, .goog-menu-vertical { color: rgb(200, 200, 200)!important; background: #333 !important; }
.material .goog-menu .goog-menuseparator { background: #424242 !important; }
.material .goog-menuheader { color: rgba(255, 255, 255, .5) !important; }
.goog-menuitem:hover { background: #363636 !important; }
.goog-menuitem:hover > .goog-menuitem-content { color: white !important; background: #363636 !important; }
.goog-menuitem-highlight .goog-menuitem-content .goog-submenu-arrow, .goog-menuitem-highlight .goog-menuitem-content { color: #fff !important; }

/* Scroll Bars */
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { border-radius: 10px; background: rgba(0, 0, 0, .1); }
::-webkit-scrollbar-thumb { border-radius: 10px; background: rgba(0, 0, 0, .5); }

/* Account Switcher etc */
#gbwa .gb_g .gb_ca, .gb_ja, #gbwa .gb_ga { background-color: rgb(80, 80, 80)!important; }
#gbwa .gb_g { border: none!important; }
#gbwa .gb_Z .gb_O { color: white!important; }
#gbwa .gb_Z .gb_O:hover { color: black!important; }
#gbwa .gb_ha a { color: rgba(255, 255, 255, 0.6)!important; }
#gbwa .gb_ka { background-color: rgb(60, 60, 60)!important; }
#gbwa .gb_ka:hover, #gbwa .gb_ka:hover > #gbwa .gb_ha a { color: rgba(255, 255, 255, 1)!important; background-color: rgb(40, 40, 40)!important; }
#gbwa .gb_ha a { color: rgba(255, 255, 255, 0.6)!important; }
#gbwa .gb_la { background-color: rgb(60, 60, 60)!important; }
#gbwa .gb_la:hover { color: rgba(255, 255, 255, 1)!important; background-color: rgb(40, 40, 40)!important; }
.gb_la { border-color: rgba(255, 255, 255, 0.3)!important; }
#gbwa .gb_wa { background: rgb(60, 60, 60)!important; }
#gbwa .gb_wa .gb_ya { color: rgba(255, 255, 255, 0.8)!important; }
.gb_fa .gb_ea .gb_b { filter: invert(1); opacity: 1; }
#gbwa>div:first-child>a:after, #gbwa > div.gb_Fc > a { filter: invert(1) !important; -webkit-filter: invert(1) !important; opacity: 1 !important; }
.gb_za .gb_ia { background: #444; }
.gb_Nc .gb_b.gb_dc, .gb_b.gb_cc, .gb_b.gb_ec, .gb_b.gb_fc, .gb_b.gb_gc { filter: invert(1); opacity: 1; }

/* Settings */
.nuq-view button.quiz-block .name{color: #5f5f5f}

/* Various fixes, like the f*cking Holo context menu on the Now Playing bar */
.now-playing-menu.goog-menu, .goog-menu { border: none !important; border-radius: 2px; box-shadow: 0 1px 4px 0 rgba(0,0,0,0.37)!important; }
.spinner { background-color: transparent !important; filter: invert(1); }

}
