options.value:
@-moz-document /*[[domain]]*/ {
/*****       Userscript       *****/
/* (code placed at top for speed) */
div.sr-cont > * {
    position: fixed;
}
div.sr-cont input {
    background-color: rgba(255, 255, 255, 0.15);
}
div.sr-overlay {
    top: 0; left: 0;
    width: 100%; height: 100%;
    background: rgba(0, 0, 0, 0.5);
    z-index: 100;
}
div.sr-menu {
    top: 50%; left: 50%;
    height: 60%; width: 60%;
    margin: -22% -30%;
    border-radius: 15px;
    background: #222;
    box-shadow: 0 0 19px #000;
    z-index: 101;
}
div.sr-header, div.sr-content {
    position: absolute;
}
div.sr-header {
    top: 0; left: 0;
    height: 8%; width: 100%;
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
}
h3.sr-title {
    position: inherit;
    top: 25%; left: 2%;
}
svg.sr-close {
    position: inherit;
    top: 18%; right: 1.3%;
    background: rgba(255, 255, 255, 0.2);
    border-radius: 50%;
}
svg.sr-close:hover {
    background: rgba(255, 255, 255, 0.3);
}
div.sr-content {
    position: absolute;
    top: 8%; left: 0;
    width: 100%; height: 92%;
}
div.section {  
    padding: 10px;
	height: 70%;
}
div.section:first-child {
	height: 20%;
}
span.header {  
    display: block;
    margin-bottom: 8px;
}
input.sr {
    width: 41.5%;
    margin: 5px;
    padding: 5px;
    border-radius: 3px;
}
input#sr_blname {
    width: 83%;
}
button.sr-button {
    height: 25px; width: 9%;
    border-radius: 3px;
    color: #fff;
    margin: -10px 5px;
    box-shadow: 0 0 6px rgba(0, 0, 0, 0.3);
}
button.sr-button.sm {
    height: 15px; width: 45px;
}
button.sr-button:hover {
    filter: brightness(1.1);
}
span.white {
    color: #fff;
}
div.section > span.gray {
    margin-left: 7px;
}
ul.sr-list {
	height: 92%;
	overflow-y: auto;
}
ul.sr-list > li {
    padding-bottom: 4px;
}
li.setting input.sr {
	margin: 0px 5px 0px 1px !important;
}
li.setting input.sr:checked:before {
    margin: 0;
}
input.sr::-webkit-input-placeholder {
    color: rgba(255, 255, 255, 0.5);
}
}

