style.css:
@-moz-document domain("op.gg")
{

    body {
        background-color: #111 !important;
        color: #7F8C8D !important;
    }

    div {
       color: #7F8C8D !important;
    }
    
    .Box {
        background: #1A1A1A !important;
        border: 1px solid #333 !important;
        box-shadow: none !important;
    }
    
    .MultiSearchResultRowHeader
    , .MostChampionSeason 
    {
        background: #1A1A1A !important;
    }
    
    
    .PreviousSeason li
    {
        background: #242424 !important;
        color: #EFEFEF !important;
    }
    
    .LayoutWrap > .ContentWrap {
        border-bottom: 1px solid #333 !important;
        background: #242424 !important;
    }
    
    .SummonerLayout > .ContentWrap {
        background-color: #111 !important;
        color: #7F8C8D !important;
    }
    
    #highcharts-0 > svg > rect {
        fill: #070707 !important;
    }
    
    .Button.SemiRound.White {
        border: 1px solid #242424 !important;
        border-bottom: 1px solid #222 !important;
        background: #111 !important;
        color: #888 !important;
    }
    
    .SummonerLayout > .Menu {
        border-bottom: 1px solid #222 !important;
    }
    
    .SummonerLayout > .Menu > .MenuList > .Item.active > a {
        border: 1px solid #222 !important;
        background: #181818 !important;
        color: #EFEFEF !important;
    }
    
    .SummonerLayout > .Menu > .MenuList > .Item > a {
        border: 1px solid #242424 !important;
        border-bottom: 1px solid #222 !important;
        background: #111 !important;
        color: #999 !important;
    }
    
    /* Summoner > Summary Tab */
    /* Summoner > Summary Tab > Left sidebar */

    .Box > .Title + .Content, .Box > .Navigation + .Content, .Box > .Tab + .Content,
    .ChampionWinRatioBox, .PositionWinRatioBox {
        background: #1A1A1A !important;
        border-bottom: 1px solid #222 !important;
    }
    
    .SummonersMostGameTable > .Header > .Row > .HeaderCell {
        background-color: #111 !important;
        border-bottom: 1px solid #222 !important;
    }
    
    .ChampionBox {
        border-bottom: 1px solid #222 !important;
    }
    
    .WinRatioGraph > .Graph > .Fill.Left {
        background: #051320 !important;
        border: 1px solid #071E33 !important;
    }
    
    .WinRatioGraph > .Graph > .Fill.Right {
        background: #200908 !important;
        border: 1px solid #300E0D !important;
    }

    /* Summoner > Summary Tab > Main content */
    
    .GameAverageStats {
        background-color: #111 !important;
        color: #7F8C8D !important;
    }
    
    .GameItem > .Card > .Title {
        border-bottom: 1px solid #333 !important;
    }
    
    .GameItem > .Card > .Title > .Right > .Actions > .Action {
        border-color: #333 !important;
    }
    
    .GameItem.Win, .GameItem.Win > .Card {
        /* background-color: #11252F !important; */
        background-color: #0c2739 !important;
        border-color: #07141d !important;
    }
    
    .GameItem.Lose, .GameItem.Lose > .Card {
        /* background-color: #2A1312 !important; */
        background-color: #2A0D0C !important;
        border-color: #1d0909 !important;
    }
    
    .GameDetailTable.Result-WIN {
        background-color: #090F12 !important;
    }
    
    .GameDetailTable.Result-LOSE {
       background-color: #170E0E !important;
    }
    
    /* Summoner > Summary Tab > Right sidebar  */
    
    .LayoutWrap > .ContentWrap > .SideBarWrap {
        background: #222 !important;
        border-left: none !important;
    }
    
    
    /* Summoner > Champions tab */
    
    .Box > .Tab > .TabList > .Item {
        background: #1A1A1A !important;
        border: #333 !important;
    }
    
    .ChampionStatsTable > .Body > .Row.TopRanker {
        background: #1A1A1A !important;
    }
    
    
    
    /* Disable ads */
    
    .adsbygoogle {
        display: none !important;
    }
    
    
    /* Old */
    
    .FooterWrap {
       display: none !important;
    }
  
    div.SummonerHeader > .SummonerHeaderInfoArea {
        background: #222 !important;
        border-color: #444 !important;
    }
    
    div.SummonerHeader > .SummonerHeaderInfoArea > .SummonerHeaderColumn > a {
        text-shadow: 0px 1px #1A1A1A !important;
    }
    
    .nBox .nBoxContent {
        background: #222 !important;
    }
    
    .nBox .nBoxHeader {
        background: #222 !important;
    }
    
    .AverageGameStats .AverageGameStatsBox {
        background-color: #222 !important;
    }
    
    .AverageGameStats .AverageGameStatsBox > .Title {
        background: #222 !important;
        text-shadow: 0px 1px #1A1A1A !important;
    }
    
    .AverageGameStats .AverageGameStatsBox > .Graph > .GraphInnerWrap > .WinRatioTitle {
        text-shadow: 0px 1px #1A1A1A !important;
    }
    
    .AverageGameStats .AverageGameStatsBox > .KDA {
        text-shadow: 0px 1px #333 !important;
    }
    
    .SummonerRatingMedium > .TierRankInfo > .WinLose > .winratio
    {
        color: rgba(76, 117, 156, 1) !important;
    }
    
    .GameBox.game-win {
       /*background: rgba(34, 59, 75, 1) !important;*/
        background: #19264D !important;
    }
    
    .GameBox.game-lose {
        /*background: rgba(63, 39, 37, 1) !important;*/
        background: #3F2725 !important;
    }

    .GameBox > .GameSimpleStats > .ItemsTrinket > .caption {
        text-shadow: 0px 1px #111 !important;
    }

	.TextArea
	, .SearchInputWrap
    , .ChampionMatchSearchWrap
    , .ChampionMatchSearchWrap .Header
	{
		background: #333333 !important;
		color: #EFEFEF !important;
		border: 1px solid #888888 !important;
	}
	
	.SearchInput
    , .ChampionMatchSearchWrap Input
    , .InputText Input
    {
		background: #333333 !important;
		color: #EFEFEF !important;
	}
    
	.SearchExtra
	, .SearchExtra
	, .tabHeaders
	, .tabHeader 
    , .SelectMatchChampionList 
    , .SelectMatchChampionList li
	{
		background: #444444 !important;
		color: #EFEFEF !important;
	}
    
    .SummonerName
    , .Link 
    , .PageDescription .Text
    , h2
    , .Region a
    , .Name
    , .FavoriteButton
    , .Button.SemiRound.White
    {
        color: #EFEFEF !important;
    }

	.Submit,
    .Button.active 
    {
		background: #2B3B4B !important;
		border: 1px solid #4A6682 !important;
	}
    
    .PageHeaderWrap > .Menu > .MenuList > .Item.active {
        border-color: #d7dada !important;
    }
    
    .SummonersMostGameTable > .Body > .Row.Odd > .Cell {
        background: #111111 !important;
    }
    
    .SummonerLayout > .Header > .PastRank > .PastRankList > .Item {
        background: #2B3B4B !important;
        color: #EFEFEF !important;
    }
    
    .GameItem > .Content > .KDA > .KDARatio .KDARatio
    , .GameItem > .Content > .KDA > .KDA .Kill
    , .GameItem > .Content > .KDA > .KDA .Assist
    , .GameItem > .Content > .GameStats Div
    , .GameItem > .Content > .Stats Div
    {
        color: #EFEFEF !important;
    }
    
    .LeagueContainer > .LeagueContent .Table > .Content > .Row.series > .Cell
    {
        background: #2B3B4B !important;
    }
    
    .LeagueContainer > .LeagueContent .Table > .Content > .Row > td
    , .LeagueContainer > .LeagueContent .Table > .Header > .Row > td
    , .LeagueContainer > .LeagueHeader > .LeagueInfo > div
    {
        color: #EFEFEF !important;
    }
    
    /* Rune Page */
    
    .RunePageWrap > .RunePageName, .MasteryPageWrap > .RunePageName, .RunePageWrap .MasteryPageName, .MasteryPageWrap .MasteryPageName
    , .RunePageSummary
    /*, .RunePageSummaryList
    /*.RunePageWrap > .RunePageName, .MasteryPageWrap > .RunePageName, .RunePageWrap .MasteryPageName, div*/
    {
        background-color: #444444 !important;
    }
    
    .RunePageStatistics > .RunePageStatisticsList > .Item span
    , .RunePageWrap > .RunePageName, .MasteryPageWrap > .RunePageName, .RunePageWrap .MasteryPageName, .MasteryPageWrap .MasteryPageName, .RunePageSummaryList
    {
        color: #EFEFEF !important;
    }
    
    /********* Profile Section ***************/
    
    .GameItem.Lose .tabItem  {
        background: #664444 !important;
    }
    
    .GameItem.Win .tabItem  {
        background: #334477 !important;
    }
    
    .MatchDetailHeader .tabItem .tabHeader a {
        color: #efefef !important;
    }
    
    .Content .tabHeader a {
        color: #efefef !important;
    }
    
    .MatchDetailHeader > .tabHeaders .tabHeader:not(.active) a {
        color: #050505 !important;
    }
    
    .GlobalHeaderNotificationBar span {
        color: #efefef !important;
    }
    
    /********* Overview Section ***************/
    
    .GameItem.Win > .Content {
        /*background-color: #a3cfec;
        border-color: #99b9cf;
        */
        /*background: rgba(34, 59, 75, 1) !important;*/
        background: #19264D !important;
    }
    
    .GameItem.Win > .Content > .StatsButton {
        /*background: rgba(34, 59, 75, 1) !important;*/
        background: #19264D !important;
    }
    
    .GameItem.Lose > .Content {
        /*background-color: #a3cfec;
        border-color: #99b9cf;
        */
        /*background: rgba(63, 39, 37, 1) !important;*/
        background: #3F2725 !important;
    }
    
    .GameItem.Lose > .Content > .StatsButton {
        /*background: rgba(63, 39, 37, 1) !important;*/
        background: #3F2725 !important;
    }
    
    .MatchDetailHeader ul li.active a {
        background: #555555 !important;
        color: #efefef !important;
    }
    
    .GameDetailTable > .Header > .Row > .HeaderCell {
        color: #efefef !important;
    }
    
    .GameDetailTable > .Content > .Row > .Cell
    , .GameDetailTable > .Content > .Row > .Cell *
    , .Team *
    {
        color: #efefef !important;
    }
    
    .GameDetailTable.Result-LOSE > .Header
    {
        background: #553333 !important;
    }
    
    .GameDetailTable.Result-LOSE > .Header .Row > .HeaderCell > .GameResult {
        color: #EE5A52 !important;
    }
    
    .GameDetailTable.Result-LOSE > .Content {
        background-color: #664444 !important;
    }
    
    .GameDetailTable.Result-LOSE > .Content > .Row.isRequester {
        background-color: #775555 !important;
    }
    
    .GameDetailTableWrap > .Summary > .Team.Result-LOSE
    {
        background: #553333 !important;
    }
    
    .GameDetailTableWrap > .Summary > .Bar.Result-LOSE {
        background: #553333 !important;
    }
    
    .GameDetailTableWrap > .Summary > .Bar.Result-LOSE > div::after {
	   border-bottom-color: #223366;
    }
    
    .GameDetailTable.Result-WIN > .Header
    {
        background: #223366 !important;
    }
    
    .GameDetailTableWrap > .Summary > .Team.Result-WIN {
        background: #223366 !important;
    }
    
    .GameDetailTableWrap > .Summary > .Bar.Result-WIN {
        background: #223366 !important;
    }
    
    .GameDetailTableWrap > .Summary > .Bar.Result-WIN > div::after {
	   border-bottom-color: #553333;
    }
    
    .GameDetailTable.Result-WIN > .Content {
        background-color: #334477 !important;
    }
    
    .GameDetailTable.Result-WIN > .Content > .Row.isRequester {
        background-color: #445588 !important;
    }
    
    .GameItem.Win .tabHeaders {
        background-color: #19264D !important;
    }
    
    .GameItem.Lose .tabHeaders {
        background-color: #3F2725 !important;
    }
    
    /********* End Overview Section ***************/
    
    
    /********* Team Analysis Section ***************/
    
    .MatchAnalysisListItemContent h4
    , .Value
    , Label span
    , .Message
    {
        color: #efefef !important;
    }
    
    .MatchAnalysisListItemContent .MatchAnalysisTable .Team ul li .Progress
    {
        background: #333333 !important;
    }
    
    .TeamAnalysisGraphTab ul li a
    {
        background: #333333 !important;
    }
    
    .MatchDetailTeamLegend ul li span {
        color: #EFEFEF !important;
    }
    
    .TeamAnalysisTimelineList > ul li > .Message b {
        color: #EFEFEF !important;
    }
    
    .TeamAnalysisTimelineList > ul li.Result-WIN > .Message {
        /*background: #8899CC !important;*/
        background: #445588 !important;
        color: #EFEFEF !important;
    }
    
    .TeamAnalysisTimelineList > ul li.Result-LOSE > .Message {
/*        background: #CC9988 !important;*/
        background: #775555 !important;
        color: #EFEFEF !important;
    }
    
    .TeamAnalysisTimelineList > ul li .ExtraEvent {
        background: #989272 !important;
    }
    
    /********* End Team Analysis Section ***************/
    
    
    /********* Builds Section ***************/
    
    .MatchBuild .Title
    , .Time
    , .Name
    , .Count
    , .Summary
    , .HotKey
    , .MatchBuild > .SkillBuild > .Content > .RealContent .SkillBuildTable > .Content > .Row > .Cell.Label
    {
        color: #efefef !important;
    }
    
    .GameItem.Lose .MatchBuild > .RuneMastery > .Mastery .MasteryPageSmall
    {
        background: #775555 !important;
    }
    
    .GameItem.Lose .MatchBuild .Title
    {
        background: #553333 !important;
    }
    
    .GameItem.Lose .MatchBuild > .ItemBuild > .List > .Item > .Items
    , .GameItem.Lose .MatchBuild > .ItemBuild > .List > .Item > .Bar
    {
        background: #EECCCC !important;
    }
    
    .GameItem.Win .MatchBuild > .RuneMastery > .Mastery .MasteryPageSmall
    {
        background: #445588 !important;
    }
    
    .GameItem.Win .MatchBuild .Title
    {
        background: #223366 !important;
    }
    
    .GameItem.Win .MatchBuild > .ItemBuild > .List > .Item > .Items
    , .GameItem.Win .MatchBuild > .ItemBuild > .List > .Item > .Bar
    {
        background: #223366 !important;
    }
    
    /********* End Builds Section ***************/
    
    
    /********* ETC Section ***************/
    
    .highcharts-axis-labels tspan
    , .highcharts-axis-labels text
    {
        fill: #efefef !important;
        color: #efefef !important;
    }
    
    .GameItem.Win .tabHeaders .tabHeader {
        background-color: #19264D !important;
    }
    
    .GameItem.Lose .tabHeaders .tabHeader {
        background-color: #3F2725 !important;
    }
    
    .GameItem .tabHeaders .tabHeader.active {
        background-color: #555555 !important;
    }
    
    /*.GameItem.Win .tabHeaders .tabHeader.active a {
        color: #050505 !important;
    }*/
    
    /********* End ETC Section ***************/
    
    /********* End Profile Section ***************/
    
    /********* Begin Live Game Section ***************/
    
    .SpectateSummoner > .Box > .Title
    , .SpectateSummoner > .Box > .Title > small {
        color: #efefef !important;
    }
    
    .SpectateSummoner > .Box > .Content > .Table > .Header > .Row > th
    , .SpectateSummoner > .Box > .Content > .Table > .Body > .Row > .Cell
    , .SpectateSummoner > .Box > .Content > .Table > .Body > .Row > .Cell > div
    {
        color: #efefef !important;
    }
    
    /********* Blue ***************/
    
    .SpectateSummoner > .Box > .Content > .Table.Team-100 > .Body > .Row > .Cell.RuneMastery > .Button
    , .SpectateSummoner > .Box > .Content > .Table.Team-100 > .Body > .Row > .Cell.RuneMastery > .Button > span {
        background: #223366 !important;
        color: #efefef !important;
    }
    
    .SpectateSummoner > .Box > .Content > .Table.Team-100 > .Header > .Row > th
    {
        background: #223366 !important;
        
    }
    
    .SpectateSummoner > .Box > .Content > .Table.Team-100 > .Body
    {
        background: #334477 !important;
    }
    
    .SpectateSummoner > .Box > .Content > .Table.Team-100 > .Body > .Row:nth-child(2n) {
        background: #445588 !important;
    }
    
    /********* Red ***************/
    
    .SpectateSummoner > .Box > .Content > .Table.Team-200 > .Body > .Row > .Cell.RuneMastery > .Button
    , .SpectateSummoner > .Box > .Content > .Table.Team-200 > .Body > .Row > .Cell.RuneMastery > .Button > span {
        background: #553333 !important;
        color: #efefef !important;
    }
    
    .SpectateSummoner > .Box > .Content > .Table.Team-200 > .Header > .Row > th
    {
        background: #553333 !important;
        
    }
    
    .SpectateSummoner > .Box > .Content > .Table.Team-200 > .Body
    {
        background: #664444 !important;
    }
    
    .SpectateSummoner > .Box > .Content > .Table.Team-200 > .Body > .Row:nth-child(2n) {
        background: #775555 !important;
    }
    
    /********* End Live Game Section ***************/
    
    /********* Ladder Ranking Section ***************/
    
    .LadderRankingTable > .Header > .Row > .HeaderCell
    , .LadderRankingTable > .Body > .Row > .Cell
    , .LadderRankingTable > .Footer > .Row > .Cell
    {
        background: #1A1A1A !important;
        color: #EFEFEF !important;
    }
    
    .LadderRankingTable > .Body > .Row > .Cell.Rank
    , .WinRatioGraph > .WinRatio
    , .WinRatioGraph > .Graph > .Text
    , .PageHeaderWrap > .PageDescription > .Small
    {
        color: #EFEFEF !important;
    }
    
    /********* End Ladder Ranking Section ***************/
}
