options.value:
/* Flash版への切り替えリンク非表示 */
.WatchAppContainer-switchToFlash {
  display: none !important;
}

/* ニュース非表示 */
.MarqueeContainer {
  display: none !important;
}

/* コメントパネル非表示 */
.MainContainer-player {
  margin-left: 192px !important;
  position: relative !important;
  z-index: 100 !important;
}
.MainContainer-commentPanel {
  margin-right: 192px !important;
}
.VideoMenuContainer-area {
  width: 0px !important;
  display: block !important;
  right: -100% !important;
}
.VideoMenuContainer-area:nth-child(1) {
  margin-bottom: 28px !important;
}
.VideoMenuContainer-area .ActionButton {
  margin-bottom: 8px !important;
}
body .HeaderContainer {
  width: 90% !important;
}

/* ニコニ広告で宣伝ボタン非表示 */
.VideoMenuContainer-uadContainer {
  display: none !important;
}

/* ソーシャルボタン非表示 */
.VideoMenuContainer-area:nth-child(2) {
  display: none !important;
}

/* ページ下部非表示 */
.BottomContainer {
  display: none !important;
}

/* 再生リスト非表示 */
.BottomContainer-main.GridCell > .BottomContainer-card:nth-of-type(1) {
  display: none !important;
}

/* ページ下部右側非表示 */
.BottomContainer-side {
  display: none !important;
}
.BottomContainer-main {
  display: block !important;
  width: calc(100% - 324px) !important;
  padding-right: 0px !important;
  margin: auto !important;
}

/* タグからおすすめ非表示 */
.BottomContainer-side.GridCell > .BottomContainer-card:nth-of-type(1) {
  display: none !important;
}

/* 話題の生放送非表示 */
.BottomContainer-side.GridCell > .BottomContainer-card:nth-of-type(3) {
  display: none !important;
}

/* フッター非表示 */
.FooterContainer {
  display: none !important;
}

/* 広告非表示 */
.NicoSpotAdContainer {
  display: none !important;
}
.MiniBannerAdContainer {
  display: none !important;
}
.CommentPanelContainer {
  bottom: 0px !important;
}
.BottomContainer-adContainer {
  display: none !important;
}
.SideFollowAdContainer {
  display: none !important;
}
