style.css:
@-moz-document domain("kitsu.io") {
/* Major */
body {
	background:#151515 !important;
	color:#ddd;
}
hr {
	border-top:1px solid #444
}

.feed-container {
	width:1200px;
}

a:focus {
	text-decoration:none;
}

/* Top Bar */
.navbar {
	background:#0c0c0c;
	border-color:#1f1f1f;;
}
.cover-page .transparent .navbar-buttons input,.site-search__input {
	background:#222;
	border-color:#252525;
	color:#bbb;
}

.cover-page .transparent .navbar-buttons input:hover,.site-search__input:hover {
	background:#252525;
	border-color:#282828;
	color:#ddd;
}

.cover-page .transparent .navbar-buttons input:focus,.site-search__input:focus {
	background:#2b2b2b;
	border-color:#2d2d2d;
	color:#ccc;
}
/* Notification List */
.notifications svg path {
	fill:#ddd;
}
.notification-list .media {
	color:#ddd;
}
.notifications:hover svg path {
	fill:#fd755c;
}
.nav-item .notifications:before {
	background:#fd755c;
	transition:all 300ms ease-in-out;
	width:25px;
	position:absolute;
	height:25px;
	content:"";
	border-radius:100%;
	margin-top:-25%;
	margin-left:-25%;
	transform:scale(0.1);
}
.cover-page .transparent .navbar-buttons li svg path,.nav-item.open .notifications svg path,.site-search__icon .icon svg {
	fill:#efefef;
}
.site-search__input.active {
	background:#2d2d2d;
}
.nav-item.open .notifications:before {
	background:#fd755c;
	transform:scale(1.0); 
	border-color:#d34833;
	width:25px;
	height:25px;
	position:absolute;
	content:"";
	border-radius:100%;
}
.nav-item.open .notifications:hover:before {
	background:#ff826b;
	transform:scale(1.0);
	border-color:#d34833;
	width:25px;
	height:25px;
	position:absolute;
	content:"";
	border-radius:100%;
}
.nav-item .notifications:hover {
	background:#252525;
	border-color:#282828;
}	
.notification-drop {
	border-color:#333;
}
.nav-item .notifications {
	background:#222;
	border-color:#252525;
}
.notification-list .media {
	background:#1f1f1f;
	border-color:#191919;
}
.notification-drop header {
	border-color:#222;
}
.notification-list .media:hover {
	background:#151515;
	border-color:#191919;
}
.cover-page .transparent .navbar-buttons .nav-item.open .notification-drop a a,.open>.dropdown-menu a a{
	color:#e06552;
}
.cover-page .transparent .navbar-buttons .nav-item.open .notification-drop a a:hover,.open>.dropdown-menu a a:hover{
	color:#f3290a;
}
.notification-list .media.new {
	background:#4c251e;
}
.notification-list .media.new:hover {
	background:#6d3d34;
}
.notifications.notifications--present {
	background:linear-gradient(RGBA(237,72,95,1),RGBA(242,124,54,1));
	transition:all 100ms linear;
}
.notifications.notifications--present:hover {
	background:linear-gradient(RGBA(242,124,54,1),RGBA(237,72,95,1));
}

/* Quick Updater */

.quick-update .flickity-viewport .action-wrapper textarea {
	background:#222;
	border-bottom-color:#2b2b2b;
	color:#ddd;
}
.quick-update .flickity-viewport .quick-update-item .overlay-panel {
	background:#2d2d2d;
}

.quick-update {
	background:#1b1b1b;
	border-color:#1f1f1f;
}


/* Trending Page */
.explore-search .explore-search--wrapper:hover svg {
	fill:#ccc;
}
.trending-banner {
	background:#191919;
}
.following-activity .activity-item,.trending-activity--header {
	border-color:#151515;
	color:#ccc;
}
.trending-banner--header .trending-row--title {
	color:#ccc;
}

.participating-following .stream-like .like-avatar .lazy-image {
