style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(intra.epitech.eu) {
    /*
    ** Light   blue : #3498db
    ** Dark    blue : #2980b9
    ** Darker  blue : #34495e
    ** Darkest blue : #2c3e50
    */

    .navigation::after,
    #main .main-wrapper::after,
    #content .bloc::after,
    #content .bloc .data::after,
    #sidebar > .title::after,
    #sidebar form.filter.course .filters .item::after,
    #sidebar form.filter.course .filters label::after,
    #sidebar form.filter.course .filters .item.location .choice::after,
    #sidebar .filter h2::after,
    #breadcrumb ul::after,
    #homeboard::after,
    #homeboard section::after,
    #homeboard section article .timeline::after, 
    #homeboard section article p::after,
    #profil .item.title::after,
    ul.tabs::after {
        display: unset;
        clear: unset;
        content: unset;
        visibility: unset;
        height: unset;
    }

    a, dt {
        transition: background-color .4s, color .4s, border .4s;
        overflow: hidden;
        text-overflow: ellipsis;
        white-space: nowrap;
    }
    
    a {
        color: rgba(0, 0, 0, .8);
    }
    
    a:hover {
        text-decoration: none;
    }
    
    body {
        background-color: #ecf0f1;
    }
    
    body, input {
        font-family: "Droid Sans", "Segoe UI", "Trebuchet MS", sans-serif;
    }

    body, a, input, .button, .button .label, dl .label, #profil .item.course, #profil .bloc.main .data .infos .info.item {
        line-height: normal !important;
    }
    
    #body-wrapper {
        margin: 0;
    }
    
/* ==========================================================================
   Header
   ========================================================================== */
    
    #header {
        width: 100%;
        height: 60px;
        position: fixed;
        top: 0;
        z-index: 98;
        box-sizing: border-box;
        background-color: #3498db;
        box-shadow: 0 0 5px #000;
    }

    #header .intra {
        height: 100%;
        position: static;
        background: none;
    }

    #header .intra .header-wrapper {
        height: 100%;
    }

    #header .intra .header-wrapper .reserved {
        width: 300px;
        height: 100%;
        margin-left: 0;
        background-color: #2980b9;
    }

    #header #language {
        display: none;
    }

/* Generic                                                             Header
   ========================================================================== */
    
    #header .intra .navigation,
    #header .intra #notification,
    #header .intra #identification {
        height: 100%;
        margin: 0;
        padding: 10px;
        box-sizing: border-box;
        background-color: unset;
    }
    
    #header .intra .navigation table,
    #header .intra #notification .dropdown,
    #header .intra #identification .infos {
        height: 100%;
        background-color: #2980b9;
    }

    #header .intra .navigation table tr td,
    #header .intra #notification .dropdown {
        border-right: 1px solid rgba(0, 0, 0, .1);
    }
    
    #header .intra .navigation table tr td .button:hover,
    #header .intra #notification .dropdown > a:hover,
    #header .intra #notification .dropdown.ui-expanded > a {
        background-color: rgba(0, 0, 0, .2);
    }

/* Menu                                                                Header
   ========================================================================== */
    
    #header .intra .navigation {
        margin-left: 300px;
        width: auto;
        max-width: 650px;
    }

    #header .intra .navigation:hover {
        height: inherit;
    }

    #header .intra .navigation table {
        width: 100%;
        border-radius: 2px;
        overflow: hidden;
    }

    #header .intra .navigation table td {
        height: 100%;
        overflow: hidden;
    }
    
    #header .intra .navigation table td:last-child {
        border-right: none;
    }

    #header .intra .navigation table td a.button {
        display: table;
        max-width: unset;
        width: 100%;
        height: 100%;
        position: unset;
    }

    #header .intra .navigation table td a.button .icon {
        display: none;
    }

    #header .intra .navigation table td a.button .label {
        display: table-cell;
        width: 100%;
        padding: 10px;
        position: unset;
        vertical-align: middle;
        text-align: center;
        background-color: unset;
        font-weight: bold;
        text-transform: capitalize;
    }
    
    #header .intra .navigation table td a.button:hover .label {
        display: table-cell;
    }

/* Notifications                                                       Header
   ========================================================================== */
    
    #header .intra #notification {
        float: right;
    }
    
    #header .intra #notification .dropdown {
        overflow: hidden;
    }
    
    #header .intra #notification .dropdown.rdv {
        border-top-left-radius: 2px;
        border-bottom-left-radius: 2px;
    }
    
    #header .intra #notification .dropdown.pedago {
        border-top-right-radius: 2px;
        border-bottom-right-radius: 2px;
        border-right: none;
    }
    
    #header .intra #notification .dropdown a {
        height: 100%;
        padding: 8px;
        box-sizing: border-box;
        border-radius: 0;
    }
    
    #header .intra #notification .dropdown a .logo {
        margin: 0;
    }
    
    #header .intra #notification .dropdown.rdv a .logo { 
        background-position: -190px -109px;
    }
    
    #header .intra #notification .dropdown.message a .logo { 
        background-position: -144.5px -108px;
    }
    
    #header .intra #notification .dropdown.message.news a .logo {
        background-position: -144.5px -130px;
    }
    
    #header .intra #notification .dropdown.pedago a .logo { 
        background-position: -168px -108px;
    }
    
    #header .intra #notification .dropdown .layer {
        width: 300px;
        margin: 0;
        padding: 0;
        position: absolute;
        top: 70px;
        right: 135px;
        z-index: 99;
        box-sizing: border-box;
        background-color: #fff;
        color: rgba(0, 0, 0, .6);
        border-radius: 2px;
        border: unset;
        box-shadow: 0 0 5px rgba(0, 0, 0, .6);
    }
    
    #header .intra #notification .dropdown .layer .item,
    #header .intra #notification .dropdown .layer .empty {
        padding: 15px;
        border-color: rgba(0, 0, 0, .1);
        font-style: normal;
    }
    
    #header .intra #notification .dropdown .layer .item a {
        color: #000;
        padding: 0;
    }

/* User                                                                Header
   ========================================================================== */
    
    #header .intra #identification .infos {
        width: 200px;
        max-width: 200px;
        height: 100%;
        margin: 0;
        padding: 11px;
        box-sizing: border-box;
        border-radius: 2px;
        text-decoration: none;
    }
    
    #header .intra #identification .infos:hover {
        background-color: #216694;
        color: #fff;
    }
    
    #header .intra #identification.expanded .infos {
        background-color: #2980b9;
        color: #fff;
        border-bottom-left-radius: 0;
        border-bottom-right-radius: 0;
    }
    
    #header .intra #identification .infos .picture {
        width: 22px;
        height: 22px;
        margin: -1px 0 0 0;
        border-radius: 50%;
        background-color: rgba(0, 0, 0, .3) !important;
    }
    
    #header .intra #identification .infos .title {
        margin: 0;
        padding: 1px 0 0 10px;
        font-weight: bold;
    }
    
    #header .intra #identification.expanded .menu {
        width: 200px;
        min-width: unset;
        padding: 13px;
        top: 50px;
        right: 10px;
        box-sizing: border-box;
        background-color: #2980b9;
        color: #fff;
        border-radius: 0 0 2px 2px;
        box-shadow: none;
    }
    
    #header .intra #identification.expanded .menu > span {
        display: block;
    }
    
    #header .intra #identification.expanded .menu .links a {
        display: block;
        margin-top: 13px;
        padding: 10px;
        background-color: #e74c3c;
        border-radius: 2px;
        text-align: center;
        font-weight: bold;
        color: #fff;
    }
    
    #header .intra #identification.expanded .menu .links a span {
        text-transform: capitalize;
    }
    
    @media screen and (max-width: 1000px) {
        #header .intra #identification {
            width: 64px;
            min-width: unset;
        }
        
        #header .intra #identification .infos {
            width: auto;
        }
        
        #header .intra #identification .infos .title {
            display: none;
        }
    }
    
/* ==========================================================================
   Sidebar
   ========================================================================== */

    #sidebar {
        width: 300px;
        min-width: 300px;
        min-height: unset !important;
        position: fixed;
        top: 60px;
        bottom: 0;
        z-index: 99;
        overflow-y: auto !important;
        box-sizing: border-box;
        background-color: #2c3e50;
        transition: width .5s, min-width .5s;
    }
    
    #sidebar.fold {
        min-width: 0 !important;
        width: 0 !important;
    }
    
    #sidebar #toggle-sidebar {
        display: inline-block;
        font-size: 30px;
        padding-top: 0;
        padding-right: 3px;
        padding-left: 2px;
        text-align: center;
        position: fixed;
        top: 10px;
        left: 20px;
        z-index: 50001;
        background-color: #2c3e50;
        border-radius: 50%;
        font-family: sans-serif;
    }
    
    #sidebar > .wrapper {
        border-bottom: none;
    }
    
    #sidebar hr {
        border: 0;
        height: 1px;
        background-color: rgba(255, 255, 255, .15);
        margin: 0 0 20px !important;
    }
    
/* Generic : Header                                                   Sidebar
   ========================================================================== */
    
    #sidebar > .title {
        margin: 0 20px 20px;
        padding: 20px 0;
        overflow: hidden;
        border-bottom: 1px solid rgba(255, 255, 255, .15)
    }
    
    #sidebar > .title > .icon {
        display: none;
    }

    #sidebar > .title > .label {
        padding: 0;
        margin: 0;
        font-size: 1.2rem;
    }
    
    #sidebar > .title br {
        display: none;
    }
    
/* Generic : Lists                                                    Sidebar
   ========================================================================== */
    
    #sidebar ul, #sidebar ol, #sidebar li, #sidebar dd {
        margin: 0 !important;
        padding: 0 !important;
    }
    
    #sidebar li > a,
    #sidebar .datatree > dl > dt > span,
    #sidebar .datatree > dl > dd > dl > dt,
    #sidebar .datatree > dl > dd > dl > dd > dl > dt a {
        display: block;
        padding: 13px 20px 13px 17px !important;
        border-left: 3px solid transparent !important;
        background-color: transparent !important;
    }
    
    #sidebar li > a,
    #sidebar dl > dt > span .label,
    #sidebar dl dl > dt > a {
        color: rgba(255, 255, 255, .5) !important;
        font-size: .8rem !important;
        font-weight: normal !important;
        height: unset !important;
    }
    
    #sidebar li > a:hover,
    #sidebar dt:hover > a,
    #sidebar dt.expanded > a {
        color: #FFF !important;
    }
    
    #sidebar li > a:hover,
    #sidebar .datatree > dl > dt > span:hover,
    #sidebar .datatree > dl > dd > dl > dt:hover,
    #sidebar .datatree > dl > dd > dl > dd > dl > dt > a:hover {
        border-color: #3498db !important;
        background-color: rgba(0, 0, 0, .2) !important;
    }
    
    #sidebar li li > a,
    #sidebar .datatree > dl > dd > dl > dt {
        background-color: rgba(0, 0, 0, .2) !important;
    }
    
    #sidebar li li > a:hover,
    #sidebar li li li > a,
    #sidebar .datatree > dl > dd > dl > dt:hover,
    #sidebar .datatree > dl > dd > dl > dd > dl > dt > a {
        background-color: rgba(0, 0, 0, .4) !important;
    }
    
    #sidebar li li li > a:hover,
    #sidebar li li li.ui-active > a,
    #sidebar .datatree > dl > dd > dl > dd > dl > dt > a:hover {
        background-color: rgba(0, 0, 0, .6) !important;
    }
    
    #sidebar li > a .icon,
    #sidebar dt > span .icon,
    #sidebar dt > a span.icon,
    #sidebar dt > span.button,
    #sidebar dt > a.button {
        display: block !important;
        width: 22px !important;
        margin: 0 20px 0 0 !important;
        padding-left: 0 !important;
        float: left;
    }
    
    #sidebar li.ui-active > a,
    #sidebar dt.expanded > span .label {
        color: #FFF !important;
    }
    
    #sidebar dl > dt > span {
        display: block;
    }
    
    #sidebar dd {
        background: transparent !important;
        border: none !important;
    }
    
    #sidebar dt {
        height: unset !important;
        box-shadow: none !important;
        background-color: transparent !important;
    }

/* Generic : Buttons                                                  Sidebar
   ========================================================================== */
    
    #sidebar div.button {
        height: unset !important;
        margin: 0 20px 20px !important;
        padding: 10px;
        box-sizing: border-box;
        border-radius: 20px !important;
        border: none !important;
        background-color: #1abc9c !important;
        transition: background-color .2s;
        box-shadow: none !important;
    }
    
    #sidebar div.button:hover {
        background-color: #16a085 !important;
    }
    
    #sidebar div.button .transparency {
        display: none;
    }
    
    #sidebar div.button > span {
        height: 100%;
    }
    
    #sidebar div.button > span .icon {
        width: 25px !important;
        height: 20px !important;
        margin: -4px 5px 0 2px !important;
        background-position: -10px -1249px !important;
        transition: background-position .8s;
        float: left;
    }
    
    #sidebar form.filter.expanded div.button > span .icon,
    #sidebar div.button.expanded > span .icon {
        background-position: -10px -1268px !important;
    }
    
    #sidebar div.button > span .label {
        color: #fff !important;
        height: unset !important;
        margin: 0 !important;
        padding: 0 !important;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
        text-align: left !important;
        font-weight: bold;
        font-size: 13px;
    }    

/* Generic : Forms                                                    Sidebar
   ========================================================================== */

/* 
** AUX DEVELOPPEURS DE L'INTRANET #BigInt
** --------------------------------------
**
** Si un jour vous lisez ça : vous avez déjà appris les bases des langages du
** web ? Votre code est juste ignoble, et j'espère que vous le savez !
** Les <span> ne sont pas des éléments à mettre dans tous les  sens,  et  les
** sélecteurs CSS ça se généralise ! Bon, je ne vais pas vous lister toute la
** mocheté de votre code, ça prendrait trop de temps.
**
** Et puis soyons honnête, ce serait plus rapide de recommencer from scratch.
**
** Sans rancune,
** Thinker.
*/

    #sidebar > .wrapper > .filter:not(.button),
    #sidebar .planif:not(.button) {
        background: unset !important;
        padding: 0 !important;
    }
    
    #sidebar .filter .filters,
    #sidebar .bloc > .filters,
    #sidebar .bloc.locationfilter,
    #user #sidebar .filter.find,
    #sidebar .filter.find,
    #sidebar .title.trombi ~ form.filter .filter > .item,
    #sidebar .filter.trombi h2,
    #sidebar fieldset {
        margin: 0 20px 20px;
    }
    
    #sidebar .filter .filters div .item,
    #sidebar .title.trombi ~ form.filter .filter > .item,
    #sidebar fieldset {
        padding: 10px;
        background-color: rgba(0, 0, 0, .2);
        border-radius: 2px;
        overflow: hidden;
        border: none;
    }

    #sidebar .filters .item {
       clear: both;
    }
    
    #sidebar fieldset {
        padding-top: 30px;
        position: relative;
    }
    
    #sidebar .filter .filters .item > label[for^="filter"],
    #sidebar .title.trombi ~ form.filter .filter > .item:not(.active) > label,
    #sidebar fieldset legend {
        display: unset !important;
        text-transform: uppercase;
        color: #8093A5 !important;
        font-size: .7rem;
        font-weight: bold;
        background-color: transparent;
        border: none;
    }
    
    #sidebar fieldset legend {
        width: calc(100% - 20px);
        padding: 0;
        position: absolute;
        top: 10px;
        left: 10px;
        right: 10px;
        text-overflow: ellipsis;
        white-space: nowrap;
        overflow: hidden;
    }
    
    #sidebar .item .input input,
    #sidebar input[type=text],
    #sidebar input[type=submit],
    #sidebar a.button,
    #sidebar .filters.semester .actions a {
        width: 100% !important;
        display: block;
        padding: 10px;
        margin: 0;
        box-sizing: border-box;
        border: none;
        border-radius: 2px !important;
        background-color: rgba(0, 0, 0, .3);
        color: rgba(255, 255, 255, .6);
        font-size: 11px !important;
    }
    
    #sidebar input[type=submit],
    #sidebar a.button,
    #sidebar .filters.semester .actions a {
        background-color: #e67e22 !important;
        color: #fff;
        font-weight: bold;
        text-align: center;
        text-transform: capitalize;
    }
    
    #sidebar input[type=checkbox] {
        float: left;
        margin-left: 0;
    }
    
    #sidebar input[type=submit] {
        padding-top: 9px;
        padding-bottom: 9px;
        transition: background-color .2s;
    }
    
    #sidebar input[type=submit]:hover {
        cursor: pointer;
        background-color: #d35400 !important;
    }
    
    #sidebar input[type=checkbox] + label {
        display: block !important;
        padding-top: 3px;
        padding-left: 25px;
        color: rgba(255, 255, 255, .5);
        float: none !important;
    }

    #sidebar .filters.instance fieldset .item .input,
    #sidebar .filter.find .item.input {
        width: 75%;
        margin: 0;
        float: left;
    }
    
    #sidebar .filters.instance fieldset .item .submit,
    #sidebar .filter.find .item.submit {
        width: 21%;
        margin: 0 0 0 4%;
        float: left;
    }
    
    #sidebar a.button > span {
        padding: 0 !important;
    }
    
    #sidebar a.button > span span.icon {
        display: none;
    }
    
    #sidebar a.button > span span.label {
        color: #fff !important;
    }
    
    #sidebar .filter .filters span {
        margin: 0 !important;
    }
    
    #sidebar .filter .filters span label span {
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
    }
    
    #sidebar .item .list .choice,
    #sidebar fieldset > div {
        overflow: hidden;
    }
    
/* Home                                                               Sidebar
   ========================================================================== */
    
    #wall #sidebar .title .picture {
        display: none;
    }

    #wall #sidebar li a .icon {
        margin-top: -2px;
    }

    #wall #sidebar li.module   a .icon { background-position: -254px -310px; }
    #wall #sidebar li.mark     a .icon { background-position: -295px -416px; }
    #wall #sidebar li.planning a .icon { background-position: -254px -288px; }
    #wall #sidebar li.trombi   a .icon { background-position: -253px -376px; }
    #wall #sidebar li.stage    a .icon { background-position: -254px -466px; }
    #wall #sidebar li.ticket   a .icon { background-position: -291px -311px; }
    #wall #sidebar li.document a .icon { background-position: -340px -966px; }
    
    #wall #sidebar .rzone {
        margin: 0;
        padding: 20px;
        text-align: justify;
    }
    
    #wall #sidebar .rzone .alert {
        margin: 0;
        position: absolute;
        top: 21px;
        right: 20px;
        width: 20px;
        height: 20px;
        background-image: none;
        border-radius: 50%;
    }
    
    #wall #sidebar .rzone .alert.green  { background: #2ecc71; box-shadow: 0 0 8px 1px #2ecc71; }
    #wall #sidebar .rzone .alert.orange { background: #e67e22; box-shadow: 0 0 8px 1px #e67e22; }
    #wall #sidebar .rzone .alert.red    { background: #e74c3c; box-shadow: 0 0 8px 1px #e74c3c; }
    
    #wall #sidebar .rzone > div {
        margin-top: 15px;
    }
    
/* E-learning                                                         Sidebar
   ========================================================================== */
    
    #elearning #sidebar li.ui-active > a .icon {
        background-position: -91px -608px !important;
    }
    
    #elearning #sidebar li li > a .icon {
        background-position: -89px -640px !important;
    }
    
    #elearning #sidebar li li.ui-active > a .icon {
        margin-top: 0 !important;
        background-position: -88px -640px !important;
        transform: rotate(90deg);
    }
    
    #elearning #sidebar li li li > a .icon,
    #elearning #sidebar li li li.ui-active > a .icon {
        background-position: -90px -672px !important;
        transform: rotate(0deg);
    }
    
/* Modules                                                            Sidebar
   ========================================================================== */
    
    #sidebar form.filter.course .filters {
        border-top: none;
    }
    
    #sidebar form.filter.course .filters .resume span {
        padding: 0;
        text-align: justify;
    }
    
    #sidebar form.filter.course .filters .item {
        margin: 20px 0 0;
    }
    
    #sidebar form.filter.course .filters .item.role {
        margin-top: 0;
    }
    
    #sidebar form.filter.course .filters .item.role.role-registered {
        border-bottom-left-radius: 0;
        border-bottom-right-radius: 0;
        padding-bottom: 0;
    }
    
    #sidebar form.filter.course .filters .item.role.role-unregistered {
        border-top-left-radius: 0;
        border-top-right-radius: 0;
    }
    
    #sidebar form.filter.course .filters .item.role input {
        display: unset;
        opacity: 1;
        width: unset;
        position: static;
    }
    
    #sidebar form.filter.course .filters .item.role .icon {
        display: none;
    }
    
    #sidebar form.filter.course .filters .item.course > label::after {
        content: ':';
    }

    #sidebar form.filter.course .filters .item > .list {
        margin-top: 10px;
    }
    
    #sidebar form.filter.course .filters .item span label span {
        padding: 0;
    }
    
    #sidebar form.filter.course .filters .item input:not([type=checkbox]) {
        display: none;
    }
    
    #sidebar form.filter.course .datatree > dl {
        border: none;
    }
        
    #sidebar form.filter.course .datatree > dl > dt > span .icon {
        height: 16px !important;
        background: url(https://intra.epitech.eu/static4a0d926eb5af833cbbfdb520aba359173d0de563/img/sprite-common.png) no-repeat -91px -608px !important;
    }

    #sidebar form.filter.course .datatree .label {
        height: unset;
    }
    
    #sidebar form.filter.course .datatree dt.module > a:not(.button) {
        width: 75%;
        display: block;
        margin-left: 42px;
    }
    
    #sidebar form.filter.course .datatree dt.module:hover > a {
        width: 64%;
    }
    
    #sidebar form.filter.course .datatree dt.module > a span.icon {
        position: absolute;
        top: 9px;
        right: 9px;
        margin: 0 !important;
    }
    
    #sidebar form.filter.course .datatree dt.module > a span.actions {
        top: 10px;
        right: 45px;
    }
    
    #sidebar form.filter.course .datatree dt.module > a span.modif {
        width: 5px !important;
        top: 7px !important;
        right: 0 !important;
        background-position: -173px -306px !important;
    }
    
