/* ==========================================================================
   Footer
   ========================================================================== */
    
    #footer {
        width: 80px;
        height: unset;
        padding: 10px 20px;
        position: absolute;
        right: 20px;
        bottom: 0;
        z-index: 2;
        box-sizing: border-box;
        background-color: #1a2530;
        background-image: none;
        border-radius: 3px 3px 0 0;
        transition: width .1s, right .1s;
    }
    
    #footer::before {
        content: "Footer";
        color: white;
        font-weight: bold;
        display: block;
        text-align: center;
    }
    
    #footer:hover {
        padding: 20px;
        right: 0;
        border-radius: 0;
        width: calc(100% - 300px);
    }
    
    #footer:hover::before {
        content: "";
    }
    
    #footer:hover .footer-wrapper {
        max-height: 500px;
    }
    
    #footer .footer-wrapper {
        display: block;
        max-height: 0;
        margin: 0;
        overflow: hidden;
        transition: max-height .4s;
    }
    
    #footer .footer-wrapper > ul {
        margin-top: 0 !important;
        width: 25% !important;
        padding-left: 2%;
        box-sizing: border-box;
    }
    
    #footer .footer-wrapper > ul:first-child {
        padding-left: 0;
    }
    
    #footer .footer-wrapper > ul li {
        background: none;
        padding: 0;
    }
    
    #footer .footer-wrapper > ul a {
        color: rgba(255, 255, 255, .4);
    }
    
    #footer .footer-wrapper > ul a:hover {
        color: white;
    }
