style.css:
@-moz-document domain("httpstatuses.com") {
  body {
    background-color: #333; /* #556270 */  
    color: #fff;
  }
  h1, h2, h3, h4, h5, h6 {
    color: #fff;
  }
  .banner a,
  .codes ul li a {
    color: #f5f5f5;
  }
  .banner {
    border-bottom-color: #556270;
  }
  .share {
    background-color: #556270;
  }
  .share a {
    color: #eee;
  }
  code {
    color: #556270;
  }
}
