style.css:
@-moz-document domain('lobste.rs') {

body { background-color: #222; color: #ddd; }
a { color: #ccf; }
#wrapper textarea, #wrapper input, #wrapper button { color: #ddd; background-color: #000; border-color: #444; }
#header .headerlinks a.cur_url { color: #fff; }
#header #headertitle a { color: #fff; }
#header .headerlinks a { color: #ccc; }
#inside li .link a { color: #ddf; }
#inside li .byline { color: #bbb; }
#inside li .byline a { color: #bbb; }
#inside li .domain { color: #bbb; }
#inside .comment a { color: #ccf; }
#wrapper div.morelink a { color: #ccc; }
#wrapper div#footer a { color: #ccc; }
.comment:target { background-color: #444; }

/* tag badges */
#inside a.tag { background-color: #000; border-color: #444; color: #aaa; }
#inside a.tag.tag_is_media { background-color: #622; border-color: #944; }
#inside a.tag.tag_announce, #inside a.tag.tag_ask, #inside a.tag.tag_show, #inside a.tag.tag_interview { background-color: #226; border-color: #449; }

/* hats */
#inside span.hat span.crown { background-color: #000; border-color: #444; }
#inside span.hat { border-color: #444; }
#inside span.hat span.crown, #inside span.hat a { color: #aaa; }

/* tag dropdown */
#inside .select2-container-multi .select2-choices { background-color: #000; border-color: #444; }
#inside .select2-container .select2-drop { background-color: #000; border-color: #444; }

/* search pager */
#inside div.page_link_buttons a { color: #bbb; background-color: #000; border-color: #444; }
#inside div.page_link_buttons a.cur, #inside div.page_link_buttons span { color: #bbb; background-color: #222; border-color: #222; }

/* history/filter table */
#inside table.data th { background-color: #000; border-color: #444; }
#inside table.data tr.row0 td, table.data.zebra tr:nth-child(2n) td { background-color: #222; border-color: #444; }
#inside table.data tr.row1 td, table.data.zebra tr:nth-child(2n+1) td { background-color: #000; border-color: #444; }

/* mobile/responsive fixes */
ol.stories.list li.story div.story_liner { background-color: inherit; }
div#header, div#leader { background-color: inherit; border-bottom: 1px solid #444; }
ol.stories.list li.story .mobile_comments { background-color: #444; }
ol.stories.list li.story { border-bottom: 1px solid #444; }

}
