style.css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
/*Shoutout to killhellokitty on deviantart*/

/*Urlbar shadow/border*/
#urlbar[focused="true"],
.searchbar-textbox[focused="true"],
toolbar textbox[focused="true"] {
  background: #474747 !important;
  background-image: linear-gradient(#1a1a1b,#1d1d1e,#1f1f20) !important;
  -moz-transition: .40s ease-in !important;
  box-shadow: 0px 2px 4px  rgba(0,0,0,0.36) inset, 0 0 0 2px rgba(0,173,238,0.85)inset, 0px 1px 0 rgba(255,255,255,.12) !important;
}
[privatebrowsingmode=temporary] #urlbar[focused="true"],
[privatebrowsingmode=temporary] .searchbar-textbox[focused="true"] {
  -moz-transition: .40s ease-in !important;
  box-shadow: 0px 2px 4px  rgba(0,0,0,0.36) inset, 0 0 0 2px rgba(196,111,224,0.85)inset, 0px 1px 0 rgba(255,255,255,.12) !important;
}
