style.css:
@-moz-document regexp('about:newtab') {
    :root{
        --bg1: #1d2021!important;
        --bg2: #282828!important;
        --bg3: #3c3836!important;
        --bg4: #32302f!important;
        --bg5: #665c54!important;
        --bg6: #7c6f64!important;
        --fg1: #fbf1c7!important;
        --fg2: #ebdbb2!important;
        --fg3: #d5c4a1!important;
        --fg4: #bdae93!important;
        --bluelight: #83a598!important;
        --bluedark: #458588!important;
        --graylight: #a89984!important;
        --graydark: #928374!important;
    }
  body{
    background-color: var(--bg2)!important;
  }
  #newtab-search-text,.contentSearchSuggestionTable,.contentSearchHeader,.newtab-cell{
    background-color: var(--bg1)!important;
    color: var(--fg1)!important;
    border: none!important;
    border-radius: 0px!important;
  }
  .newtab-link,.newtab-thumbnail,.newtab-site{
    border-radius: 0px!important;
  }
  .newtab-site{
    box-shadow: 0px 1px 2px var(--fg1)!important;
  }
  .newtab-title{
    color: var(--fg1)!important;
    background-color: var(--bg2)!important;
    border: none!important;
  }
  #newtab-undo-container{
    background-color: var(--bg3)!important;
    color: var(--fg3)!important;
  }
  .newtab-undo-button{
     color: var(--fg1)!important;
  }
}
