    color: white;
  }

  .ipsMessage_info,
.ipsMessage_information
  {
    background: #7a5c8f;
  }

  html[dir="ltr"] .ipsFollow
  {
    background: black;
    border: 0.25px solid white;
  }

  .ipsField_fieldList input[type="radio"],
.ipsField_fieldList input[type="checkbox"],
.ipsField_fieldList .ipsCustomInput
  {
    float: left;
    margin-top: 0px;
  }

