    margin: 0px 0px 0 0px;
    height: 0px !important;
    background: #7a5c8f !important;
  }

  .ipsTabs_panel
  {
    height: 100%;
    background: #000;
  }

  .ipsTabs_activeItem
  {
    background: #000;
    color: #fff;
    font-weight: bold;
  }

  .ipsApp .ipsDataItem_warning
  {
    background-color: #370000;
    ;
