  {
    background-color: #333;
  }

  .ipsEmbedded_headerArea
  {
    background: #000000;
  }

  .cMessage_active
  {
    background: #242020 !important;
  }

  .ipsLikeRep
  {
    background: #000000;
  }

  .ipsApp .ipsDataItem_selected
  {
    background-color: #9479a6;
  }

  .ipsSelectTree_nodes
  {
    background: #000000;
  }

  .ipsSelectTree_nodes [data-role="nodeList"]
  {
    background: #6b4982;
  }

  .ipsPageHead_special
  {
    background: rgba(255, 255, 255, 0) !Important;
  }

  .ipsHovercard
  {
    background: #6b4982;
  }

  .ipsSpoiler_header
  {
    background: rgb(107, 73, 130);
    color: #ffffff;
  }

  .ipsSpoiler, .ipsStyle_spoiler
  {
    background: #666;
  }

  #ipsLayout_header nav
  {
    background: black;
    height: 40px;
  }

  html[dir="ltr"] .ipsColumns > .ipsColumn:last-child:not( .ipsPad), html[dir="ltr"] .ipsColumns.ipsColumns_noSpacing > .ipsColumn:not( .ipsPad)
  {
    background: transparent;
  }
