.arrowchat_chatroom_content
{
  position: relative;
  border-right: 1px solid #000000;
  border-left: 1px solid #000000;
  background-color: #000;
  color: #fff;
}

.arrowchat_tabtitle, .arrowchat_chatrooms_title
{
  background-color: #7a5c8f;
}

#arrowchat_chatroom_open_chats
{
  background-color: #000000;
  overflow: hidden;
  display: none;
}

.arrowchat_chatroom_tab_focused
{
  background-color: #000 !important;
  border: 1px solid #ffffff;
  color: #fffdfd;
}

.arrowchat_chatboxmessagecontent, .arrowchat_chatroom_message_content
{
  background-color: #333;
  color: #ebebeb;
}

.arrowchat_chatboxmessage_wrapper, .arrowchat_self .arrowchat_chatroom_message_content
{
  background-color: #6b4982;
}

.arrowchat_chatboxmessagecontent, .arrowchat_self .arrowchat_chatroom_msg
{
  color: #ffffff !important;
}

.arrowchat_chatroom_room_list
{
  color: #7a5c8f;
}

#arrowchat_chatroom_welcome_msg div
{
  color: #ffffff;
  background-color: #7a5c8f;
}

#arrowchat_chatroom_chat:before
{
  background: linear-gradient(#ffffff3b,rgba(255, 255, 255, 0.00));
}

.arrowchat_group_text
{
  color: #ffffff;
}

#arrowchat_chatroom_line_users #arrowchat_chatroom_line_admins #arrowchat_chatroom_line_mods
{
  background-color: #7a5c8f;
}

.arrowchat_chatroom_content a
{
  color: #a180b7;
}

.arrowchat_chatroom_name
{
  color: #7a5c8f;
  font-size: 16px !important;
  font-weight: bold;
}

.arrowchat_closebox_bottom
{
  background-color: whitesmoke!important;
}
