style.css:
/* -=< Hide Disabled Browser Buttons - LavaFox & BlackFox by Zigboom Designs >=- */

@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

#forward-button:not([disabled="true"]) {
  margin-left: 1px !important; 
  opacity: 1 !important; 
  transition: margin-left .5s, 
  box-shadow .3s,
  background-color .3s, 
  opacity .3s !important;
}

#back-button:not([disabled="true"]) {
  margin-right: 1px !important; 
  opacity: 1 !important; 
  transition: margin-right .5s, 
  box-shadow .3s,
  background-color .3s, 
  opacity .3s !important;
}

#forward-button[disabled="true"] {
  margin-left: -29px !important; 
  opacity: 0 !important; visibility: collapse !important; 
  transition: margin-left .5s, 
  box-shadow .3s,
  background-color .3s, 
  opacity .3s, 
  visibility .5s !important; 
}

#back-button[disabled="true"] {
  margin-right: -29px !important; 
  opacity: 0 !important; visibility: collapse !important; 
  transition: margin-right .5s, 
  box-shadow .3s,
  background-color .3s, 
  opacity .3s, 
  visibility .5s !important; 
}

#back-button .toolbarbutton-menu-dropmarker, #forward-button .toolbarbutton-menu-dropmarker {
  display: none;
}



/* The End */
